parameters.jsCode:
// Generate unique beta access code
const inputData = $input.all();

const generateCode = () => {
  return "BETA-" + Math.random().toString(36).substring(2, 8).toUpperCase();
};

const outputData = inputData.map(item => ({
  json: {
    ...item.json,
    access_code: generateCode(),
    timestamp: new Date().toISOString(),
    timestamp_readable: new Date().toLocaleString('en-US', {
      dateStyle: 'medium',
      timeStyle: 'short'
    }),
    year: new Date().getFullYear()
  }
}));

return outputData;

