// Analyze COA specifications and calculate compliance
const item = $input.item.json;

const specs = item.specifications;
const totalTests = Object.keys(specs).length;
let passedTests = 0;
let failedTests = 0;
const nonCompliantParams = [];

// Count pass/fail and identify non-compliant parameters
for (const [param, data] of Object.entries(specs)) {
  if (data.status === 'pass') {
    passedTests++;
  } else {
    failedTests++;
    nonCompliantParams.push({
      parameter: param,
      standard: data.standard,
      result: data.result,
      deviation: `Out of specification`
    });
  }
}

const complianceRate = ((passedTests / totalTests) * 100).toFixed(2);
const isCompliant = failedTests === 0;

// Calculate severity score
let severityScore = 100;
if (failedTests > 0) {
  severityScore -= (failedTests * 15); // Deduct 15 points per failed test
  if (failedTests >= 3) severityScore -= 10; // Additional penalty for multiple failures
}

return {
  json: {
    ...item,
    analysis: {
      total_tests: totalTests,
      passed_tests: passedTests,
      failed_tests: failedTests,
      compliance_rate: parseFloat(complianceRate),
      is_compliant: isCompliant,
      non_compliant_parameters: nonCompliantParams,
      severity_score: Math.max(severityScore, 0),
      compliance_status: isCompliant ? 'APPROVED' : 'REJECTED'
    }
  }
};

// Calculate comprehensive vendor quality score
const item = $input.item.json;
const history = item.vendor_history;
const analysis = item.analysis;

// Weight factors for scoring
const weights = {
  historical_compliance: 0.30,
  current_batch_compliance: 0.25,
  on_time_delivery: 0.20,
  documentation_quality: 0.15,
  severity_adjustment: 0.10
};

// Calculate individual scores
const historicalComplianceScore = (history.compliant_batches / history.total_batches) * 100;
const currentBatchScore = analysis.compliance_rate;
const onTimeDeliveryScore = history.on_time_delivery;
const documentationScore = history.documentation_quality;
const severityScore = analysis.severity_score;

// Calculate weighted total score
const totalScore = (
  (historicalComplianceScore * weights.historical_compliance) +
  (currentBatchScore * weights.current_batch_compliance) +
  (onTimeDeliveryScore * weights.on_time_delivery) +
  (documentationScore * weights.documentation_quality) +
  (severityScore * weights.severity_adjustment)
).toFixed(2);

// Determine vendor rating
let rating, ratingColor, recommendation;
if (totalScore >= 95) {
  rating = 'A+ (Excellent)';
  ratingColor = 'green';
  recommendation = 'Preferred Supplier - Continue partnership';
} else if (totalScore >= 85) {
  rating = 'A (Very Good)';
  ratingColor = 'lightgreen';
  recommendation = 'Approved Supplier - Monitor performance';
} else if (totalScore >= 75) {
  rating = 'B (Good)';
  ratingColor = 'yellow';
  recommendation = 'Approved with caution - Increase inspection frequency';
} else if (totalScore >= 65) {
  rating = 'C (Fair)';
  ratingColor = 'orange';
  recommendation = 'Under Review - Quality improvement plan required';
} else {
  rating = 'D (Poor)';
  ratingColor = 'red';
  recommendation = 'Not Recommended - Consider alternative suppliers';
}

// Risk assessment
let riskLevel;
if (analysis.failed_tests === 0 && historicalComplianceScore >= 95) {
  riskLevel = 'LOW';
} else if (analysis.failed_tests <= 1 && historicalComplianceScore >= 85) {
  riskLevel = 'MEDIUM';
} else {
  riskLevel = 'HIGH';
}

return {
  json: {
    ...item,
    vendor_score: {
      total_score: parseFloat(totalScore),
      rating: rating,
      rating_color: ratingColor,
      recommendation: recommendation,
      risk_level: riskLevel,

      score_breakdown: {
        historical_compliance: parseFloat(historicalComplianceScore.toFixed(2)),
        current_batch: parseFloat(currentBatchScore),
        on_time_delivery: onTimeDeliveryScore,
        documentation: documentationScore,
        severity_adjusted: severityScore
      },

      weights_applied: weights
    }
  }
};

// Generate approval certificate for compliant batches
const item = $input.item.json;
const now = new Date().toISOString();

const certificate = {
  certificate_number: `CERT-${item.batch_number}-${Date.now()}`,
  certificate_date: now,
  status: 'APPROVED',
  vendor_name: item.vendor_name,
  material_name: item.material_name,
  batch_number: item.batch_number,
  test_date: item.test_date,
  expiry_date: item.expiry_date,

  compliance_summary: {
    total_tests: item.analysis.total_tests,
    passed_tests: item.analysis.passed_tests,
    compliance_rate: `${item.analysis.compliance_rate}%`,
    vendor_score: item.vendor_score.total_score,
    vendor_rating: item.vendor_score.rating
  },

  approval_details: {
    approved_by: 'Quality Assurance Department',
    approval_date: now,
    valid_until: item.expiry_date,
    release_status: 'RELEASED FOR PRODUCTION',
    storage_conditions: 'Store in cool, dry place. Protect from light.'
  },

  remarks: item.vendor_score.recommendation,
  next_review_date: new Date(Date.now() + 90*24*60*60*1000).toISOString().split('T')[0]
};

return {
  json: {
    ...item,
    certificate: certificate,
    workflow_status: 'COMPLETED - APPROVED'
  }
};

// Generate rejection report for non-compliant batches
const item = $input.item.json;
const now = new Date().toISOString();

const rejectionReport = {
  rejection_number: `REJ-${item.batch_number}-${Date.now()}`,
  rejection_date: now,
  status: 'REJECTED',
  vendor_name: item.vendor_name,
  material_name: item.material_name,
  batch_number: item.batch_number,
  test_date: item.test_date,

  non_compliance_details: {
    failed_tests: item.analysis.failed_tests,
    compliance_rate: `${item.analysis.compliance_rate}%`,
    non_compliant_parameters: item.analysis.non_compliant_parameters,
    severity_score: item.analysis.severity_score
  },

  vendor_performance: {
    vendor_score: item.vendor_score.total_score,
    vendor_rating: item.vendor_score.rating,
    risk_level: item.vendor_score.risk_level,
    recommendation: item.vendor_score.recommendation
  },

  actions_required: [
    'Return batch to vendor',
    'Request investigation report from vendor',
    'Vendor must submit CAPA (Corrective and Preventive Action)',
    'Re-testing required before acceptance',
    'Update vendor quality file'
  ],

  rejection_details: {
    rejected_by: 'Quality Assurance Department',
    rejection_date: now,
    disposition: 'RETURN TO VENDOR',
    financial_impact: 'Debit note to be raised'
  },

  escalation: item.vendor_score.risk_level === 'HIGH' ? 'Escalate to QA Manager and Procurement Head' : 'Notify QA Supervisor',
  follow_up_date: new Date(Date.now() + 7*24*60*60*1000).toISOString().split('T')[0]
};

return {
  json: {
    ...item,
    rejection_report: rejectionReport,
    workflow_status: 'COMPLETED - REJECTED'
  }
};

// Generate comprehensive final report
const items = $input.all();

const summary = {
  workflow_execution: {
    execution_date: new Date().toISOString(),
    total_coa_processed: items.length,
    approved_batches: items.filter(i => i.json.analysis.is_compliant).length,
    rejected_batches: items.filter(i => !i.json.analysis.is_compliant).length
  },

  detailed_results: items.map(item => {
    const data = item.json;
    return {
      vendor_name: data.vendor_name,
      material_name: data.material_name,
      batch_number: data.batch_number,
      compliance_status: data.analysis.compliance_status,
      compliance_rate: `${data.analysis.compliance_rate}%`,
      vendor_score: data.vendor_score.total_score,
      vendor_rating: data.vendor_score.rating,
      risk_level: data.vendor_score.risk_level,
      failed_tests: data.analysis.failed_tests,
      non_compliant_params: data.analysis.non_compliant_parameters,
      recommendation: data.vendor_score.recommendation,
      certificate_or_rejection: data.analysis.is_compliant ? 
        data.certificate.certificate_number : 
        data.rejection_report.rejection_number
    };
  }),

  vendor_performance_summary: items.map(item => {
    const data = item.json;
    return {
      vendor_name: data.vendor_name,
      total_score: data.vendor_score.total_score,
      rating: data.vendor_score.rating,
      historical_compliance: data.vendor_score.score_breakdown.historical_compliance,
      current_batch_compliance: data.vendor_score.score_breakdown.current_batch,
      on_time_delivery: data.vendor_score.score_breakdown.on_time_delivery,
      documentation_quality: data.vendor_score.score_breakdown.documentation,
      risk_level: data.vendor_score.risk_level,
      total_batches_history: data.vendor_history.total_batches,
      compliant_batches_history: data.vendor_history.compliant_batches
    };
  }),

  system_statistics: {
    average_vendor_score: (items.reduce((sum, i) => sum + i.json.vendor_score.total_score, 0) / items.length).toFixed(2),
    overall_compliance_rate: ((items.filter(i => i.json.analysis.is_compliant).length / items.length) * 100).toFixed(2) + '%',
    high_risk_vendors: items.filter(i => i.json.vendor_score.risk_level === 'HIGH').length,
    vendors_requiring_action: items.filter(i => !i.json.analysis.is_compliant).length
  },

  action_items: items
    .filter(i => !i.json.analysis.is_compliant)
    .map(i => ({
      vendor: i.json.vendor_name,
      batch: i.json.batch_number,
      action: 'CAPA Required',
      priority: i.json.vendor_score.risk_level,
      follow_up: i.json.rejection_report.follow_up_date
    }))
};

return { json: summary };
