// Calculate total expenses by summing all cost categories and converting to base currency

const config = $('Workflow Configuration').first().json;
const exchangeData = $('Extract Currency Data').first().json;
const flightOptions = $('Aggregate Flight Options').first().json;
const conferenceDetails = $('Get Conference Details').first().json;

// Helper function to extract numeric value from string (handles currency symbols and formatting)
function extractAmount(value) {
  if (typeof value === 'number') return value;
  if (!value) return 0;

  // Remove currency symbols, commas, and extract number
  const cleaned = String(value).replace(/[^0-9.-]/g, '');
  return parseFloat(cleaned) || 0;
}

// Extract all cost components
const registrationFee = extractAmount(config.registrationFee);
const accommodationCost = extractAmount(config.accommodationCost);
const visaCost = extractAmount(config.visaCost);
const mealsCost = extractAmount(config.mealsCost);
const transportCost = extractAmount(config.transportCost);

// Parse flight quotations and find cheapest option
const flight1 = extractAmount(config.flightQuote1);
const flight2 = extractAmount(config.flightQuote2);
const flight3 = extractAmount(config.flightQuote3);

const flightQuotes = [
  { option: 'A', amount: flight1, original: config.flightQuote1 },
  { option: 'B', amount: flight2, original: config.flightQuote2 },
  { option: 'C', amount: flight3, original: config.flightQuote3 }
].filter(f => f.amount > 0);

// Find cheapest flight
const cheapestFlight = flightQuotes.reduce((min, current) => 
  current.amount < min.amount ? current : min, 
  flightQuotes[0] || { option: 'N/A', amount: 0, original: '0' }
);

// Calculate total in local currency
const totalLocalCurrency = 
  registrationFee + 
  cheapestFlight.amount + 
  accommodationCost + 
  visaCost + 
  mealsCost + 
  transportCost;

// Get exchange rate (default to 1 if not available)
const exchangeRate = exchangeData?.exchangeRate || 1;
const baseCurrency = exchangeData?.baseCurrency || 'USD';
const localCurrency = exchangeData?.localCurrency || 'USD';

// Convert to USD
const totalInUSD = totalLocalCurrency * exchangeRate;

// Create detailed breakdown
const breakdown = {
  registrationFee: {
    amount: registrationFee,
    amountUSD: registrationFee * exchangeRate,
    original: config.registrationFee
  },
  flight: {
    amount: cheapestFlight.amount,
    amountUSD: cheapestFlight.amount * exchangeRate,
    original: cheapestFlight.original,
    option: cheapestFlight.option
  },
  accommodation: {
    amount: accommodationCost,
    amountUSD: accommodationCost * exchangeRate,
    original: config.accommodationCost
  },
  visa: {
    amount: visaCost,
    amountUSD: visaCost * exchangeRate,
    original: config.visaCost
  },
  meals: {
    amount: mealsCost,
    amountUSD: mealsCost * exchangeRate,
    original: config.mealsCost
  },
  transport: {
    amount: transportCost,
    amountUSD: transportCost * exchangeRate,
    original: config.transportCost
  }
};

// Currency conversion details
const currencyConversion = {
  localCurrency: localCurrency,
  baseCurrency: baseCurrency,
  exchangeRate: exchangeRate,
  totalLocal: totalLocalCurrency,
  totalUSD: totalInUSD,
  conversionDate: exchangeData?.date || new Date().toISOString().split('T')[0]
};

