// Helper function to parse flight quote string
// Expected format examples:
// "Airline Name - $1,200 - Route: City A to City B - Duration: 8h 30m"
// "Airline: XYZ Airways, Price: $850, Route: NYC-LON, Duration: 7h"
function parseFlightQuote(quoteString, quoteNumber) {
  if (!quoteString || quoteString.includes('<__PLACEHOLDER_VALUE__')) {
    return {
      quoteNumber: quoteNumber,
      airline: `[Airline ${quoteNumber}]`,
      price: '[Price]',
      route: '[Route]',
      duration: '[Duration]',
      rawQuote: quoteString
    };
  }

  // Initialize parsed data
  let airline = '';
  let price = '';
  let route = '';
  let duration = '';

  // Try to extract airline (usually at the beginning)
  const airlineMatch = quoteString.match(/^([^-$,]+?)(?:\s*[-:,]|\s+\$)/i);
  if (airlineMatch) {
    airline = airlineMatch[1].trim();
  }

  // Try to extract price (look for currency symbols and numbers)
  const priceMatch = quoteString.match(/(?:price[:\s]*)?([\$£€¥]?\s*[\d,]+(?:\.\d{2})?(?:\s*[A-Z]{3})?)/i);
  if (priceMatch) {
    price = priceMatch[1].trim();
  }

  // Try to extract route
  const routeMatch = quoteString.match(/route[:\s]*([^-,]+?)(?:\s*[-,]|\s+duration|$)/i);
  if (routeMatch) {
    route = routeMatch[1].trim();
  } else {
    // Alternative: look for city codes or "to" pattern
    const routeAltMatch = quoteString.match(/([A-Z]{3}\s*-\s*[A-Z]{3}|[A-Za-z\s]+\s+to\s+[A-Za-z\s]+)/i);
    if (routeAltMatch) {
      route = routeAltMatch[1].trim();
    }
  }

  // Try to extract duration
  const durationMatch = quoteString.match(/duration[:\s]*([\d]+h?\s*[\d]*m?|[\d]+:[\d]+)/i);
  if (durationMatch) {
    duration = durationMatch[1].trim();
  } else {
    // Alternative: look for time patterns
    const durationAltMatch = quoteString.match(/([\d]+h\s*[\d]*m?|[\d]+:[\d]+)/i);
    if (durationAltMatch) {
      duration = durationAltMatch[1].trim();
    }
  }

  return {
    quoteNumber: quoteNumber,
    airline: airline || `[Airline ${quoteNumber}]`,
    price: price || '[Price]',
    route: route || '[Route]',
    duration: duration || '[Duration]',
    rawQuote: quoteString
  };
}

