// Extract expense categories
const categories = {
  registrationFee: parseFloat(expenseData.registrationFee || 0),
  flight: parseFloat(expenseData.flight || 0),
  accommodation: parseFloat(expenseData.accommodation || 0),
  visa: parseFloat(expenseData.visa || 0),
  meals: parseFloat(expenseData.meals || 0),
  transport: parseFloat(expenseData.transport || 0)
};

// Calculate total expenses
const totalExpenses = Object.values(categories).reduce((sum, amount) => sum + amount, 0);

// Calculate percentage distribution
const percentageBreakdown = {};
for (const [category, amount] of Object.entries(categories)) {
  percentageBreakdown[category] = totalExpenses > 0 ? ((amount / totalExpenses) * 100).toFixed(2) + '%' : '0%';
}

// Budget compliance check
const budgetLimit = parseFloat(expenseData.budgetLimit || 0);
const isWithinBudget = totalExpenses <= budgetLimit;
const budgetVariance = budgetLimit - totalExpenses;
const budgetVariancePercentage = budgetLimit > 0 ? ((budgetVariance / budgetLimit) * 100).toFixed(2) + '%' : '0%';

// Generate recommendations
const recommendations = [];
if (!isWithinBudget) {
  recommendations.push('Total expenses exceed budget limit. Consider reducing costs.');

  // Identify highest expense categories
  const sortedCategories = Object.entries(categories)
    .sort((a, b) => b[1] - a[1])
    .slice(0, 3);

  recommendations.push(`Highest expenses: ${sortedCategories.map(([cat, amt]) => `${cat} ($${amt})`).join(', ')}`);
} else {
  recommendations.push('Expenses are within budget.');
  recommendations.push(`Budget remaining: $${budgetVariance.toFixed(2)} (${budgetVariancePercentage})`);
}

// Return structured analysis
const analysis = {
  totalExpenses: totalExpenses.toFixed(2),
  breakdown: categories,
  percentageDistribution: percentageBreakdown,
  budgetCompliance: {
    budgetLimit: budgetLimit.toFixed(2),
    isWithinBudget: isWithinBudget,
    variance: budgetVariance.toFixed(2),
    variancePercentage: budgetVariancePercentage
  },
  recommendations: recommendations
};

return JSON.stringify(analysis, null, 2);

