function extractDurationMinutes(durationStr) {
  if (!durationStr) return 0;

  // Match patterns like "8h 30m", "8h", "30m", "8:30"
  const hoursMatch = durationStr.match(/(\d+)\s*h/);
  const minutesMatch = durationStr.match(/(\d+)\s*m/);
  const colonMatch = durationStr.match(/(\d+):(\d+)/);

  let totalMinutes = 0;

  if (colonMatch) {
    totalMinutes = parseInt(colonMatch[1]) * 60 + parseInt(colonMatch[2]);
  } else {
    if (hoursMatch) totalMinutes += parseInt(hoursMatch[1]) * 60;
    if (minutesMatch) totalMinutes += parseInt(minutesMatch[1]);
  }

  return totalMinutes;
}

// Parse flight quotations
const flights = [
  {
    option: 'A',
    price: extractAmount(config.flightQuote1),
    duration: extractDurationMinutes(config.flightQuote1),
    raw: config.flightQuote1
  },
  {
    option: 'B',
    price: extractAmount(config.flightQuote2),
    duration: extractDurationMinutes(config.flightQuote2),
    raw: config.flightQuote2
  },
  {
    option: 'C',
    price: extractAmount(config.flightQuote3),
    duration: extractDurationMinutes(config.flightQuote3),
    raw: config.flightQuote3
  }
].filter(f => f.price > 0);

if (flights.length === 0) {
  return 'No valid flight quotations available for comparison.';
}

// Find cheapest and fastest options
const cheapest = flights.reduce((min, f) => f.price < min.price ? f : min, flights[0]);
const fastest = flights.reduce((min, f) => f.duration < min.duration ? f : min, flights[0]);

// Calculate savings
const maxPrice = Math.max(...flights.map(f => f.price));
const savings = maxPrice - cheapest.price;
const savingsPercent = ((savings / maxPrice) * 100).toFixed(1);

// Format duration for display
function formatDuration(minutes) {
  const hours = Math.floor(minutes / 60);
  const mins = minutes % 60;
  return mins > 0 ? `${hours}h ${mins}m` : `${hours}h`;
}

// Build comparison table
let comparison = '\n=== FLIGHT COMPARISON ===\n\n';

flights.forEach(f => {
  comparison += `Option ${f.option}: $${f.price.toFixed(2)} - ${formatDuration(f.duration)}`;
  if (f.option === cheapest.option) comparison += ' ✓ CHEAPEST';
  if (f.option === fastest.option) comparison += ' ⚡ FASTEST';
  comparison += '\n';
});

comparison += `\n--- RECOMMENDATION ---\n`;
comparison += `Best Value: Option ${cheapest.option} at $${cheapest.price.toFixed(2)}\n`;
comparison += `Savings: $${savings.toFixed(2)} (${savingsPercent}% less than most expensive)\n`;

if (cheapest.option === fastest.option) {
  comparison += `\n⭐ Option ${cheapest.option} is both the cheapest AND fastest - HIGHLY RECOMMENDED\n`;
} else {
  const timeDiff = fastest.duration - cheapest.duration;
  comparison += `\nNote: Option ${fastest.option} is ${formatDuration(Math.abs(timeDiff))} faster but costs $${(fastest.price - cheapest.price).toFixed(2)} more.\n`;
}

return comparison;
