const ordersData = $input.all()[0].json;
const customersData = $input.all()[1].json;

const customerSLAMap = {};
if (customersData.customers) {
  customersData.customers.forEach(customer => {
    customerSLAMap[customer.customer_id] = {
      sla_tier: customer.sla_tier || 'bronze',
      delivery_days: customer.delivery_days || 7,
      priority_multiplier: customer.priority_multiplier || 1,
      profit_margin: customer.profit_margin || 20
    };
  });
}

const tierWeights = {
  'platinum': 100,
  'gold': 75,
  'silver': 50,
  'bronze': 25,
  'standard': 10
};

const enrichedOrders = ordersData.orders.map(order => {
  const customerSLA = customerSLAMap[order.customer_id] || {
    sla_tier: 'bronze',
    delivery_days: 7,
    priority_multiplier: 1,
    profit_margin: 20
  };

  const orderDate = new Date(order.created_at);
  const slaDeadline = new Date(orderDate);
  slaDeadline.setDate(slaDeadline.getDate() + customerSLA.delivery_days);
  const daysUntilBreach = Math.ceil((slaDeadline - new Date()) / (1000 * 60 * 60 * 24));

  const urgencyScore = daysUntilBreach <= 0 ? 1000 : (100 / Math.max(daysUntilBreach, 1));
  const tierScore = tierWeights[customerSLA.sla_tier.toLowerCase()] || 10;
  const valueScore = Math.min((order.total_amount || 0) / 100, 100);
  const marginScore = (customerSLA.profit_margin || 20);

  const priorityScore = Math.round(
    (urgencyScore * 0.4) + 
    (tierScore * 0.3) + 
    (valueScore * 0.2) + 
    (marginScore * 0.1)
  ) * customerSLA.priority_multiplier;

  let urgencyLevel = 'normal';
  if (daysUntilBreach <= 0) urgencyLevel = 'critical';
  else if (daysUntilBreach <= 1) urgencyLevel = 'urgent';
  else if (daysUntilBreach <= 2) urgencyLevel = 'high';

  return {
    ...order,
    sla_info: {
      tier: customerSLA.sla_tier,
      delivery_days: customerSLA.delivery_days,
      deadline: slaDeadline.toISOString(),
      days_until_breach: daysUntilBreach,
      urgency_level: urgencyLevel
    },
    scoring: {
      priority_score: priorityScore,
      urgency_score: Math.round(urgencyScore),
      tier_score: tierScore,
      value_score: Math.round(valueScore),
      margin_score: marginScore
    },
    profit_margin: customerSLA.profit_margin
  };
});

enrichedOrders.sort((a, b) => b.scoring.priority_score - a.scoring.priority_score);

return enrichedOrders.map(order => ({ json: order }));

