const items = $input.all();
const ordersByUrgency = {
  high: [],
  normal: [],
  low: []
};

items.forEach(item => {
  const order = item.json;
  const urgency = order.sla_info.urgency_level;

  if (urgency === 'high' || urgency === 'urgent') {
    ordersByUrgency.high.push(order);
  } else if (order.sla_info.days_until_breach <= 4) {
    ordersByUrgency.normal.push(order);
  } else {
    ordersByUrgency.low.push(order);
  }
});

const now = new Date();
const schedules = [];

ordersByUrgency.high.forEach((order, index) => {
  const scheduledTime = new Date(now.getTime() + (index * 30 * 60000));
  schedules.push({
    ...order,
    scheduled_time: scheduledTime.toISOString(),
    time_slot: 'high_priority',
    estimated_pick_duration: 45
  });
});

ordersByUrgency.normal.forEach((order, index) => {
  const scheduledTime = new Date(now.getTime() + ((index + 8) * 60 * 60000));
  schedules.push({
    ...order,
    scheduled_time: scheduledTime.toISOString(),
    time_slot: 'normal',
    estimated_pick_duration: 30
  });
});

ordersByUrgency.low.forEach((order, index) => {
  const scheduledTime = new Date(now.getTime() + ((index + 24) * 60 * 60000));
  schedules.push({
    ...order,
    scheduled_time: scheduledTime.toISOString(),
    time_slot: 'standard',
    estimated_pick_duration: 30
  });
});

return [{ json: { 
  schedules: schedules, 
  summary: {
    total_orders: schedules.length,
    high_priority: ordersByUrgency.high.length,
    normal_priority: ordersByUrgency.normal.length,
    low_priority: ordersByUrgency.low.length
  }
}}];

