const scheduleData = $input.first().json;
const pickingTasks = $input.all()[1].json;

const dispatchSchedule = scheduleData.schedules.map(order => {
  const pickingTime = new Date(order.scheduled_time);
  const dispatchTime = new Date(pickingTime.getTime() + 
    ((order.estimated_pick_duration + 45) * 60000));

  let shippingMethod = 'standard';
  if (order.sla_info.urgency_level === 'critical' || order.sla_info.urgency_level === 'urgent') {
    shippingMethod = 'express';
  } else if (order.sla_info.urgency_level === 'high') {
    shippingMethod = 'priority';
  }

  return {
    order_id: order.order_id,
    customer_id: order.customer_id,
    dispatch_time: dispatchTime.toISOString(),
    shipping_method: shippingMethod,
    carrier: order.preferred_carrier || 'auto_select',
    sla_deadline: order.sla_info.deadline,
    priority: order.scoring.priority_score,
    customer_tier: order.sla_info.tier
  };
});

const dispatchSlots = {};
dispatchSchedule.forEach(dispatch => {
  const hour = new Date(dispatch.dispatch_time).toISOString().slice(0, 13);
  if (!dispatchSlots[hour]) dispatchSlots[hour] = [];
  dispatchSlots[hour].push(dispatch);
});

return [{ json: {
  dispatch_schedule: dispatchSchedule,
  dispatch_slots: dispatchSlots,
  total_dispatches: dispatchSchedule.length
}}];

