parameters.jsonBody:
={
  "model": "sora-2-image-to-video",
  "input": {
    "prompt": "{{ $json.output.replace(/\r?\n|\r/g, ' ').replace(/"/g, '\\"') }}",
    "image_urls": [
      "{{
  $('Get row(s) in sheet').item.json['Image Link'].match(/\/d\/(.*?)\//)
    ? 'https://drive.google.com/uc?export=download&id=' + $('Get row(s) in sheet').item.json['Image Link'].match(/\/d\/(.*?)\//)[1]
    : $('Get row(s) in sheet').item.json['Image Link']
}}"
    ],
    "aspect_ratio": "portrait",
    "n_frames": "10",
    "remove_watermark": true
  }
}
