const j = $input.first().json || {};
const sid = j.sessionId || 'test-session-1';
const text = j.text || j.message || j.input || JSON.stringify(j);
return [{ json: { ...j, sessionId: String(sid), text } }];

// Read config from Set Config node (stable across items)
const cfgNode = $nodes['Set Config1']?.data?.json || {};
const cfg = {
  MAX_LEN: Number(cfgNode.MAX_LEN ?? 600),
  EMOJI_ALLOWED: cfgNode.EMOJI_ALLOWED === false ? false : Boolean(cfgNode.EMOJI_ALLOWED),
  SIGNOFF: String(cfgNode.SIGNOFF ?? ''),
  BLOCK_LINKS: cfgNode.BLOCK_LINKS === undefined ? true : Boolean(cfgNode.BLOCK_LINKS)
};

const input = $input.first().json || {};
let out = { ...input };

out.sentiment = (out.sentiment || '').toLowerCase();
out.tone = (out.tone || '').toLowerCase();
out.reply = (out.reply || '').toString();
out.confidence = (typeof out.confidence === 'number') ? out.confidence : 0.6;
out.needs_handover = !!out.needs_handover;

if (cfg.BLOCK_LINKS) {
  out.reply = out.reply
    .replace(/https?:\/\/\S+/gi, '')
    .replace(/#[\w-]+/g, '')
    .replace(/\s{2,}/g, ' ');
}

if (cfg.EMOJI_ALLOWED === false) {
  out.reply = out.reply.replace(/[\p{Extended_Pictographic}\p{Emoji_Component}]/gu, '');
}

out.reply = out.reply
  .replace(/[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}/ig, '[email hidden]')
  .replace(/\b\+?\d[\d\s().-]{6,}\b/g, '[phone hidden]');

const maxLen = Number(cfg.MAX_LEN || 600);
if (out.reply.length > maxLen) {
  out.reply = out.reply.slice(0, maxLen - 1).replace(/\s+\S*$/, '') + '‚Ä¶';
}

if (cfg.SIGNOFF && !/\b(best|thanks|regards)\b/i.test(out.reply)) {
  out.reply = out.reply.trim() + "\n" + cfg.SIGNOFF;
}

return [{ json: out }];

// Read RISK_WORDS from Set Config node
const cfgNode = $nodes['Set Config']?.data?.json || {};
const riskWords = Array.isArray(cfgNode.RISK_WORDS) ? cfgNode.RISK_WORDS : [];

const escapeRegex = (s) => String(s).replace(/[.*+?^${}()|[\]\\]/g, '\\$&');
const riskPattern = riskWords.length ? '(' + riskWords.map(escapeRegex).join('|') + ')' : '(?!)';
const risk = new RegExp(riskPattern, 'i');

const j = $input.first().json || {};
const originalText = j.text || j.message || j.input || '';

const lowConfidence = (j.confidence || 0) < 0.45;
const highRisk = risk.test(originalText) || risk.test(j.reply || '');
const veryNegative = (j.sentiment || '') === 'negative';

const needsHandover = Boolean(j.needs_handover || lowConfidence || highRisk || veryNegative);

return [{ json: { ...j, needs_handover: needsHandover, _debug: { lowConfidence, highRisk, veryNegative, originalText: String(originalText).substring(0,120), riskWordsUsed: riskWords } } }];
