// n8n Code Node
// Purpose: Get the last row with status = "pending" from Google Sheets input data

// Get input data from previous node
const data = $input.all().map(item => item.json);

// Filter to only "pending" posts
const pendingRows = data.filter(item => item.status === "pending");

// If no pending rows, return an error message
if (pendingRows.length === 0) {
  return [{ message: "No pending rows found" }];
}

// Get the last pending row (highest row_number)
const lastRow = pendingRows.reduce((latest, current) => {
  return current.row_number > latest.row_number ? current : latest;
});

// Return the last pending row
return [{ json: lastRow }];

