// Input: Facebook Graph API response
const posts = $json.data || [];
return posts.map(post => {
  return {
    id: post.id,
    message: post.message || "",
    created_time: post.created_time,
    image: post.full_picture || "",
    post_url: post.permalink_url,
    likes: post.likes?.summary?.total_count || 0,
    comments: post.comments?.summary?.total_count || 0,
    reactions: post.reactions?.summary?.total_count || 0,
    author: post.from?.name || "",
    category: post.from?.category || ""
  };
});

const tweets = $json["data"] || [];
const media = ($json["includes"] && $json["includes"]["media"]) || [];

const mediaMap = {};
for (const m of media) mediaMap[m.media_key] = m;

const formatted = tweets.map(t => {
  const urls = t.entities?.urls?.map(u => u.expanded_url).join(', ') || '';
  const hashtags = t.entities?.hashtags?.map(h => `#${h.tag}`).join(' ') || '';
  const mediaUrls = (t.attachments?.media_keys || [])
    .map(k => mediaMap[k]?.url || mediaMap[k]?.preview_image_url)
    .filter(Boolean)
    .join(', ');

  return {
    id: t.id,
    text: t.text,
    created_at: t.created_at,
    hashtags,
    retweets: t.public_metrics?.retweet_count || 0,
    likes: t.public_metrics?.like_count || 0,
    impressions: t.public_metrics?.impression_count || 0,
    media: mediaUrls,
    urls,
  };
});

return formatted.map(f => ({ json: f }));

// Combine all incoming data from Twitter + Facebook into one single item
const allItems = $input.all().map(item => item.json);

// Structure data by platform
const twitterPosts = allItems.filter(i => i.text).map(t => ({
  id: t.id,
  text: t.text,
  created_at: t.created_at,
  hashtags: t.hashtags,
  retweets: t.retweets,
  likes: t.likes,
  impressions: t.impressions,
  media: t.media,
  urls: t.urls
}));

const facebookPosts = allItems.filter(i => i.message).map(f => ({
  id: f.id,
  message: f.message,
  created_time: f.created_time,
  image: f.image,
  post_url: f.post_url,
  likes: f.likes,
  comments: f.comments,
  reactions: f.reactions,
  author: f.author,
  category: f.category
}));

// Single combined JSON item
return [
  {
    json: {
      summary: {
        total_twitter_posts: twitterPosts.length,
        total_facebook_posts: facebookPosts.length
      },
      twitter_data: twitterPosts,
      facebook_data: facebookPosts
    }
  }
];
