const items = $input.all();
const companies = new Set();

items.forEach(item => {
  item.json.attendees?.forEach(attendee => {
    const domain = attendee.email?.split('@')[1];
    if (domain && domain !== 'explorium.ai' && domain !== 'resource.calendar.google.com') {
      companies.add(domain);
    }
  });
});

return Array.from(companies).map(company => ({
  json: { company_domain: company }
}));

