const email = $input.item.json.email;
const domain = email.split('@')[1];

return [{
  json: {
    email: email,
    domain: domain
  }
}];

const items = $input.all();
const eventsWithExternalAttendees = [];
items.forEach(item => {
  // Filter attendees to only external domains --> REPLACE 'explorium.ai' with your domain
  const externalAttendees = item.json.attendees?.filter(attendee => {
    const domain = attendee.email?.split('@')[1];
    return domain && domain !== 'explorium.ai' && domain !== 'resource.calendar.google.com';
  }) || [];

  // Only include events that have external attendees
  if (externalAttendees.length > 0) {
    // Extract unique company domains from this event
    const companyDomains = [...new Set(externalAttendees.map(a => a.email.split('@')[1]))];

    eventsWithExternalAttendees.push({
      ...item.json,
      external_attendees: externalAttendees,
      company_domains: companyDomains,
      external_attendee_count: externalAttendees.length
    });
  }
});
return eventsWithExternalAttendees.map(event => ({
  json: event
}));

// Get ALL input items
const allItems = $input.all();
console.log("Number of items:", allItems.length);

// First item contains enrichmentsResponse array
const enrichmentsArray = allItems[0]?.json?.enrichmentsResponse || [];
const enrichedData = allItems[0]?.json?.enriched_data?.[0]?.data;

// Second item contains events
const eventsData = allItems[1]?.json?.output_events || [];

console.log("Number of enrichments:", enrichmentsArray.length);
console.log("Number of events:", eventsData.length);

// Extract each enrichment by type
const firmographics = enrichmentsArray.find(e => e.enrichment_type === "firmographics")?.response?.data?.[0]?.data;
const websiteChanges = enrichmentsArray.find(e => e.enrichment_type === "website_changes")?.response?.data || [];
const technographics = enrichmentsArray.find(e => e.enrichment_type === "technographics")?.response?.data?.[0]?.data;

console.log("Company:", firmographics?.name);
console.log("Website changes:", websiteChanges.length);
console.log("Tech stack items:", technographics?.full_tech_stack?.length);

const structuredInput = {
  company_profile: {
    name: firmographics?.name || "Unknown",
    description: firmographics?.business_description || "",
    website: firmographics?.website || "",
    industry: firmographics?.linkedin_industry_category || "",
    employees: firmographics?.number_of_employees_range || "",
    revenue: firmographics?.yearly_revenue_range || "",
    headquarters: `${firmographics?.city_name || ""}, ${firmographics?.region_name || ""}, ${firmographics?.country_name || ""}`,
    ticker: firmographics?.ticker || "",
    linkedin: firmographics?.linkedin_profile || "",
    logo: firmographics?.business_logo || ""
  },

  recent_website_changes: websiteChanges.map(change => ({
    what_changed: change.data?.change_description || "",
    business_implication: change.data?.change_implication || "",
    detected_date: change.data?.date || "",
    url: change.data?.url || ""
  })),

  technology_stack: {
    full_stack: technographics?.full_tech_stack || [],
    by_category: technographics?.full_nested_tech_stack || [],
    bi_and_analytics: technographics?.bi_and_analytics || [],
    sales: technographics?.sales || [],
    marketing: technographics?.marketing || []
  },

  recent_events: eventsData.map(event => ({
    event_type: event.event_name || "",
    date: event.event_time || "",
    description: event.data?.description || event.data?.product_description || event.data?.award_reason || "",
    details: event.data || {}
  }))
};

console.log("Structured data created successfully");

return [{ json: structuredInput }];

const items = $input.all();
const allEmails = [];

items.forEach(item => {
  const externalAttendees = item.json.external_attendees || [];
  externalAttendees.forEach(attendee => {
    allEmails.push(attendee.email);
  });
});

console.log("Total external attendees:", allEmails.length);

return allEmails.map(email => ({ json: { email: email } }));

// Get all items
const allItems = $input.all();
console.log("Number of items:", allItems.length);

// First item contains enrichmentsResponse array
const enrichmentsArray = allItems[0]?.json?.enrichmentsResponse || [];
console.log("Number of enrichments:", enrichmentsArray.length);

// Extract each enrichment by type
const profileEnrichment = enrichmentsArray.find(e => e.enrichment_type === "profiles")?.response?.data?.[0]?.data;
const contactsEnrichment = enrichmentsArray.find(e => e.enrichment_type === "contacts")?.response?.data?.[0]?.data;
const postsEnrichment = enrichmentsArray.find(e => e.enrichment_type === "linkedin_posts")?.response?.data || [];

// Use contacts for email/phone, profiles for other data
const profileData = profileEnrichment || contactsEnrichment;
const contactData = contactsEnrichment;

console.log("Person:", profileData?.full_name);
console.log("LinkedIn posts:", postsEnrichment.length);

// Get current role (the one marked as primary or most recent)
const currentRole = profileData?.experience?.find(exp => exp.is_primary) || profileData?.experience?.[0];

const structuredInput = {
  personal_info: {
    name: profileData?.full_name || "Unknown",
    location: `${profileData?.city || ""}, ${profileData?.region_name || ""}, ${profileData?.country_name || ""}`.trim().replace(/^,\s*|,\s*$/g, ''),
    linkedin: profileData?.linkedin || "",
    age_group: profileData?.age_group || "",
    gender: profileData?.gender || ""
  },

  contact_info: {
    email: contactData?.professions_email || "",
    email_status: contactData?.professional_email_status || "",
    all_emails: contactData?.emails || [],
    mobile_phone: contactData?.mobile_phone || "",
    all_phones: contactData?.phone_numbers || []
  },

  current_role: {
    title: currentRole?.title?.name || profileData?.job_title || "",
    company: currentRole?.company?.name || profileData?.company_name || "",
    company_website: currentRole?.company?.website || profileData?.company_website || "",
    company_linkedin: profileData?.company_linkedin || "",
    department: profileData?.job_department || "",
    seniority: profileData?.job_seniority_level || "",
    start_date: currentRole?.start_date || "",
    summary: currentRole?.summary || ""
  },

  career_history: (profileData?.experience || [])
    .filter(exp => !exp.is_primary) // Exclude current role
    .map(exp => ({
      title: exp.title?.name || "",
      company: exp.company?.name || "",
      company_website: exp.company?.website || "",
      start_date: exp.start_date || "",
      end_date: exp.end_date || "",
      summary: exp.summary || ""
    })),

  education: (profileData?.education || []).map(edu => ({
    school: edu.school?.name || "School information not available",
    degrees: edu.degrees || [],
    majors: edu.majors || [],
    start_date: edu.start_date || "",
    end_date: edu.end_date || ""
  })),

  skills: profileData?.skills || [],

  recent_linkedin_activity: postsEnrichment.map(post => ({
    text: post.data?.post_text || "",
    date: post.data?.created_at || "",
    days_ago: post.data?.days_since_posted || 0,
    engagement: {
      likes: post.data?.number_of_likes || 0,
      comments: post.data?.number_of_comments || 0
    },
    url: post.data?.post_url || ""
  }))
};

console.log("Structured data for:", structuredInput.personal_info.name);
console.log("Email:", structuredInput.contact_info.email);
console.log("Current role:", structuredInput.current_role.title);

return [{ json: structuredInput }];

// Formats structured output for Google Docs with title and bold sections
const rawData = $input.item.json;
const data = Array.isArray(rawData) ? rawData[0].output : (rawData.output || rawData);

// Extract company name from first bullet of company overview or use a passed parameter
let companyName = 'Sales Research'; // Default fallback
if (data.company_overview && data.company_overview.length > 0) {
  // Try to extract company name from first sentence
  const firstItem = data.company_overview[0];
  const match = firstItem.match(/^([A-Z][a-zA-Z\s&]+?)(?:\s+is|\s+operates|\s+with|\s+-)/);
  if (match) {
    companyName = match[1].trim();
  }
}

// Extract domain from calendar event
let domain = '';
try {
  const calendarEvent = $('Loop Over Items').first().json;
  // Get first domain from company_domains array
  if (calendarEvent.company_domains && calendarEvent.company_domains.length > 0) {
    domain = calendarEvent.company_domains[0];
  }
} catch (error) {
  console.log('Could not extract domain:', error);
}

let docContent = '';

// Add title with visual emphasis (Google Docs doesn't support direct markdown)
docContent += '═══════════════════════════════════════\n';
// docContent += `${companyName.toUpperCase()}\n`;
docContent += `${$('Loop Over Items').first().json.summary.toUpperCase()}\n`;
docContent += '═══════════════════════════════════════\n\n';

// Helper function to safely add section with bold-style headers
function addSection(title, items) {
  if (items && Array.isArray(items) && items.length > 0) {
    docContent += `━━━ ${title.toUpperCase()} ━━━\n`;
    items.forEach(item => {
      docContent += `• ${item}\n`;
    });
    docContent += '\n';
  }
}

addSection('Company Overview', data.company_overview);
addSection('Website Changes', data.website_changes);
addSection('Tech Stack & Product Focus', data.tech_stack_product_focus);
addSection('Potential Pain Points', data.potential_pain_points);

// Handle nested explorium_opportunity object
if (data.explorium_opportunity) {
  docContent += '━━━ EXPLORIUM OPPORTUNITY ━━━\n';

  if (data.explorium_opportunity.relevance && data.explorium_opportunity.relevance.length > 0) {
    docContent += '→ Why Explorium is relevant:\n';
    data.explorium_opportunity.relevance.forEach(item => {
      docContent += `• ${item}\n`;
    });
  }

  if (data.explorium_opportunity.resonant_capabilities && data.explorium_opportunity.resonant_capabilities.length > 0) {
    docContent += '\n→ Best Fit Capabilities:\n';
    data.explorium_opportunity.resonant_capabilities.forEach(item => {
      docContent += `• ${item}\n`;
    });
  }
  docContent += '\n';
}

addSection('Conversation Starters', data.conversation_starters);

return [{
  json: {
    doc_content: docContent,
    company_name: companyName,
    domain: domain  // Added domain key
  }
}];

// Formats attendee research as subsection for Google Docs
const rawData = $input.first().json;
const data = Array.isArray(rawData) ? rawData[0].output : (rawData.output || rawData);

// Extract domain from the Extract External Company Domains1 node
let domain = '';
try {
  domain = $('Extract External Company Domains1').first().json.domain || '';
} catch (error) {
  console.log('Could not extract domain:', error);
}

let docContent = '';

// Add attendee name as subsection title
docContent += `\n▬▬▬▬▬▬▬▬▬▬▬▬▬▬▬▬▬▬▬▬▬▬▬▬▬▬▬▬▬▬▬▬▬\n`;
docContent += `ATTENDEE: ${data.attendee_name.toUpperCase()}\n`;
docContent += `▬▬▬▬▬▬▬▬▬▬▬▬▬▬▬▬▬▬▬▬▬▬▬▬▬▬▬▬▬▬▬▬▬\n\n`;

// Background section
if (data.background) {
  docContent += '━━━ Background ━━━\n';

  if (data.background.current_role && data.background.current_role.length > 0) {
    docContent += '→ Current Role:\n';
    data.background.current_role.forEach(item => {
      docContent += `  • ${item}\n`;
    });
