  if (data.background.previous_experience && data.background.previous_experience.length > 0) {
    docContent += '→ Previous Experience:\n';
    data.background.previous_experience.forEach(item => {
      docContent += `  • ${item}\n`;
    });
  }

  if (data.background.education && data.background.education.length > 0) {
    docContent += '→ Education:\n';
    data.background.education.forEach(item => {
      docContent += `  • ${item}\n`;
    });
  }

  docContent += '\n';
}

// Recent Activity & Interests
if (data.recent_activity_interests && data.recent_activity_interests.length > 0) {
  docContent += '━━━ Recent Activity & Interests ━━━\n';
  data.recent_activity_interests.forEach(item => {
    docContent += `  • ${item}\n`;
  });
  docContent += '\n';
}

// Conversation Angles
if (data.conversation_angles && data.conversation_angles.length > 0) {
  docContent += '━━━ Conversation Angles ━━━\n';
  data.conversation_angles.forEach(item => {
    docContent += `  • ${item}\n`;
  });
  docContent += '\n';
}

// How to Approach
if (data.how_to_approach) {
  docContent += '━━━ How to Approach ━━━\n';

  if (data.how_to_approach.pain_points && data.how_to_approach.pain_points.length > 0) {
    docContent += '→ Likely Pain Points:\n';
    data.how_to_approach.pain_points.forEach(item => {
      docContent += `  • ${item}\n`;
    });
  }

  if (data.how_to_approach.relevant_capabilities && data.how_to_approach.relevant_capabilities.length > 0) {
    docContent += '→ Relevant Explorium Capabilities:\n';
    data.how_to_approach.relevant_capabilities.forEach(item => {
      docContent += `  • ${item}\n`;
