// --- Helpers ---------------------------------------------------------------

/** Extract an array of Unstructured elements from various possible shapes */
function extractElements(obj) {
  if (!obj || typeof obj !== "object") return [];
  // Some HTTP Request nodes return data under json.body / json.response / json.data
  const candidates = [obj, obj.body, obj.response, obj.data];
  for (const c of candidates) {
    if (Array.isArray(c)) return c;
  }
  // Single element fallback: an object with .type and .text
  if (obj.type && typeof obj.text === "string") return [obj];
  return [];
}

// --- Main ------------------------------------------------------------------

const rawItems = $input.all();

let elements = [];
for (const item of rawItems) {
  const base = item?.json ?? {};
  const found = extractElements(base);
  if (found.length === 0) {
    // Extra fallbacks: sometimes APIs return { result: [...] } or { elements: [...] }
    const fallback =
      (Array.isArray(base?.result) && base.result) ||
      (Array.isArray(base?.elements) && base.elements) ||
      [];
    elements.push(...fallback);
  } else {
    elements.push(...found);
  }
}

// Defensive flatten & null filtering
elements = elements.flat().filter(Boolean);

// Transform Unstructured elements -> Documents
const docs = [];
for (const element of elements) {
  const txt = (element?.text ?? "").trim();
  if (!txt) continue; // skip empty strings only

  const md = element?.metadata || {};
  // Remove very heavy fields that shouldn't be indexed
  if ("orig_elements" in md) delete md.orig_elements;

  docs.push({
    element_id: element.element_id,
    type: element.type,
    text: txt,
    filename: md.filename,
    page_number: md.page_number
  });
}

// Emit one item per document for the Default Data Loader
return docs.map(d => ({ json: d }));

const items = $input.all();
if (!items.length) {
  return [{ json: { texts: [], metas: [] } }];
}

return [{
  json: {
    texts: items.map(i => i.json.text ?? ''),
    metas: items.map(i => {
      const meta = i.json || {};
      return meta;
    }),
  }
}];

const embsObj = $json;
const embsArr = embsObj?.data || [];

let metas = embsObj?.metas;
if (!Array.isArray(metas)) {
  metas = $item(0).$node["Pack"]?.json?.metas;
}
if (!Array.isArray(metas)) metas = [];

const n = Math.min(embsArr.length, metas.length);
const out = [];

for (let i = 0; i < n; i++) {
  const m = metas[i] || {};
  const emb = embsArr[i]?.embedding || [];

  out.push({
    json: {
      element_id: m.element_id,
      type: m.type,
      filename: m.filename,
      page_number: m.page_number,
      text: m.text,
      embedding: emb
    }
  });
}

return out;
