// Analyze Stock Trends - Calculate technical indicators

// Get stock data from previous node
const stockData = $input.all();

if (!stockData || stockData.length === 0) {
  return [{ json: { error: 'No stock data available' } }];
}

// Helper function to calculate Simple Moving Average
function calculateSMA(data, period) {
  if (data.length < period) return null;
  const sum = data.slice(-period).reduce((acc, val) => acc + val, 0);
  return sum / period;
}

// Helper function to calculate RSI (Relative Strength Index)
function calculateRSI(prices, period = 14) {
  if (prices.length < period + 1) return null;

  let gains = 0;
  let losses = 0;

  // Calculate initial average gain and loss
  for (let i = prices.length - period; i < prices.length; i++) {
    const change = prices[i] - prices[i - 1];
    if (change > 0) {
      gains += change;
    } else {
      losses += Math.abs(change);
    }
  }

  const avgGain = gains / period;
  const avgLoss = losses / period;

  if (avgLoss === 0) return 100;

  const rs = avgGain / avgLoss;
  const rsi = 100 - (100 / (1 + rs));

  return rsi;
}

// Helper function to find support and resistance levels
function findSupportResistance(prices, window = 5) {
  const levels = [];

  for (let i = window; i < prices.length - window; i++) {
    const current = prices[i];
    let isSupport = true;
    let isResistance = true;

    // Check if it's a local minimum (support)
    for (let j = i - window; j <= i + window; j++) {
      if (j !== i && prices[j] < current) {
        isSupport = false;
      }
      if (j !== i && prices[j] > current) {
        isResistance = false;
      }
    }

    if (isSupport) {
      levels.push({ type: 'support', price: current, index: i });
    }
    if (isResistance) {
      levels.push({ type: 'resistance', price: current, index: i });
    }
  }

  return levels;
}

// Extract price data (assuming the stock data has a 'close' or 'price' field)
const prices = stockData.map(item => item.json.close || item.json.price || 0);
const volumes = stockData.map(item => item.json.volume || 0);
const currentPrice = prices[prices.length - 1];

// Calculate Moving Averages
const sma20 = calculateSMA(prices, 20);
const sma50 = calculateSMA(prices, 50);
const sma200 = calculateSMA(prices, 200);

// Calculate RSI
const rsi = calculateRSI(prices, 14);

// Calculate Volume Trends
const avgVolume20 = calculateSMA(volumes, 20);
const currentVolume = volumes[volumes.length - 1];
const volumeTrend = avgVolume20 ? ((currentVolume - avgVolume20) / avgVolume20 * 100).toFixed(2) : 0;

// Find Support and Resistance Levels
const supportResistance = findSupportResistance(prices);
const recentSupport = supportResistance.filter(l => l.type === 'support').slice(-3);
const recentResistance = supportResistance.filter(l => l.type === 'resistance').slice(-3);

// Determine trend based on moving averages
let trend = 'Neutral';
if (sma20 && sma50) {
  if (currentPrice > sma20 && sma20 > sma50) {
    trend = 'Bullish';
  } else if (currentPrice < sma20 && sma20 < sma50) {
    trend = 'Bearish';
  }
}

// RSI interpretation
let rsiSignal = 'Neutral';
if (rsi !== null) {
  if (rsi > 70) {
    rsiSignal = 'Overbought';
  } else if (rsi < 30) {
    rsiSignal = 'Oversold';
  }
}

// Volume analysis
let volumeSignal = 'Normal';
if (volumeTrend > 50) {
  volumeSignal = 'High Volume (Bullish)';
} else if (volumeTrend < -50) {
  volumeSignal = 'Low Volume (Bearish)';
}

// Compile analysis results
const analysis = {
  currentPrice: currentPrice,
  movingAverages: {
    sma20: sma20 ? sma20.toFixed(2) : 'N/A',
    sma50: sma50 ? sma50.toFixed(2) : 'N/A',
    sma200: sma200 ? sma200.toFixed(2) : 'N/A'
  },
  rsi: rsi ? rsi.toFixed(2) : 'N/A',
  rsiSignal: rsiSignal,
  volumeTrend: {
    current: currentVolume,
    average20Day: avgVolume20 ? avgVolume20.toFixed(0) : 'N/A',
    percentChange: volumeTrend + '%',
    signal: volumeSignal
  },
  supportLevels: recentSupport.map(s => s.price.toFixed(2)),
  resistanceLevels: recentResistance.map(r => r.price.toFixed(2)),
  overallTrend: trend,
  timestamp: new Date().toISOString()
};

return [{ json: analysis }];

// Predict Future Trends using Simple Linear Regression
// This code analyzes historical stock data and generates buy/sell/hold recommendations

const items = $input.all();

// Helper function to perform simple linear regression
function linearRegression(data) {
  const n = data.length;
  let sumX = 0, sumY = 0, sumXY = 0, sumX2 = 0;

  data.forEach((point, index) => {
    const x = index;
    const y = point;
    sumX += x;
    sumY += y;
    sumXY += x * y;
    sumX2 += x * x;
  });

  const slope = (n * sumXY - sumX * sumY) / (n * sumX2 - sumX * sumX);
  const intercept = (sumY - slope * sumX) / n;

  return { slope, intercept };
}

// Helper function to predict future values
function predictFuture(regression, currentIndex, daysAhead) {
  const predictions = [];
  for (let i = 1; i <= daysAhead; i++) {
    const futureIndex = currentIndex + i;
    const predictedValue = regression.slope * futureIndex + regression.intercept;
    predictions.push(predictedValue);
  }
  return predictions;
}

// Helper function to generate recommendation
function generateRecommendation(slope, predictions, currentPrice) {
  const avgPrediction = predictions.reduce((a, b) => a + b, 0) / predictions.length;
  const priceChange = ((avgPrediction - currentPrice) / currentPrice) * 100;

  let recommendation = 'HOLD';
  let confidence = 'Medium';

  if (slope > 0 && priceChange > 5) {
    recommendation = 'BUY';
    confidence = priceChange > 10 ? 'High' : 'Medium';
  } else if (slope < 0 && priceChange < -5) {
    recommendation = 'SELL';
    confidence = priceChange < -10 ? 'High' : 'Low';
  }

  return { recommendation, confidence, priceChange: priceChange.toFixed(2) };
}

// Process each item
const results = items.map(item => {
  const stockData = item.json;

  // Extract historical prices (assuming they're in the data)
  const historicalPrices = stockData.historicalPrices || [];
  const currentPrice = stockData.currentPrice || historicalPrices[historicalPrices.length - 1];

  // Perform linear regression
  const regression = linearRegression(historicalPrices);

  // Predict next 7 days
  const predictions = predictFuture(regression, historicalPrices.length - 1, 7);

  // Generate recommendation
  const analysis = generateRecommendation(regression.slope, predictions, currentPrice);

  return {
    json: {
      ...stockData,
      prediction: {
        trend: regression.slope > 0 ? 'Upward' : regression.slope < 0 ? 'Downward' : 'Stable',
        slope: regression.slope.toFixed(4),
        predictions: predictions.map(p => p.toFixed(2)),
        recommendation: analysis.recommendation,
        confidence: analysis.confidence,
        expectedPriceChange: analysis.priceChange + '%',
        currentPrice: currentPrice,
        predictedPrice7Days: predictions[6].toFixed(2)
      }
    }
  };
});

return results;

