// Analyze News Sentiment
// Extract headlines and calculate sentiment scores

const items = $input.all();

if (!items || items.length === 0) {
  return [{ json: { error: 'No news data available', sentiment: 'neutral', score: 0 } }];
}

// Simple sentiment analysis using keyword matching
const positiveWords = [
  'gain', 'gains', 'profit', 'profits', 'surge', 'surges', 'rally', 'rallies',
  'rise', 'rises', 'rising', 'up', 'bullish', 'growth', 'strong', 'beat',
  'beats', 'outperform', 'outperforms', 'upgrade', 'upgrades', 'positive',
  'success', 'successful', 'record', 'high', 'highs', 'soar', 'soars',
  'jump', 'jumps', 'boost', 'boosts', 'optimistic', 'breakthrough', 'innovation'
];

const negativeWords = [
  'loss', 'losses', 'fall', 'falls', 'falling', 'drop', 'drops', 'decline',
  'declines', 'down', 'bearish', 'weak', 'weakness', 'miss', 'misses',
  'underperform', 'underperforms', 'downgrade', 'downgrades', 'negative',
  'concern', 'concerns', 'risk', 'risks', 'low', 'lows', 'plunge', 'plunges',
  'crash', 'crashes', 'pessimistic', 'warning', 'warnings', 'threat', 'threats'
];

// Function to calculate sentiment score for a text
function calculateSentiment(text) {
  if (!text) return { score: 0, sentiment: 'neutral' };

  const lowerText = text.toLowerCase();
  let positiveCount = 0;
  let negativeCount = 0;

  positiveWords.forEach(word => {
    const regex = new RegExp('\\b' + word + '\\b', 'gi');
    const matches = lowerText.match(regex);
    if (matches) positiveCount += matches.length;
  });

  negativeWords.forEach(word => {
    const regex = new RegExp('\\b' + word + '\\b', 'gi');
    const matches = lowerText.match(regex);
    if (matches) negativeCount += matches.length;
  });

  const totalWords = positiveCount + negativeCount;
  if (totalWords === 0) return { score: 0, sentiment: 'neutral' };

  // Calculate score from -1 (very negative) to +1 (very positive)
  const score = (positiveCount - negativeCount) / totalWords;

  let sentiment = 'neutral';
  if (score > 0.2) sentiment = 'positive';
  else if (score < -0.2) sentiment = 'negative';

  return { score, sentiment, positiveCount, negativeCount };
}

// Process all news articles
const articles = [];
let totalScore = 0;
let positiveArticles = 0;
let negativeArticles = 0;
let neutralArticles = 0;

items.forEach(item => {
  const headline = item.json.headline || item.json.title || '';
  const description = item.json.description || item.json.summary || '';
  const fullText = headline + ' ' + description;

  const sentimentResult = calculateSentiment(fullText);

  articles.push({
    headline: headline,
    sentiment: sentimentResult.sentiment,
    score: sentimentResult.score.toFixed(3),
    positiveWords: sentimentResult.positiveCount,
    negativeWords: sentimentResult.negativeCount
  });

  totalScore += sentimentResult.score;

  if (sentimentResult.sentiment === 'positive') positiveArticles++;
  else if (sentimentResult.sentiment === 'negative') negativeArticles++;
  else neutralArticles++;
});

// Calculate overall sentiment
const avgScore = items.length > 0 ? totalScore / items.length : 0;
let overallSentiment = 'neutral';
if (avgScore > 0.2) overallSentiment = 'positive';
else if (avgScore < -0.2) overallSentiment = 'negative';

// Calculate sentiment distribution percentages
const total = items.length;
const positivePercent = total > 0 ? ((positiveArticles / total) * 100).toFixed(1) : 0;
const negativePercent = total > 0 ? ((negativeArticles / total) * 100).toFixed(1) : 0;
const neutralPercent = total > 0 ? ((neutralArticles / total) * 100).toFixed(1) : 0;

// Generate sentiment rating (1-10 scale)
const sentimentRating = Math.round((avgScore + 1) * 5); // Convert -1 to 1 scale to 0-10

const result = {
  overallSentiment: overallSentiment,
  sentimentScore: avgScore.toFixed(3),
  sentimentRating: sentimentRating,
  totalArticles: items.length,
  distribution: {
    positive: positiveArticles,
    negative: negativeArticles,
    neutral: neutralArticles,
    positivePercent: positivePercent + '%',
    negativePercent: negativePercent + '%',
    neutralPercent: neutralPercent + '%'
  },
  articles: articles.slice(0, 10), // Include top 10 articles
  summary: `${overallSentiment.toUpperCase()} sentiment detected. ${positivePercent}% positive, ${negativePercent}% negative, ${neutralPercent}% neutral news coverage.`,
  timestamp: new Date().toISOString()
};

return [{ json: result }];

// Process Analyst Ratings - Aggregate recommendations and calculate consensus

const items = $input.all();

if (!items || items.length === 0) {
  return [{ json: { error: 'No analyst ratings data available' } }];
}

// Helper function to calculate average
function calculateAverage(numbers) {
  if (numbers.length === 0) return 0;
  const sum = numbers.reduce((acc, val) => acc + val, 0);
  return sum / numbers.length;
}

// Helper function to determine consensus rating
function determineConsensus(ratings) {
  const total = ratings.buy + ratings.hold + ratings.sell;
  if (total === 0) return 'No Consensus';

  const buyPercent = (ratings.buy / total) * 100;
  const sellPercent = (ratings.sell / total) * 100;

  if (buyPercent >= 60) return 'Strong Buy';
  if (buyPercent >= 40) return 'Buy';
  if (sellPercent >= 60) return 'Strong Sell';
  if (sellPercent >= 40) return 'Sell';
  return 'Hold';
}

// Process analyst ratings data
const results = items.map(item => {
  const data = item.json;

  // Initialize counters
  const ratings = {
    buy: 0,
    hold: 0,
    sell: 0
  };

  const priceTargets = [];

  // Extract ratings from the data
  // Assuming data structure has an array of analyst ratings
  const analystRatings = data.ratings || data.analystRatings || [];

  analystRatings.forEach(rating => {
    const recommendation = (rating.recommendation || rating.rating || '').toLowerCase();

    if (recommendation.includes('buy') || recommendation.includes('outperform') || recommendation.includes('overweight')) {
      ratings.buy++;
    } else if (recommendation.includes('sell') || recommendation.includes('underperform') || recommendation.includes('underweight')) {
      ratings.sell++;
    } else if (recommendation.includes('hold') || recommendation.includes('neutral')) {
      ratings.hold++;
    }

    // Collect price targets
    if (rating.priceTarget || rating.targetPrice) {
      priceTargets.push(parseFloat(rating.priceTarget || rating.targetPrice));
    }
  });

  // Calculate statistics
  const totalRatings = ratings.buy + ratings.hold + ratings.sell;
  const averagePriceTarget = calculateAverage(priceTargets);
  const consensusRating = determineConsensus(ratings);

  // Calculate price target range
  const minPriceTarget = priceTargets.length > 0 ? Math.min(...priceTargets) : null;
  const maxPriceTarget = priceTargets.length > 0 ? Math.max(...priceTargets) : null;

  // Calculate percentages
  const buyPercent = totalRatings > 0 ? ((ratings.buy / totalRatings) * 100).toFixed(1) : 0;
  const holdPercent = totalRatings > 0 ? ((ratings.hold / totalRatings) * 100).toFixed(1) : 0;
  const sellPercent = totalRatings > 0 ? ((ratings.sell / totalRatings) * 100).toFixed(1) : 0;

  return {
    json: {
      analystRatings: {
        summary: {
          totalAnalysts: totalRatings,
          buyRatings: ratings.buy,
          holdRatings: ratings.hold,
          sellRatings: ratings.sell,
          buyPercent: buyPercent + '%',
          holdPercent: holdPercent + '%',
          sellPercent: sellPercent + '%'
        },
        priceTargets: {
          average: averagePriceTarget ? averagePriceTarget.toFixed(2) : 'N/A',
          min: minPriceTarget ? minPriceTarget.toFixed(2) : 'N/A',
          max: maxPriceTarget ? maxPriceTarget.toFixed(2) : 'N/A',
          count: priceTargets.length
        },
        consensus: {
          rating: consensusRating,
          confidence: totalRatings >= 10 ? 'High' : totalRatings >= 5 ? 'Medium' : 'Low'
        },
        timestamp: new Date().toISOString()
      }
    }
  };
});

return results;

