const items = $input.all();

if (!items || items.length === 0) {
  return [{ json: { error: 'No social media data available' } }];
}

// Helper function to analyze sentiment from text
function analyzeSentiment(text) {
  if (!text) return 0;

  const positiveWords = ['bullish', 'buy', 'moon', 'rocket', 'gain', 'profit', 'up', 'rise', 'strong', 'good', 'great', 'excellent', 'positive', 'growth', 'winning', 'success'];
  const negativeWords = ['bearish', 'sell', 'crash', 'loss', 'down', 'fall', 'weak', 'bad', 'terrible', 'negative', 'decline', 'losing', 'failure', 'dump', 'drop'];

  const lowerText = text.toLowerCase();
  let score = 0;

  positiveWords.forEach(word => {
    const regex = new RegExp('\\b' + word + '\\b', 'gi');
    const matches = lowerText.match(regex);
    if (matches) score += matches.length;
  });

  negativeWords.forEach(word => {
    const regex = new RegExp('\\b' + word + '\\b', 'gi');
    const matches = lowerText.match(regex);
    if (matches) score -= matches.length;
  });

  return score;
}

// Helper function to extract hashtags
function extractHashtags(text) {
  if (!text) return [];
  const hashtagRegex = /#(\w+)/g;
  const matches = text.match(hashtagRegex);
  return matches ? matches.map(tag => tag.toLowerCase()) : [];
}

// Helper function to calculate engagement score
function calculateEngagement(item) {
  const likes = item.likes || item.favorites || 0;
  const retweets = item.retweets || item.shares || 0;
  const comments = item.comments || item.replies || 0;

  return likes + (retweets * 2) + (comments * 3);
}

// Process all social media posts
let totalSentiment = 0;
let positiveCount = 0;
let negativeCount = 0;
let neutralCount = 0;
const allHashtags = [];
const topPosts = [];

items.forEach(item => {
  const data = item.json;
  const text = data.text || data.content || data.message || '';

  // Calculate sentiment for this post
  const sentimentScore = analyzeSentiment(text);
  totalSentiment += sentimentScore;

  if (sentimentScore > 0) {
    positiveCount++;
  } else if (sentimentScore < 0) {
    negativeCount++;
  } else {
    neutralCount++;
  }

  // Extract hashtags
  const hashtags = extractHashtags(text);
  allHashtags.push(...hashtags);

  // Calculate engagement
  const engagement = calculateEngagement(data);

  topPosts.push({
    text: text.substring(0, 100),
    sentiment: sentimentScore,
    engagement: engagement,
    author: data.author || data.username || 'Unknown',
    timestamp: data.timestamp || data.created_at || new Date().toISOString()
  });
});

// Calculate overall sentiment
const totalPosts = items.length;
const avgSentiment = totalPosts > 0 ? totalSentiment / totalPosts : 0;
const sentimentPercentage = {
  positive: ((positiveCount / totalPosts) * 100).toFixed(1),
  negative: ((negativeCount / totalPosts) * 100).toFixed(1),
  neutral: ((neutralCount / totalPosts) * 100).toFixed(1)
};

// Determine community mood
let communityMood = 'Neutral';
if (avgSentiment > 2) {
  communityMood = 'Very Bullish';
} else if (avgSentiment > 0.5) {
  communityMood = 'Bullish';
} else if (avgSentiment < -2) {
  communityMood = 'Very Bearish';
} else if (avgSentiment < -0.5) {
  communityMood = 'Bearish';
}

// Find trending hashtags
const hashtagCounts = {};
allHashtags.forEach(tag => {
  hashtagCounts[tag] = (hashtagCounts[tag] || 0) + 1;
});

const trendingHashtags = Object.entries(hashtagCounts)
  .sort((a, b) => b[1] - a[1])
  .slice(0, 10)
  .map(([tag, count]) => ({ tag, count }));

// Sort top posts by engagement
topPosts.sort((a, b) => b.engagement - a.engagement);
const topEngagingPosts = topPosts.slice(0, 5);

// Compile analysis results
const analysis = {
  totalPosts: totalPosts,
  sentimentScore: avgSentiment.toFixed(2),
  communityMood: communityMood,
  sentimentBreakdown: sentimentPercentage,
  trendingHashtags: trendingHashtags,
  topEngagingPosts: topEngagingPosts,
  analysis: {
    bullishSignals: positiveCount,
    bearishSignals: negativeCount,
    neutralSignals: neutralCount
  },
  timestamp: new Date().toISOString()
};

return [{ json: analysis }];

