const items = $input.all();

if (!items || items.length === 0) {
  return [{ json: { error: 'No data available for analysis' } }];
}

// Extract data from merged inputs
const technicalAnalysis = items[0]?.json || {};
const newsSentiment = items[1]?.json || {};
const analystRatings = items[2]?.json || {};
const socialSentiment = items[3]?.json || {};

// Define weights for each analysis component
const WEIGHTS = {
  technical: 0.35,
  news: 0.25,
  analyst: 0.25,
  social: 0.15
};

// Helper function to convert sentiment/recommendation to numeric score (-100 to 100)
function convertToScore(data, type) {
  let score = 0;

  switch(type) {
    case 'technical':
      // Based on trend, RSI, and moving averages
      if (data.overallTrend === 'Bullish') score += 40;
      else if (data.overallTrend === 'Bearish') score -= 40;

      if (data.rsiSignal === 'Oversold') score += 30;
      else if (data.rsiSignal === 'Overbought') score -= 30;

      if (data.prediction?.recommendation === 'BUY') score += 30;
      else if (data.prediction?.recommendation === 'SELL') score -= 30;
      break;

    case 'news':
      // Based on news sentiment score
      const newsScore = data.sentimentScore || 0;
      score = newsScore * 100; // Assuming sentiment is between -1 and 1
      break;

    case 'analyst':
      // Based on analyst ratings (buy/hold/sell ratio)
      const buyRatio = data.buyRatio || 0;
      const sellRatio = data.sellRatio || 0;
      score = (buyRatio - sellRatio) * 100;
      break;

    case 'social':
      // Based on social media sentiment
      const socialScore = data.sentimentScore || 0;
      score = socialScore * 100;
      break;
  }

  return Math.max(-100, Math.min(100, score)); // Clamp between -100 and 100
}

// Calculate individual scores
const technicalScore = convertToScore(technicalAnalysis, 'technical');
const newsScore = convertToScore(newsSentiment, 'news');
const analystScore = convertToScore(analystRatings, 'analyst');
const socialScore = convertToScore(socialSentiment, 'social');

// Calculate weighted composite score
const compositeScore = (
  technicalScore * WEIGHTS.technical +
  newsScore * WEIGHTS.news +
  analystScore * WEIGHTS.analyst +
  socialScore * WEIGHTS.social
);

// Determine recommendation based on composite score
let recommendation = 'HOLD';
let confidence = 'Medium';

if (compositeScore > 40) {
  recommendation = 'STRONG BUY';
  confidence = 'High';
} else if (compositeScore > 20) {
  recommendation = 'BUY';
  confidence = 'Medium';
} else if (compositeScore < -40) {
  recommendation = 'STRONG SELL';
  confidence = 'High';
} else if (compositeScore < -20) {
  recommendation = 'SELL';
  confidence = 'Medium';
} else {
  recommendation = 'HOLD';
  confidence = compositeScore > -10 && compositeScore < 10 ? 'High' : 'Medium';
}

// Calculate confidence score (0-100)
const confidenceScore = Math.abs(compositeScore);

// Generate detailed breakdown
const breakdown = {
  technical: {
    score: technicalScore.toFixed(2),
    weight: (WEIGHTS.technical * 100) + '%',
    contribution: (technicalScore * WEIGHTS.technical).toFixed(2)
  },
  news: {
    score: newsScore.toFixed(2),
    weight: (WEIGHTS.news * 100) + '%',
    contribution: (newsScore * WEIGHTS.news).toFixed(2)
  },
  analyst: {
    score: analystScore.toFixed(2),
    weight: (WEIGHTS.analyst * 100) + '%',
    contribution: (analystScore * WEIGHTS.analyst).toFixed(2)
  },
  social: {
    score: socialScore.toFixed(2),
    weight: (WEIGHTS.social * 100) + '%',
    contribution: (socialScore * WEIGHTS.social).toFixed(2)
  }
};

// Generate summary text
const summaryText = `
Based on comprehensive analysis:
- Technical Analysis: ${technicalAnalysis.overallTrend || 'N/A'} (Score: ${technicalScore.toFixed(0)})
- News Sentiment: ${newsSentiment.sentiment || 'N/A'} (Score: ${newsScore.toFixed(0)})
- Analyst Ratings: ${analystRatings.consensus || 'N/A'} (Score: ${analystScore.toFixed(0)})
- Social Sentiment: ${socialSentiment.sentiment || 'N/A'} (Score: ${socialScore.toFixed(0)})

Composite Score: ${compositeScore.toFixed(2)}/100
`;

// Compile final recommendation
const result = {
  recommendation: recommendation,
  confidence: confidence,
  confidenceScore: confidenceScore.toFixed(2),
  compositeScore: compositeScore.toFixed(2),
  breakdown: breakdown,
  summary: summaryText,
  timestamp: new Date().toISOString(),
  rawData: {
    technical: technicalAnalysis,
    news: newsSentiment,
    analyst: analystRatings,
    social: socialSentiment
  }
};

return [{ json: result }];

