// Evaluate AI Consensus - Analyze recommendations from multiple AI models
// and generate a consensus recommendation

const items = $input.all();

if (!items || items.length === 0) {
  return [{ json: { error: 'No AI validation data available' } }];
}

// Extract recommendations from each AI validator
const aiRecommendations = items.map((item, index) => ({
  model: index === 0 ? 'OpenAI' : index === 1 ? 'Anthropic' : 'Gemini',
  recommendation: item.json.recommendation || 'HOLD',
  confidence: item.json.confidence || 0,
  reasoning: item.json.reasoning || ''
}));

// Helper function to convert recommendation to numeric score
function recommendationToScore(rec) {
  const recUpper = rec.toUpperCase();
  if (recUpper.includes('STRONG BUY')) return 100;
  if (recUpper.includes('BUY')) return 50;
  if (recUpper.includes('STRONG SELL')) return -100;
  if (recUpper.includes('SELL')) return -50;
  return 0; // HOLD
}

// Calculate consensus
const scores = aiRecommendations.map(ai => recommendationToScore(ai.recommendation));
const avgScore = scores.reduce((a, b) => a + b, 0) / scores.length;

// Determine consensus recommendation
let consensusRecommendation = 'HOLD';
if (avgScore > 60) {
  consensusRecommendation = 'STRONG BUY';
} else if (avgScore > 25) {
  consensusRecommendation = 'BUY';
} else if (avgScore < -60) {
  consensusRecommendation = 'STRONG SELL';
} else if (avgScore < -25) {
  consensusRecommendation = 'SELL';
}

// Calculate agreement level
const uniqueRecs = [...new Set(aiRecommendations.map(ai => ai.recommendation))];
const agreementLevel = uniqueRecs.length === 1 ? 'Full Agreement' : 
                       uniqueRecs.length === 2 ? 'Partial Agreement' : 
                       'Divergent Views';

// Calculate average confidence
const avgConfidence = aiRecommendations.reduce((sum, ai) => sum + parseFloat(ai.confidence || 0), 0) / aiRecommendations.length;

// Compile consensus result
const result = {
  consensusRecommendation: consensusRecommendation,
  consensusScore: avgScore.toFixed(2),
  agreementLevel: agreementLevel,
  averageConfidence: avgConfidence.toFixed(2),
  aiRecommendations: aiRecommendations,
  summary: `AI Consensus: ${consensusRecommendation} (Agreement: ${agreementLevel}, Confidence: ${avgConfidence.toFixed(0)}%)`,
  timestamp: new Date().toISOString()
};

return [{ json: result }];
