const technicalData = $('Calculate Technical Indicators').first().json;
const aiPrediction = $input.first().json;

const categories = Object.keys(technicalData);
const recommendations = {};

categories.forEach(category => {
  const tech = technicalData[category];
  if (tech.error) {
    recommendations[category] = { action: 'Insufficient Data', reasoning: tech.error };
    return;
  }

  let score = 50;
  let reasoning = [];

  if (tech.latestPrice < tech.ma12) {
    score += 15;
    reasoning.push('Price below 12-week average (bullish signal)');
  } else if (tech.latestPrice > tech.ma12 * 1.1) {
    score -= 15;
    reasoning.push('Price significantly above moving average (overheated)');
  }

  if (tech.rateOfChange < -5) {
    score += 20;
    reasoning.push(`Strong downward momentum (${tech.rateOfChange.toFixed(1)}%)`);
  } else if (tech.rateOfChange > 10) {
    score -= 20;
    reasoning.push(`Strong upward momentum (${tech.rateOfChange.toFixed(1)}%) - avoid buying`);
  }

  if (tech.isCNYPeriod) {
    score -= 10;
    reasoning.push('CNY period typically sees higher prices');
  }

  if (tech.isYearEnd) {
    score -= 5;
    reasoning.push('Year-end period - slightly elevated demand');
  }

  if (tech.volatility > tech.ma12 * 0.15) {
    score -= 10;
    reasoning.push('High volatility - uncertain market conditions');
  }

  let action, timingAdvice, potentialSavings;
  if (score >= 70) {
    action = 'BUY NOW';
    timingAdvice = 'Strong buying opportunity. Market conditions favorable.';
    potentialSavings = 0;
  } else if (score >= 50) {
    action = 'MONITOR CLOSELY';
    timingAdvice = 'Market at neutral position. Consider waiting 2-4 weeks.';
    potentialSavings = Math.round(tech.latestPrice * 0.03);
  } else {
    action = 'WAIT';
    const weeksToWait = score < 30 ? 8 : 4;
    timingAdvice = `Wait ${weeksToWait} weeks. Market showing unfavorable conditions.`;
    potentialSavings = Math.round(tech.latestPrice * 0.08);
  }

  recommendations[category] = {
    action,
    score,
    currentPrice: tech.latestPrice,
    timingAdvice,
    potentialSavings,
    reasoning: reasoning.join('; '),
    trend: tech.rateOfChange > 5 ? 'Uptrend' : tech.rateOfChange < -5 ? 'Downtrend' : 'Consolidation',
    volatilityLevel: tech.volatility > tech.ma12 * 0.15 ? 'High' : tech.volatility > tech.ma12 * 0.08 ? 'Medium' : 'Low'
  };
});

const bestCategory = Object.entries(recommendations)
  .filter(([_, rec]) => !rec.reasoning.includes('Insufficient'))
  .sort((a, b) => b[1].score - a[1].score)[0];

return [{
  json: {
    recommendations,
    bestCategory: bestCategory ? bestCategory[0] : 'None',
    bestCategoryScore: bestCategory ? bestCategory[1].score : 0,
    analysisDate: new Date().toISOString(),
    marketOverview: {
      averageVolatility: Object.values(recommendations)
        .filter(r => r.volatilityLevel)
        .reduce((sum, r) => sum + (r.volatilityLevel === 'High' ? 3 : r.volatilityLevel === 'Medium' ? 2 : 1), 0) / categories.length,
      dominantTrend: Object.values(recommendations)
        .filter(r => r.trend)
        .reduce((acc, r) => {
          acc[r.trend] = (acc[r.trend] || 0) + 1;
          return acc;
        }, {})
    }
  }
}];

