// Get all the items coming into this node
const items = $items();

// Loop through each item (this code works even if there's only one)
for (const item of items) {

  // 1. Get the original script text from the 'full_script_text' field.
  const originalScript = item.json.full_script_text;

  // Get the row number to make the filename unique
  const rowNumber = item.json.row_number;

  // 2. Create the clean filename
  //    - Convert to lowercase for consistency
  //    - Replace any character that is NOT a letter or number with an underscore
  //    - Shorten it to the first 60 characters to prevent it from being too long
  const sanitizedScriptPart = originalScript
    .toLowerCase()
    .replace(/[^a-z0-9]/g, '_') 
    .substring(0, 60);

  // 3. Combine the row number and the sanitized text to create a final, unique filename
  const safeFilename = `${rowNumber}_${sanitizedScriptPart}`;

  // 4. Add the new, safe filename back to the data
  item.json.safeFilename = safeFilename;
}

// Return the modified data to the next node
return items;

