// Get the input data from the previous node
const inputData = $input.all()[0].json;

// Use the EXISTING PCM file path from the input
const pcmFilePath = inputData.fileName; // "/Users/jjtham/3_dream_of_a_marriage_free_from_painful_surprises__imagine_a_f.pcm"

// Generate the WAV output path by replacing .pcm with .wav
const wavFilePath = pcmFilePath.replace('.pcm', '.wav');

// Build the ffmpeg command (quotes handle spaces in paths)
const ffmpegCommand = `ffmpeg -y -f s16le -ar 24000 -ac 1 -i "${pcmFilePath}" "${wavFilePath}"`;

return {
  json: {
    pcmPath: pcmFilePath,
    wavPath: wavFilePath,
    command: ffmpegCommand
  }
};
