// Function node code (n8n)
// Input: expects previous node to provide .json.output which may be a stringified JSON array
// Output: array of items -> each item: { json: { platform, post, imagePrompt, sheetRow: [platform, post, imagePrompt] } }

const rawOutput = $input.first().json.output || '';

// helper: try several JSON parse strategies for escaped strings
function tryParseJSON(input) {
  if (!input || typeof input !== 'string') return null;
  const attempts = [
    input,
    input.trim(),
    input.replace(/\\n/g, '\n'),
    input.replace(/\\"/g, '"'),
    input.replace(/\\'/g, "'"),
    input.replace(/\\n/g, '\n').replace(/\\"/g, '"')
  ];
  for (const attempt of attempts) {
    try {
      const parsed = JSON.parse(attempt);
      return parsed;
    } catch (e) {
      // continue trying
    }
  }
  return null;
}

// fallback smartExtract (your original approach) ‚Äî used if parsing fails
function smartExtract(text, keywords) {
  if (!text || typeof text !== 'string') return '';
  const patterns = [
    new RegExp(`---${keywords}---([\\s\\S]*?)(?=---|$)`, 'i'),
    new RegExp(`####${keywords}####([\\s\\S]*?)(?=####|$)`, 'i'),
    new RegExp(`##\\s*${keywords}[\\s\\S]*?\\n([\\s\\S]*?)(?=##|$)`, 'i'),
    new RegExp(`${keywords}[\\s\\S]*?\\n([\\s\\S]*?)(?=\\n\\n[A-Z]|$)`, 'i')
  ];
  for (const pattern of patterns) {
    const match = text.match(pattern);
    if (match && match[1]) {
      return match[1].trim().replace(/```/g, '').replace(/\n{3,}/g, '\n\n');
    }
  }
  return '';
}

// normalize text: convert escaped newlines to real ones, trim excessive whitespace
function normalizeText(s) {
  if (!s && s !== '') return '';
  let out = typeof s === 'string' ? s : String(s);
  // convert common escaped sequences
  out = out.replace(/\\n/g, '\n').replace(/\\r/g, '\r').replace(/\\"/g, '"').replace(/\\'/g, "'");
  // collapse more than 2 newlines into 2
  out = out.replace(/\n{3,}/g, '\n\n');
  // trim leading/trailing whitespace on each line
  out = out.split('\n').map(l => l.trimEnd()).join('\n').trim();
  return out;
}

let parsed = tryParseJSON(rawOutput);
let rows = [];

// If parsed is a top-level object with "output" key that itself is a stringified JSON,
// handle that too (some agents return [ { "output": "[{...}]" } ])
if (!parsed && rawOutput && rawOutput.includes('"[') && rawOutput.includes(']"')) {
  // attempt to extract the inner JSON substring
  const innerMatch = rawOutput.match(/"(\[.*\])"/s);
  if (innerMatch && innerMatch[1]) {
    parsed = tryParseJSON(innerMatch[1]);
  }
}

// If parsed looks like an array of objects (good), use it
if (Array.isArray(parsed)) {
  // Normalize common shapes: each element might be { platform, post, imagePrompt } or { "platform":..., "post": "...", ... }
  for (const el of parsed) {
    // if the AI returned wrapped objects like {"platform":..., "post": "..."} OR single-key objects
    // sometimes the array elements are strings containing JSON, try to parse those too
    let item = el;
    if (typeof el === 'string') {
      const maybe = tryParseJSON(el);
      if (maybe) item = maybe;
    }
    // attempt to find platform/post/imagePrompt inside item
    const platform = item.platform || item.Platform || (item.output && item.output.platform) || '';
    const post = item.post || item.Post || item.caption || item.content || item.text || '';
    const imagePrompt = item.imagePrompt || item.image_prompt || item.image || item.ImagePrompt || '';
    // If values still empty, skip or push empty strings
    const p = normalizeText(post);
    const img = normalizeText(imagePrompt);
    const plat = String(platform || '').trim();
    rows.push({
      json: {
        platform: plat,
        post: p,
        imagePrompt: img,
        sheetRow: [plat, p, img]
      }
    });
  }
}

// If parsing failed or rows empty, fall back to smartExtract on the rawOutput (transcript-style)
if (rows.length === 0) {
  const fbPost = smartExtract(rawOutput, 'FACEBOOK_POST') || smartExtract(rawOutput, 'FACEBOOK') || '';
  const fbImage = smartExtract(rawOutput, 'FACEBOOK_IMAGE_PROMPT') || smartExtract(rawOutput, 'FACEBOOK_IMAGE') || '';
  const waPost = smartExtract(rawOutput, 'WHATSAPP_CHANNEL') || smartExtract(rawOutput, 'WHATSAPP') || '';
  const waImage = smartExtract(rawOutput, 'WHATSAPP_IMAGE_PROMPT') || smartExtract(rawOutput, 'WHATSAPP_IMAGE') || '';
  const liPost = smartExtract(rawOutput, 'LINKEDIN_POST') || smartExtract(rawOutput, 'LINKEDIN') || '';
  const liImage = smartExtract(rawOutput, 'LINKEDIN_IMAGE_PROMPT') || smartExtract(rawOutput, 'LINKEDIN_IMAGE') || '';
  const igPost = smartExtract(rawOutput, 'INSTAGRAM_CAPTION') || smartExtract(rawOutput, 'INSTAGRAM') || '';
  const igImage = smartExtract(rawOutput, 'INSTAGRAM_IMAGE_PROMPT') || smartExtract(rawOutput, 'INSTAGRAM_IMAGE') || '';
  const rdPost = smartExtract(rawOutput, 'REDDIT_POST') || smartExtract(rawOutput, 'REDDIT') || '';
  const rdImage = smartExtract(rawOutput, 'REDDIT_IMAGE_PROMPT') || smartExtract(rawOutput, 'REDDIT_IMAGE') || '';

  rows = [
    { json: { platform: 'Facebook', post: normalizeText(fbPost), imagePrompt: normalizeText(fbImage), sheetRow: ['Facebook', normalizeText(fbPost), normalizeText(fbImage)] } },
    { json: { platform: 'WhatsApp', post: normalizeText(waPost), imagePrompt: normalizeText(waImage), sheetRow: ['WhatsApp', normalizeText(waPost), normalizeText(waImage)] } },
    { json: { platform: 'LinkedIn', post: normalizeText(liPost), imagePrompt: normalizeText(liImage), sheetRow: ['LinkedIn', normalizeText(liPost), normalizeText(liImage)] } },
    { json: { platform: 'Instagram', post: normalizeText(igPost), imagePrompt: normalizeText(igImage), sheetRow: ['Instagram', normalizeText(igPost), normalizeText(igImage)] } },
    { json: { platform: 'Reddit', post: normalizeText(rdPost), imagePrompt: normalizeText(rdImage), sheetRow: ['Reddit', normalizeText(rdPost), normalizeText(rdImage)] } }
  ];
}

// Final safety: ensure at least the five platforms exist (avoid missing rows)
const requiredPlatforms = ['Facebook','WhatsApp','LinkedIn','Instagram','Reddit'];
const existingPlats = rows.map(r => r.json.platform);
for (const rp of requiredPlatforms) {
  if (!existingPlats.includes(rp)) {
    rows.push({
      json: {
        platform: rp,
        post: '',
        imagePrompt: '',
        sheetRow: [rp, '', '']
      }
    });
  }
}

return rows;
