// Validate purchase request data
const item = $input.first().json;

// Required fields
const requiredFields = ['items', 'requestedBy', 'department'];
const missingFields = requiredFields.filter(field => !item[field]);

if (missingFields.length > 0) {
  throw new Error(`Missing: ${missingFields.join(', ')}`);
}

if (!Array.isArray(item.items) || item.items.length === 0) {
  throw new Error('Items array required with at least one item');
}

// Process items
item.items.forEach((lineItem, i) => {
  if (!lineItem.productName || !lineItem.quantity) {
    throw new Error(`Item ${i + 1} missing productName or quantity`);
  }
  lineItem.quantity = parseFloat(lineItem.quantity);
  lineItem.unitPrice = parseFloat(lineItem.unitPrice) || 0;
  lineItem.totalPrice = lineItem.quantity * lineItem.unitPrice;
});

// Calculate totals
item.subtotal = item.items.reduce((sum, i) => sum + i.totalPrice, 0);
item.tax = parseFloat(item.tax) || (item.subtotal * 0.1);
item.shipping = parseFloat(item.shipping) || 0;
item.total = item.subtotal + item.tax + item.shipping;

// Generate PO number
if (!item.poNumber) {
  const date = new Date();
  const year = date.getFullYear();
  const month = String(date.getMonth() + 1).padStart(2, '0');
  const random = Math.floor(Math.random() * 10000).toString().padStart(4, '0');
  item.poNumber = `PO-${year}${month}-${random}`;
}

// Set defaults
item.currency = item.currency || 'USD';
item.currencySymbol = item.currencySymbol || '$';
item.urgency = item.urgency || 'normal';
item.poDate = new Date().toLocaleDateString('en-US', { year: 'numeric', month: 'long', day: 'numeric' });
item.deliveryDate = item.deliveryDate || new Date(Date.now() + 14*24*60*60*1000).toLocaleDateString('en-US', { year: 'numeric', month: 'long', day: 'numeric' });

// Determine if approval needed (e.g., over $5000)
item.requiresApproval = item.total > 5000;
item.approvalThreshold = 5000;

return { json: item };

