// Initialize player stats (demo mode - random values)
// In production, you could load this from a database

const today = new Date();
const weekday = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'][today.getDay()];

// Generate random stats for demonstration
const streak = Math.floor(Math.random() * 30) + 1;
const level = Math.floor(streak / 7) + 1;
const experience = Math.floor(Math.random() * 100);
const coins = level * 50 + Math.floor(Math.random() * 100);

// Determine player class based on level
let playerClass = 'Novice Adventurer';
if (level >= 20) playerClass = 'Legendary Sage';
else if (level >= 15) playerClass = 'Master Achiever';
else if (level >= 10) playerClass = 'Expert Habitmaster';
else if (level >= 5) playerClass = 'Skilled Practitioner';
else if (level >= 2) playerClass = 'Apprentice';

return {
  // Player stats
  level: level,
  experience: experience,
  nextLevelXP: 100,
  hp: 100,
  maxHp: 100,
  streak: streak,
  coins: coins,
  class: playerClass,
  title: 'Habit Explorer',

  // Date information
  date: today.toLocaleDateString('en-US', { 
    weekday: 'long', 
    year: 'numeric', 
    month: 'long', 
    day: 'numeric' 
  }),
  weekday: weekday,
  weekdayNumber: today.getDay(),
