};

// Select quests based on day of week
let todaysQuests = [...questDatabase.daily];

// Add special quests for specific days
if (weekdayNum === 0 || weekdayNum === 6) {
  // Weekend
  todaysQuests.push(questDatabase.weekend[0]);
}

if (weekdayNum === 1) {
  // Monday
  todaysQuests.push(questDatabase.monday[0]);
}

// Random bonus quest (20% chance)
if (Math.random() < 0.2) {
  todaysQuests.push({
    name: 'Bonus: Early Bird Achievement',
    difficulty: 'Bonus',
    xp: 30,
    coins: 20,
    emoji: 'üåÖ'
  });
}

// Calculate total possible rewards
const totalPossibleXP = todaysQuests.reduce((sum, quest) => sum + quest.xp, 0);
const totalPossibleCoins = todaysQuests.reduce((sum, quest) => sum + quest.coins, 0);

return {
  ...playerStats,
  todaysQuests: todaysQuests,
  totalPossibleXP: totalPossibleXP,
  totalPossibleCoins: totalPossibleCoins,
  questCount: todaysQuests.length
};

