// Rank system based on level
function getRank(level) {
  if (level >= 50) return { rank: 'SSS', color: '#FFD700', title: 'Legendary Hero' };
  if (level >= 40) return { rank: 'SS', color: '#FF6B6B', title: 'Unstoppable Force' };
  if (level >= 30) return { rank: 'S', color: '#4ECDC4', title: 'Master' };
  if (level >= 20) return { rank: 'A', color: '#95E77E', title: 'Expert' };
  if (level >= 10) return { rank: 'B', color: '#68B0AB', title: 'Intermediate' };
  if (level >= 5) return { rank: 'C', color: '#8B95C9', title: 'Apprentice' };
  return { rank: 'D', color: '#B8B8B8', title: 'Beginner' };
}

const rankInfo = getRank(data.level);

