// Dynamic Google Ads API cleaner for n8n.
// This version handles the direct, nested JSON output from the API.
const items = $input.all();
const cleanedData = [];

for (const item of items) {
  const data = item.json;
  const cleaned = {};

  // --- Campaign Info ---
  // Extracts name and parses Campaign ID from the 'resourceName'
  if (data.campaign) {
    cleaned.campaign = data.campaign.name;
    // Example resourceName: "customers/8497428868/campaigns/1590383188"
    // We split by '/' and take the last part.
    cleaned.campaignId = data.campaign.resourceName?.split('/').pop();
  }

  // --- Ad Group Info ---
  if (data.adGroup) {
    cleaned.adGroup = data.adGroup.name;
    cleaned.adGroupId = data.adGroup.id;
  }

  // --- Metrics ---
  // Parses all metrics and calculates cost in USD
  if (data.metrics) {
    cleaned.clicks = parseInt(data.metrics.clicks || 0);
    cleaned.impressions = parseInt(data.metrics.impressions || 0);
    cleaned.conversions = parseFloat(data.metrics.conversions || 0);
    cleaned.allConversions = parseFloat(data.metrics.allConversions || 0);

    const costMicros = parseInt(data.metrics.costMicros || 0);
    cleaned.costMicros = costMicros;
    cleaned.costUSD = parseFloat((costMicros / 1000000).toFixed(2));
  }

  // --- Search Term Info ---
  if (data.searchTermView) {
    cleaned.searchTerm = data.searchTermView.searchTerm;
    cleaned.status = data.searchTermView.status;
  }

  // --- Segments (like date) ---
  if (data.segments) {
    cleaned.date = data.segments.date;
  }

  // --- Calculate Derived Metrics (CTR, CPC) ---
  if (cleaned.impressions > 0) {
    cleaned.ctr = ((cleaned.clicks / cleaned.impressions) * 100).toFixed(2) + '%';
  } else {
    cleaned.ctr = '0.00%';
  }

  if (cleaned.clicks > 0 && cleaned.costUSD > 0) {
    cleaned.cpc = (cleaned.costUSD / cleaned.clicks).toFixed(2);
  } else {
    cleaned.cpc = '0.00';
  }

  cleanedData.push({ json: cleaned });
}

return cleanedData;

// Input: items array from the previous node (with conversion data)
// Output: combined items aggregated by searchTerm + campaign + adGroup

const combined = {};

for (const item of items) {
  const d = item.json;
  // Use a robust key to handle potential missing data
  const key = `${d.searchTerm}_${d.campaignId}_${d.adGroupId}`;

  if (!combined[key]) {
    // If we see this combination for the first time, create its entry
    combined[key] = {
      searchTerm: d.searchTerm,
      campaign: d.campaign,
      campaignId: d.campaignId,
      adGroup: d.adGroup,
      adGroupId: d.adGroupId,
      status: d.status,
      // Initialize all metrics to zero
      clicks: 0,
      impressions: 0,
      costMicros: 0,
      costUSD: 0,
      conversions: 0,      // <-- ADDED
      allConversions: 0    // <-- ADDED
    };
  }

  // Add the current item's metrics to the running total
  combined[key].clicks += d.clicks || 0;
  combined[key].impressions += d.impressions || 0;
  combined[key].costMicros += d.costMicros || 0;
  combined[key].costUSD += d.costUSD || 0;
  combined[key].conversions += d.conversions || 0;          // <-- ADDED
  combined[key].allConversions += d.allConversions || 0;    // <-- ADDED
}

// Convert the combined object back into an array for n8n
return Object.values(combined).map(x => ({ json: x }));

// Input: items[0].json.output (your LLM structured output)
// This version uses the robust "POST" method for Slack interactivity.
const data = items[0].json.output;

const blocks = [];

// Header
blocks.push({
  type: "header",
  text: {
    type: "plain_text",
    text: `ðŸ“Š ${data.reportTitle}`,
    emoji: true
  }
});

// Date and Period
blocks.push({
  type: "section",
  fields: [
    { type: "mrkdwn", text: `*Date:*\n${data.date}` },
    { type: "mrkdwn", text: `*Period:*\n${data.period}` }
  ]
});

// Summary
blocks.push({
  type: "section",
  text: { type: "mrkdwn", text: `${data.summary}` }
});

// Total Ad Wastage
blocks.push({
  type: "section",
  text: { type: "mrkdwn", text: `ðŸ’¸ *Total Ad Wastage:* *$${data.totalAdWastageUSD} USD*` }
});

blocks.push({ type: "divider" });

// --- SECTION 1: Recommended Negative Keywords ---
if (data.recommendations && data.recommendations.length > 0) {
  blocks.push({
    type: "section",
    text: { type: "mrkdwn", text: "âœ… *Recommended Negative Keywords to Add*" }
  });

  for (const rec of data.recommendations) {
    const keywordList = rec.negativeKeywords.map(k => `â€¢ \`${k}\``).join('\n');
    blocks.push({
      type: "section",
      text: {
        type: "mrkdwn",
        text: `*ðŸ“ Campaign:* ${rec.campaign} (ID: ${rec.campaignId})\n*Ad Group:* ${rec.adGroup} (ID: ${rec.adGroupId})\n\n${keywordList}`
      }
    });
  }
  blocks.push({ type: "divider" });
}

// --- SECTION 2: Keywords for Manual Review (CONDITIONAL) ---
if (data.keywordsForReview && data.keywordsForReview.length > 0) {
  blocks.push({
    type: "section",
    text: { type: "mrkdwn", text: "âš ï¸ *Keywords for Manual Review (Generated Conversions)*" }
  });

  for (const reviewItem of data.keywordsForReview) {
    const reviewText = `*Search Term:* \`${reviewItem.searchTerm}\`\n*Cost:* $${reviewItem.costUSD} USD\n*Conversions:* ${reviewItem.conversions}\n*Reason:* _${reviewItem.reason}_`;
    blocks.push({
      type: "section",
      text: { type: "mrkdwn", text: reviewText }
    });
  }
  blocks.push({ type: "divider" });
}

// --- SECTION 3: Confirmation and Action Buttons (POST Method) ---
// This section now builds a JSON payload to send in the body of a POST request.
if (data.recommendations && data.recommendations.length > 0) {

  // Create a clean JSON payload. This is the data your next workflow will receive.
  // We stringify it to pass it in the button's 'value' field.
  const payload = JSON.stringify({
    // We send the whole recommendations array to handle multiple ad groups in the future
    recommendations: data.recommendations 
  });

  blocks.push({
    type: "section",
    text: { type: "mrkdwn", text: "ðŸš€ *Should I proceed to exclude the recommended keywords?*" }
  });

  blocks.push({
    type: "actions",
    elements: [
      {
        type: "button",
        text: { type: "plain_text", text: "âœ… Yes, exclude them" },
        style: "primary",
        action_id: "approve_exclusions", // An identifier for this action
        value: payload // <-- The rich JSON data is passed here
      },
      {
        type: "button",
        text: { type: "plain_text", text: "âŒ No, ignore" },
        style: "danger",
        action_id: "reject_exclusions"
        // No 'value' is needed for the reject action
      }
    ]
  });
}

return [
  {
    json: {
      blocksUi: { blocks }
    }
  }
];
