const item = $input.item.json;

// Get ALL items from Preserve Original Data
let preserved = {
  _id: 'unknown',
  _category: 'unknown',
  _expected: 'unknown',
  _description: 'unknown'
};

try {
  const allPreserved = $('Preserve Original Data').all();

  // Strategy 1: Try to match by index from Loop Over Items
  // The loop processes items sequentially, so we can use array position
  const currentIndex = allPreserved.findIndex(p => {
    // Match by text content (most reliable identifier)
    return p.json.text === (item.text || item.guardrailsInput);
  });

  if (currentIndex >= 0) {
    preserved = allPreserved[currentIndex].json;
  } else {
    // Strategy 2: If we're processing sequentially, use the last item
    // This works because Loop Over Items processes one at a time
    const lastItem = allPreserved[allPreserved.length - 1];
    if (lastItem && lastItem.json) {
      preserved = lastItem.json;
    }
  }
} catch (e) {
  // If we can't access Preserve Original Data, check if fields exist in current item
  if (item._id !== undefined) {
    preserved = {
      _id: item._id,
      _category: item._category,
      _expected: item._expected,
      _description: item._description
    };
  }
  console.error("Could not access Preserve Original Data:", e.message);
}

return {
  json: {
    id: preserved._id || "unknown",
    category: preserved._category || "unknown",
    text: item.text || item.guardrailsInput || "unknown",
    expected: preserved._expected || "unknown",
    actual: "PASS",
    correct: preserved._expected === "PASS",
    score: 0,
    violation_type: "None",
    description: preserved._description || "unknown",
