// Get ALL items from Preserve Original Data
let preserved = {
  _id: 'unknown',
  _category: 'unknown',
  _expected: 'unknown',
  _description: 'unknown'
};

try {
  const allPreserved = $('Preserve Original Data').all();

  // Strategy 1: Try to match by text content
  const currentIndex = allPreserved.findIndex(p => {
    return p.json.text === (item.text || item.guardrailsInput);
  });

  if (currentIndex >= 0) {
    preserved = allPreserved[currentIndex].json;
  } else {
    // Strategy 2: Use the last item (works for sequential processing)
    const lastItem = allPreserved[allPreserved.length - 1];
    if (lastItem && lastItem.json) {
      preserved = lastItem.json;
    }
  }
} catch (e) {
  // Fallback: Check if fields exist in current item
  if (item._id !== undefined) {
    preserved = {
      _id: item._id,
      _category: item._category,
      _expected: item._expected,
      _description: item._description
    };
  }
  console.error("Could not access Preserve Original Data:", e.message);
}

// Extract violation information from checks array
let violationType = "Unknown";
let score = 1.0;

if (item.checks && Array.isArray(item.checks)) {
  const triggeredChecks = item.checks.filter(check => check.triggered === true);

  if (triggeredChecks.length > 0) {
    violationType = triggeredChecks.map(c => c.name).join(", ");

    const scores = triggeredChecks
      .filter(c => c.confidenceScore !== undefined && c.confidenceScore !== null)
      .map(c => c.confidenceScore);

    if (scores.length > 0) {
      score = Math.max(...scores);
    }
  }
}

return {
  json: {
    id: preserved._id || "unknown",
    category: preserved._category || "unknown",
    text: item.text || item.guardrailsInput || "unknown",
    expected: preserved._expected || "unknown",
    actual: "VIOLATION",
    correct: preserved._expected === "VIOLATION",
    score: score,
    violation_type: violationType,
    description: preserved._description || "unknown",
    timestamp: new Date().toISOString()
  }
};

// The Loop Over Items "Done" output sends accumulated results
// Access them differently than from Merge
let items;

// Check if we're receiving from Loop Over Items done output
if ($input.all().length > 0 && $input.all()[0].json) {
  items = $input.all();
} else if ($input.item && $input.item.json) {
  // Fallback: single item
  items = [$input.item];
} else {
  // No data
  items = [];
}

// Initialize counters for confusion matrix
let truePositives = 0;
let trueNegatives = 0;
let falsePositives = 0;
let falseNegatives = 0;

// Collect all results
const results = [];
const categoryStats = {};

items.forEach(item => {
  const data = item.json;

  // Skip if no valid data
  if (!data || !data.expected || !data.actual) {
    return;
  }

  results.push(data);

  // Update confusion matrix
  if (data.actual === "VIOLATION" && data.expected === "VIOLATION") {
    truePositives++;
  } else if (data.actual === "PASS" && data.expected === "PASS") {
    trueNegatives++;
  } else if (data.actual === "VIOLATION" && data.expected === "PASS") {
    falsePositives++;
  } else if (data.actual === "PASS" && data.expected === "VIOLATION") {
    falseNegatives++;
  }

  // Track by category
  if (!categoryStats[data.category]) {
    categoryStats[data.category] = {
      total: 0,
      correct: 0,
      incorrect: 0
    };
  }
  categoryStats[data.category].total++;
  if (data.correct) {
    categoryStats[data.category].correct++;
  } else {
    categoryStats[data.category].incorrect++;
  }
});

// Calculate metrics
const total = items.length;
const correct = truePositives + trueNegatives;
const accuracy = total > 0 ? (correct / total * 100).toFixed(2) : 0;

// Precision: Of all predicted violations, how many were correct?
const precision = (truePositives + falsePositives) > 0 
  ? (truePositives / (truePositives + falsePositives) * 100).toFixed(2) 
  : 0;

// Recall: Of all actual violations, how many did we catch?
const recall = (truePositives + falseNegatives) > 0 
  ? (truePositives / (truePositives + falseNegatives) * 100).toFixed(2) 
  : 0;

// F1 Score: Harmonic mean of precision and recall
const f1Score = (parseFloat(precision) + parseFloat(recall)) > 0
  ? (2 * (parseFloat(precision) * parseFloat(recall)) / (parseFloat(precision) + parseFloat(recall))).toFixed(2)
  : 0;

// Calculate category accuracies
const categoryPerformance = {};
Object.keys(categoryStats).forEach(cat => {
  const stats = categoryStats[cat];
  categoryPerformance[cat] = {
    accuracy: (stats.correct / stats.total * 100).toFixed(2) + "%",
    correct: stats.correct,
    total: stats.total
  };
});

// Create summary
const summary = {
  total_tests: total,
  correct_predictions: correct,
  incorrect_predictions: total - correct,
  accuracy: accuracy + "%",

  confusion_matrix: {
    true_positives: truePositives,
    true_negatives: trueNegatives,
    false_positives: falsePositives,
    false_negatives: falseNegatives
  },

  metrics: {
    precision: precision + "%",
    recall: recall + "%",
    f1_score: f1Score + "%"
  },

  category_performance: categoryPerformance,

  timestamp: new Date().toISOString()
};

// Return both summary and detailed results
return {
  json: {
    summary: summary,
    detailed_results: results
  }
};

