// Get the output from AI Agent
const aiOutput = $input.item.json.output;

if (!aiOutput) {
  throw new Error('No output found from AI Agent');
}

// The AI should only return pure JSON on approval.
// But we still defensively versuchen, JSON aus gemischtem Text zu extrahieren.
let jsonString = aiOutput;

// Remove markdown code fences if present
jsonString = jsonString
  .replace(/```json\s*/gi, '')
  .replace(/```\s*/g, '');

// Try to find a JSON object in the text
const jsonMatch = jsonString.match(/\{[\s\S]*\}/);
if (jsonMatch) {
  jsonString = jsonMatch[0];
}

try {
  const parsed = JSON.parse(jsonString);

  // Validate required field
  if (!parsed.Post) {
    throw new Error('Missing required field: Post');
  }

  // Return normalized structure
  return {
    Post: parsed.Post
  };

} catch (error) {
  // Fallback: try to extract Post via regex if JSON parsing failed
  const postMatch = aiOutput.match(/"Post"\s*:\s*"([\s\S]*?)"/);

  if (postMatch && postMatch[1]) {
    return {
      Post: postMatch[1]
    };
  }

  throw new Error(`Failed to parse AI output: ${error.message}. Output was: ${aiOutput}`);
}

