// This is the final, universal code that will get all your files.
// It recursively finds all files and filters out temporary/system files.
const allFiles = [];
const excludedExtensions = ['.db', '.msg', '.xlsx','.xlsm','.pptx']; // <-- UPDATED THIS LINE

/**
 * This function recursively digs through a folder structure to find all files.
 * @param {Object} folder - A folder object that may contain files and other folders.
 */
function findFilesRecursively(folder) {
  // Safety check to make sure we have a valid folder
  if (!folder || typeof folder !== 'object') {
    return;
  }

  // If there's a 'Files' array, add its contents to our main list
  if (Array.isArray(folder.Files)) {
    allFiles.push(...folder.Files);
  }

  // If there's a 'Folders' array, run this same function on each subfolder
  if (Array.isArray(folder.Folders)) {
    for (const subFolder of folder.Folders) {
      findFilesRecursively(subFolder);
    }
  }
}

// Directly process the first item from the input.
const topLevelFolderObject = $input.item.json;

// The data you provided is a single object with a "Folders" key at the top.
// We start the search from this single object.
findFilesRecursively(topLevelFolderObject);

// Filter out items based on multiple conditions
const filteredFiles = allFiles.filter(item => {
  // Ensure item.Name exists and is a string before proceeding
  if (typeof item.Name !== 'string') {
    return false; // Exclude items that don't have a valid Name property
  }
  const fileName = item.Name;
  const lowerCaseFileName = fileName.toLowerCase();

  // Condition 1: Check if the filename starts with '~$' (Office temporary files)
  const isTempFile = fileName.startsWith('~$');

  // Condition 2: Check if the filename ends with an excluded extension
  const hasExcludedExtension = excludedExtensions.some(ext => lowerCaseFileName.endsWith(ext));

  // Keep the file only if it is NOT a temp file AND does NOT have an excluded extension
  return !isTempFile && !hasExcludedExtension;
});


// Return the final, flattened list of all valid file items.
return filteredFiles.map(file => ({ json: file }));

const items = $input.all();

const outputItems = items.map(item => {
  const data = item.json;

  // Use existing LinkingUrl if available
  if (data.LinkingUrl) {
    return {
      json: {
        ...data,
        fileUrl: data.LinkingUrl
      }
    };
  }

  // Construct versioned URL
  let fileUrl = '';

  if (data.ServerRelativeUrl && data.UniqueId) {
    // Base URL with server relative path
    fileUrl = 'https://nosta.sharepoint.com' + data.ServerRelativeUrl;

    // Use UniqueId for version parameter (remove hyphens)
    const versionId = data.UniqueId.replace(/-/g, '');
    fileUrl += `?d=w${versionId}`;
  }

  return {
    json: {
      ...data,
      fileUrl: fileUrl
    }
  };
});

return outputItems;
