parameters.jsCode:
const input = $input.first().json;
const output = input.output || {};
const markdown = (output.content || '').trim();
const title = output.title || '';
const resultUrls = input.resultUrls || [];

const requests = [];
let cursor = 1;

// Step 1: Insert image
if (Array.isArray(resultUrls) && resultUrls.length > 0) {
  const imageUrl = resultUrls[0];
  requests.push({
    insertInlineImage: {
      location: { index: cursor },
      uri: imageUrl,
      objectSize: { height: { magnitude: 200, unit: 'PT' } }
    }
  });
  cursor += 1; // move past image
  requests.push({
    insertText: {
      location: { index: cursor },
      text: '\n\n'
    }
  });
  cursor += 2;
}

// Step 2: Insert Title
if (title) {
  const titleText = title + '\n\n';
  requests.push({
    insertText: { location: { index: cursor }, text: titleText }
  });
  requests.push({
    updateParagraphStyle: {
      range: { startIndex: cursor, endIndex: cursor + title.length },
      paragraphStyle: { namedStyleType: 'HEADING_1' },
      fields: 'namedStyleType'
    }
  });
  cursor += titleText.length;
}

// Step 3: Remove duplicate markdown title
const markdownContent = markdown.replace(/^#.*\n/, '').trim();

// Step 4: Insert all markdown safely
const lines = markdownContent.split('\n');
for (const line of lines) {
  const trimmed = line.trim();
  if (!trimmed) {
    requests.push({
      insertText: { location: { index: cursor }, text: '\n' }
    });
    cursor += 1;
    continue;
  }

  // Headings
  const headingMatch = trimmed.match(/^(#{1,3})\s*(.*)/);
  if (headingMatch) {
    const hashes = headingMatch[1].length;
    const headingText = headingMatch[2].trim() + '\n\n';
    let styleType = 'NORMAL_TEXT';
    if (hashes === 1) styleType = 'HEADING_2';
    if (hashes === 2) styleType = 'HEADING_3';
    if (hashes === 3) styleType = 'HEADING_4';

    requests.push({ insertText: { location: { index: cursor }, text: headingText } });
    requests.push({
      updateParagraphStyle: {
        range: { startIndex: cursor, endIndex: cursor + headingText.length - 2 },
        paragraphStyle: { namedStyleType: styleType },
        fields: 'namedStyleType'
      }
    });
    cursor += headingText.length;
    continue;
  }

  // Normal paragraph (remove bold markers)
  const paragraph = trimmed.replace(/[*_]{2}/g, '') + '\n\n';
  requests.push({ insertText: { location: { index: cursor }, text: paragraph } });
  cursor += paragraph.length;
}

// Step 5: Return requests
return [{ json: { requests } }];
