// MCDM Scoring Engine: AHP-TOPSIS Implementation
// Multi-Criteria Decision Making for Lead Scoring

// Get input items (merged lead data)
const leads = $input.all();

// Get configuration from Workflow Configuration node
const config = $('Workflow Configuration').first().json;

// Default criteria weights (AHP-derived) if not in config
const criteriaWeights = config.criteriaWeights || {
  companySize: 0.25,
  budget: 0.30,
  engagement: 0.20,
  fitScore: 0.15,
  urgency: 0.10
};

// Extract criteria values from leads
const criteria = ['companySize', 'budget', 'engagement', 'fitScore', 'urgency'];

// Step 1: Build decision matrix
const decisionMatrix = leads.map(lead => {
  return {
    leadId: lead.json.leadId || lead.json.id,
    values: criteria.map(criterion => parseFloat(lead.json[criterion]) || 0),
    originalData: lead.json
  };
});

// Step 2: Normalize the decision matrix (vector normalization)
const normalizedMatrix = [];
for (let j = 0; j < criteria.length; j++) {
  const columnSum = Math.sqrt(
    decisionMatrix.reduce((sum, row) => sum + Math.pow(row.values[j], 2), 0)
  );

  decisionMatrix.forEach((row, i) => {
    if (!normalizedMatrix[i]) normalizedMatrix[i] = [];
    normalizedMatrix[i][j] = columnSum > 0 ? row.values[j] / columnSum : 0;
  });
}

// Step 3: Apply weights to normalized matrix
const weightedMatrix = normalizedMatrix.map(row => 
  row.map((value, j) => value * criteriaWeights[criteria[j]])
);

// Step 4: Determine ideal best (A+) and ideal worst (A-) solutions
const idealBest = [];
const idealWorst = [];

for (let j = 0; j < criteria.length; j++) {
  const column = weightedMatrix.map(row => row[j]);
  idealBest[j] = Math.max(...column);
  idealWorst[j] = Math.min(...column);
}

// Step 5: Calculate separation measures
const separations = weightedMatrix.map(row => {
  const distanceToBest = Math.sqrt(
    row.reduce((sum, value, j) => sum + Math.pow(value - idealBest[j], 2), 0)
  );

  const distanceToWorst = Math.sqrt(
    row.reduce((sum, value, j) => sum + Math.pow(value - idealWorst[j], 2), 0)
  );

  return { distanceToBest, distanceToWorst };
});

// Step 6: Calculate closeness coefficient (TOPSIS score)
const scoredLeads = decisionMatrix.map((lead, i) => {
  const { distanceToBest, distanceToWorst } = separations[i];
  const closenessCoefficient = distanceToWorst / (distanceToBest + distanceToWorst) || 0;

  return {
    json: {
      ...lead.originalData,
      leadId: lead.leadId,
      mcdmScore: Math.round(closenessCoefficient * 100) / 100,
      normalizedScore: Math.round(closenessCoefficient * 100), // 0-100 scale
      criteriaBreakdown: {
        companySize: lead.values[0],
        budget: lead.values[1],
        engagement: lead.values[2],
        fitScore: lead.values[3],
        urgency: lead.values[4]
      },
      weightedScores: {
        companySize: weightedMatrix[i][0],
        budget: weightedMatrix[i][1],
        engagement: weightedMatrix[i][2],
        fitScore: weightedMatrix[i][3],
        urgency: weightedMatrix[i][4]
      },
      distanceToBest: Math.round(separations[i].distanceToBest * 1000) / 1000,
      distanceToWorst: Math.round(separations[i].distanceToWorst * 1000) / 1000,
      scoringMethod: 'AHP-TOPSIS',
      scoredAt: new Date().toISOString()
    }
  };
});

// Step 7: Rank leads by MCDM score (descending)
scoredLeads.sort((a, b) => b.json.mcdmScore - a.json.mcdmScore);

// Add rank to each lead
scoredLeads.forEach((lead, index) => {
  lead.json.rank = index + 1;
  lead.json.percentile = Math.round((1 - index / scoredLeads.length) * 100);
});

console.log(`MCDM Scoring Complete: ${scoredLeads.length} leads scored and ranked`);
console.log(`Top Lead Score: ${scoredLeads[0]?.json.mcdmScore}`);
console.log(`Criteria Weights Used:`, criteriaWeights);

return scoredLeads;

// Extract lead information from the query
const leadData = typeof query === 'string' ? JSON.parse(query) : query;

// Analyze MCDM scores and patterns
const mcdmScore = leadData.mcdmScore || 0;
const ahpScore = leadData.ahpScore || 0;
const topsisScore = leadData.topsisScore || 0;

// Determine lead quality tier
let qualityTier = 'Low';
let qualificationInsights = [];

if (mcdmScore >= 0.8) {
  qualityTier = 'Enterprise';
  qualificationInsights.push('High MCDM score indicates strong fit for enterprise sales');
} else if (mcdmScore >= 0.6) {
  qualityTier = 'Mid-Market';
  qualificationInsights.push('Moderate MCDM score suitable for mid-market engagement');
} else if (mcdmScore >= 0.4) {
  qualityTier = 'SMB';
  qualificationInsights.push('MCDM score indicates SMB potential');
} else {
  qualityTier = 'Nurture';
  qualificationInsights.push('Low MCDM score - recommend nurture campaign');
}

// Analyze behavioral patterns
if (leadData.engagementScore && leadData.engagementScore > 0.7) {
  qualificationInsights.push('High engagement score indicates active interest');
}

if (leadData.companySize && leadData.companySize > 500) {
  qualificationInsights.push('Large company size increases enterprise potential');
}

if (leadData.budget && leadData.budget > 100000) {
  qualificationInsights.push('Significant budget allocation detected');
}

// Create enriched lead profile
const enrichedData = {
  ...leadData,
  qualityTier,
  qualificationInsights,
  enrichmentTimestamp: new Date().toISOString(),
  recommendedAction: qualityTier === 'Nurture' ? 'Add to nurture campaign' : `Route to ${qualityTier} sales team`,
  priorityScore: mcdmScore,
  confidenceLevel: (ahpScore + topsisScore) / 2
};

// Return enriched data as JSON string
return JSON.stringify(enrichedData, null, 2);

// Calculate Performance KPIs for Lead Qualification System

const items = $input.all();

// Initialize counters and metrics
const tierCounts = {
  enterprise: 0,
  midMarket: 0,
  smb: 0,
  nurture: 0
};

const tierScores = {
  enterprise: [],
  midMarket: [],
  smb: [],
  nurture: []
};

const tierConversions = {
  enterprise: 0,
  midMarket: 0,
  smb: 0,
  nurture: 0
};

const responseTimes = [];
const processingTimes = [];
let totalLeads = items.length;
let accurateQualifications = 0;

// Process each lead
for (const item of items) {
  const json = item.json;

  // Determine tier from routing or score
  let tier = 'nurture';
  const score = json.leadScore || json.score || 0;

  if (json.tier) {
    tier = json.tier.toLowerCase();
  } else if (score >= 80) {
    tier = 'enterprise';
  } else if (score >= 60) {
    tier = 'midMarket';
  } else if (score >= 40) {
    tier = 'smb';
  }

  // Count leads by tier
  tierCounts[tier]++;

  // Collect scores by tier
  tierScores[tier].push(score);

  // Track conversions (if conversion data exists)
  if (json.converted === true || json.status === 'converted') {
    tierConversions[tier]++;
  }

  // Collect response times (if available)
  if (json.responseTime) {
    responseTimes.push(json.responseTime);
  }

  // Collect processing times (if available)
  if (json.processingTime) {
    processingTimes.push(json.processingTime);
  }

  // Track model accuracy (if validation data exists)
  if (json.actualQuality && json.predictedQuality) {
    if (json.actualQuality === json.predictedQuality) {
      accurateQualifications++;
    }
  }
}

// Calculate average scores by tier
const avgScoresByTier = {};
for (const [tier, scores] of Object.entries(tierScores)) {
  if (scores.length > 0) {
    avgScoresByTier[tier] = scores.reduce((a, b) => a + b, 0) / scores.length;
  } else {
    avgScoresByTier[tier] = 0;
  }
}

// Calculate conversion rates by tier
const conversionRatesByTier = {};
for (const [tier, count] of Object.entries(tierCounts)) {
  if (count > 0) {
    conversionRatesByTier[tier] = (tierConversions[tier] / count) * 100;
  } else {
    conversionRatesByTier[tier] = 0;
  }
}

// Calculate average response time
const avgResponseTime = responseTimes.length > 0
  ? responseTimes.reduce((a, b) => a + b, 0) / responseTimes.length
  : 0;

// Calculate average processing time
const avgProcessingTime = processingTimes.length > 0
  ? processingTimes.reduce((a, b) => a + b, 0) / processingTimes.length
  : 0;

// Calculate model accuracy
const modelAccuracy = totalLeads > 0
  ? (accurateQualifications / totalLeads) * 100
  : 0;

// Calculate lead distribution percentages
const leadDistribution = {};
for (const [tier, count] of Object.entries(tierCounts)) {
  leadDistribution[tier] = totalLeads > 0 ? (count / totalLeads) * 100 : 0;
}

// Compile KPIs
const kpis = {
  timestamp: new Date().toISOString(),
  totalLeadsProcessed: totalLeads,

  conversionRates: {
    enterprise: conversionRatesByTier.enterprise.toFixed(2) + '%',
    midMarket: conversionRatesByTier.midMarket.toFixed(2) + '%',
    smb: conversionRatesByTier.smb.toFixed(2) + '%',
    nurture: conversionRatesByTier.nurture.toFixed(2) + '%',
    overall: ((Object.values(tierConversions).reduce((a, b) => a + b, 0) / totalLeads) * 100).toFixed(2) + '%'
  },

  averageLeadScores: {
    enterprise: avgScoresByTier.enterprise.toFixed(2),
    midMarket: avgScoresByTier.midMarket.toFixed(2),
    smb: avgScoresByTier.smb.toFixed(2),
    nurture: avgScoresByTier.nurture.toFixed(2)
  },

  leadDistribution: {
    enterprise: leadDistribution.enterprise.toFixed(2) + '%',
    midMarket: leadDistribution.midMarket.toFixed(2) + '%',
    smb: leadDistribution.smb.toFixed(2) + '%',
    nurture: leadDistribution.nurture.toFixed(2) + '%'
  },

  leadCounts: tierCounts,

  performanceMetrics: {
    averageResponseTime: avgResponseTime.toFixed(2) + ' ms',
    averageProcessingTime: avgProcessingTime.toFixed(2) + ' ms',
    modelAccuracy: modelAccuracy.toFixed(2) + '%'
  }
};

return [{ json: kpis }];

