// Data Cleaning & Preprocessing for Oil Price Crisis Detection
// Handles data from multiple sources: API, OPEC, Shipping, News

const cleanedData = [];

// Process all input items
for (const item of $input.all()) {
  try {
    const source = item.json.source || 'unknown';
    const timestamp = item.json.timestamp || new Date().toISOString();

    // Standardize timestamp to ISO format
    let standardizedTimestamp;
    try {
      standardizedTimestamp = new Date(timestamp).toISOString();
    } catch (e) {
      standardizedTimestamp = new Date().toISOString();
    }

    // Initialize cleaned data object
    const cleanedItem = {
      timestamp: standardizedTimestamp,
      source: source,
      rawData: item.json
    };

    // Extract oil price data
    if (item.json.price !== undefined && item.json.price !== null) {
      cleanedItem.oilPrice = parseFloat(item.json.price) || 0;
    } else if (item.json.brent_price !== undefined) {
      cleanedItem.oilPrice = parseFloat(item.json.brent_price) || 0;
    } else if (item.json.wti_price !== undefined) {
      cleanedItem.oilPrice = parseFloat(item.json.wti_price) || 0;
    }

    // Extract volume data
    if (item.json.volume !== undefined && item.json.volume !== null) {
      cleanedItem.volume = parseFloat(item.json.volume) || 0;
    } else if (item.json.trading_volume !== undefined) {
      cleanedItem.volume = parseFloat(item.json.trading_volume) || 0;
    }

    // Extract shipping/supply data
    if (item.json.shipping_volume !== undefined) {
      cleanedItem.shippingVolume = parseFloat(item.json.shipping_volume) || 0;
    }
    if (item.json.supply_disruption !== undefined) {
      cleanedItem.supplyDisruption = item.json.supply_disruption;
    }

    // Extract and analyze news sentiment
    if (item.json.title || item.json.headline || item.json.text) {
      const newsText = (item.json.title || item.json.headline || item.json.text || '').toLowerCase();

      // Simple sentiment analysis based on keywords
      const negativeKeywords = ['crisis', 'war', 'conflict', 'shortage', 'disruption', 'sanction', 'embargo', 'attack', 'threat'];
      const positiveKeywords = ['stable', 'agreement', 'peace', 'increase', 'surplus', 'cooperation'];

      let sentimentScore = 0;
      negativeKeywords.forEach(keyword => {
        if (newsText.includes(keyword)) sentimentScore -= 1;
      });
      positiveKeywords.forEach(keyword => {
        if (newsText.includes(keyword)) sentimentScore += 1;
      });

      cleanedItem.newsSentiment = sentimentScore;
      cleanedItem.newsText = item.json.title || item.json.headline || item.json.text;
    }

    // Extract geopolitical indicators
    if (item.json.region !== undefined) {
      cleanedItem.region = item.json.region;
    }
    if (item.json.country !== undefined) {
      cleanedItem.country = item.json.country;
    }

    // Handle missing values - set defaults
    if (cleanedItem.oilPrice === undefined) {
      cleanedItem.oilPrice = null;
    }
    if (cleanedItem.volume === undefined) {
      cleanedItem.volume = null;
    }
    if (cleanedItem.newsSentiment === undefined) {
      cleanedItem.newsSentiment = 0;
    }

    // Add data quality flag
    cleanedItem.dataQuality = {
      hasPriceData: cleanedItem.oilPrice !== null,
      hasVolumeData: cleanedItem.volume !== null,
      hasNewsData: cleanedItem.newsText !== undefined,
      completeness: 0
    };

    // Calculate completeness score
    let completenessCount = 0;
    if (cleanedItem.oilPrice !== null) completenessCount++;
    if (cleanedItem.volume !== null) completenessCount++;
    if (cleanedItem.newsText !== undefined) completenessCount++;
    cleanedItem.dataQuality.completeness = completenessCount / 3;

    cleanedData.push(cleanedItem);

  } catch (error) {
    console.log('Error processing item:', error.message);
    // Continue processing other items
  }
}

// Return structured data array
return cleanedData.map(item => ({ json: item }));

// Statistical Anomaly Detection for Oil Price Crisis Detection
// Implements Z-score, moving average, volatility, and spike detection

const items = $input.all();

// Helper function to calculate mean
function calculateMean(values) {
  return values.reduce((sum, val) => sum + val, 0) / values.length;
}

// Helper function to calculate standard deviation
function calculateStdDev(values, mean) {
  const squaredDiffs = values.map(val => Math.pow(val - mean, 2));
  const variance = squaredDiffs.reduce((sum, val) => sum + val, 0) / values.length;
  return Math.sqrt(variance);
}

// Helper function to calculate Z-score
function calculateZScore(value, mean, stdDev) {
  if (stdDev === 0) return 0;
  return (value - mean) / stdDev;
}

// Helper function to calculate moving average
function calculateMovingAverage(values, window = 5) {
  if (values.length < window) return calculateMean(values);
  const recentValues = values.slice(-window);
  return calculateMean(recentValues);
}

// Extract price data from items
const priceData = items.map(item => ({
  price: parseFloat(item.json.price || item.json.oilPrice || 0),
  volume: parseFloat(item.json.volume || item.json.tradingVolume || 0),
  timestamp: item.json.timestamp || new Date().toISOString()
}));

// Calculate statistics
const prices = priceData.map(d => d.price);
const volumes = priceData.map(d => d.volume);

const priceMean = calculateMean(prices);
const priceStdDev = calculateStdDev(prices, priceMean);
const volumeMean = calculateMean(volumes);
const volumeStdDev = calculateStdDev(volumes, volumeMean);

// Calculate moving average (5-period)
const movingAverage = calculateMovingAverage(prices, 5);

// Get current (latest) values
const currentPrice = prices[prices.length - 1];
const previousPrice = prices.length > 1 ? prices[prices.length - 2] : currentPrice;
const currentVolume = volumes[volumes.length - 1];

// Calculate price change percentage
const priceChangePercent = previousPrice !== 0 
  ? ((currentPrice - previousPrice) / previousPrice) * 100 
  : 0;

// Calculate Z-scores
const priceZScore = calculateZScore(currentPrice, priceMean, priceStdDev);
const volumeZScore = calculateZScore(currentVolume, volumeMean, volumeStdDev);

// Calculate price volatility (coefficient of variation)
const priceVolatility = priceMean !== 0 ? (priceStdDev / priceMean) * 100 : 0;

// Detect anomalies
const suddenPriceChange = Math.abs(priceChangePercent) > 5;
const volumeSpike = Math.abs(volumeZScore) > 2;
const priceAnomaly = Math.abs(priceZScore) > 2;
const highVolatility = priceVolatility > 10;

// Calculate composite anomaly score (0-100)
let anomalyScore = 0;

// Price Z-score contribution (0-30 points)
anomalyScore += Math.min(Math.abs(priceZScore) * 10, 30);

// Sudden price change contribution (0-25 points)
if (suddenPriceChange) {
  anomalyScore += Math.min(Math.abs(priceChangePercent) * 2, 25);
}

// Volume spike contribution (0-25 points)
if (volumeSpike) {
  anomalyScore += Math.min(Math.abs(volumeZScore) * 8, 25);
}

// Volatility contribution (0-20 points)
anomalyScore += Math.min(priceVolatility * 2, 20);

// Cap at 100
anomalyScore = Math.min(anomalyScore, 100);

// Determine overall anomaly flag
const isAnomaly = anomalyScore > 50 || suddenPriceChange || (priceAnomaly && volumeSpike);

// Return enriched data with anomaly detection results
return [{
  json: {
    // Original data
    ...items[items.length - 1].json,

    // Statistical metrics
    statistics: {
      priceMean: priceMean.toFixed(2),
      priceStdDev: priceStdDev.toFixed(2),
      movingAverage: movingAverage.toFixed(2),
      priceVolatility: priceVolatility.toFixed(2),
      volumeMean: volumeMean.toFixed(2),
      volumeStdDev: volumeStdDev.toFixed(2)
    },

    // Current values
    currentMetrics: {
      price: currentPrice,
      previousPrice: previousPrice,
      priceChangePercent: priceChangePercent.toFixed(2),
      volume: currentVolume,
      priceZScore: priceZScore.toFixed(2),
      volumeZScore: volumeZScore.toFixed(2)
    },

    // Anomaly detection results
    anomalyDetection: {
      anomalyScore: Math.round(anomalyScore),
      isAnomaly: isAnomaly,
      flags: {
        suddenPriceChange: suddenPriceChange,
        volumeSpike: volumeSpike,
        priceAnomaly: priceAnomaly,
        highVolatility: highVolatility
      },
      details: {
        priceChangeDetected: suddenPriceChange ? `${priceChangePercent.toFixed(2)}% change detected` : 'Normal',
        volumeStatus: volumeSpike ? 'Volume spike detected' : 'Normal',
        priceStatus: priceAnomaly ? 'Price anomaly detected' : 'Normal',
        volatilityStatus: highVolatility ? 'High volatility' : 'Normal'
      }
    },

    // Timestamp
    analysisTimestamp: new Date().toISOString()
  }
}];

// Calculate Comprehensive Crisis Risk Score
// Combines multiple data sources with weighted scoring

const items = $input.all();
const results = [];

for (const item of items) {
  const data = item.json;

  // Extract scores from previous nodes
  const statisticalAnomalyScore = data.anomalyScore || 0;
  const aiGeopoliticalScore = data.geopoliticalRiskScore || 0;
  const priceVolatility = data.priceVolatility || 0;
  const volumeChange = data.volumeChange || 0;

  // Normalize all scores to 0-100 scale if needed
  const normalizedStatistical = Math.min(Math.max(statisticalAnomalyScore, 0), 100);
  const normalizedGeopolitical = Math.min(Math.max(aiGeopoliticalScore, 0), 100);
  const normalizedVolatility = Math.min(Math.max(priceVolatility, 0), 100);
  const normalizedVolume = Math.min(Math.max(volumeChange, 0), 100);

  // Calculate weighted crisis risk score
  const crisisRiskScore = (
    (normalizedStatistical * 0.40) +
    (normalizedGeopolitical * 0.40) +
    (normalizedVolatility * 0.10) +
    (normalizedVolume * 0.10)
  );

  // Round to 2 decimal places
  const finalScore = Math.round(crisisRiskScore * 100) / 100;

  // Determine alert level based on score
  let alertLevel;
  if (finalScore >= 70) {
    alertLevel = 'critical';
  } else if (finalScore >= 40) {
    alertLevel = 'warning';
  } else {
    alertLevel = 'info';
  }

  // Create detailed breakdown
  const breakdown = {
    statisticalAnomaly: {
      score: normalizedStatistical,
      weight: '40%',
      contribution: Math.round(normalizedStatistical * 0.40 * 100) / 100
    },
    geopoliticalRisk: {
      score: normalizedGeopolitical,
      weight: '40%',
      contribution: Math.round(normalizedGeopolitical * 0.40 * 100) / 100
    },
    priceVolatility: {
      score: normalizedVolatility,
      weight: '10%',
      contribution: Math.round(normalizedVolatility * 0.10 * 100) / 100
    },
    volumeChanges: {
      score: normalizedVolume,
      weight: '10%',
      contribution: Math.round(normalizedVolume * 0.10 * 100) / 100
    }
  };

  // Return comprehensive result
  results.push({
    json: {
      ...data,
      crisisRiskScore: finalScore,
      alertLevel: alertLevel,
      scoreBreakdown: breakdown,
      timestamp: new Date().toISOString(),
      scoringMethod: 'weighted_composite'
    }
  });
}

return results;
