// Normalize all incoming items into a unified schema
const item = $input.item.json;

// Helper function to safely extract nested values
function safeGet(obj, path, defaultValue = null) {
  try {
    return path.split('.').reduce((acc, part) => acc && acc[part], obj) || defaultValue;
  } catch (e) {
    return defaultValue;
  }
}

// Helper function to generate ID if not present
function generateId(item) {
  if (item.id) return item.id;
  if (item.url) return Buffer.from(item.url).toString('base64').substring(0, 32);
  return `item_${Date.now()}_${Math.random().toString(36).substring(7)}`;
}

// Helper function to parse date
function parseDate(dateValue) {
  if (!dateValue) return new Date().toISOString();
  try {
    const date = new Date(dateValue);
    return isNaN(date.getTime()) ? new Date().toISOString() : date.toISOString();
  } catch (e) {
    return new Date().toISOString();
  }
}

// Helper function to extract tags
function extractTags(item) {
  const tags = [];

  if (item.tags && Array.isArray(item.tags)) {
    tags.push(...item.tags);
  } else if (item.keywords && Array.isArray(item.keywords)) {
    tags.push(...item.keywords);
  } else if (item.categories && Array.isArray(item.categories)) {
    tags.push(...item.categories);
  } else if (item.hashtags && Array.isArray(item.hashtags)) {
    tags.push(...item.hashtags);
  }

  return tags.filter(tag => tag && typeof tag === 'string');
}

// Normalize the item
const normalizedItem = {
  id: generateId(item),
  title: safeGet(item, 'title') || safeGet(item, 'headline') || safeGet(item, 'name') || 'Untitled',
  content: safeGet(item, 'content') || safeGet(item, 'body') || safeGet(item, 'description') || safeGet(item, 'text') || '',
  url: safeGet(item, 'url') || safeGet(item, 'link') || safeGet(item, 'permalink') || '',
  source: safeGet(item, 'source') || safeGet(item, 'sourceName') || safeGet(item, 'publisher') || 'Unknown',
  publishedDate: parseDate(safeGet(item, 'publishedDate') || safeGet(item, 'published_at') || safeGet(item, 'createdAt') || safeGet(item, 'date')),
  author: safeGet(item, 'author') || safeGet(item, 'creator') || safeGet(item, 'username') || 'Unknown',
  tags: extractTags(item),
  metadata: {
    originalSource: safeGet(item, 'source'),
    language: safeGet(item, 'language') || safeGet(item, 'lang'),
    score: safeGet(item, 'score') || safeGet(item, 'rating'),
    engagement: {
      likes: safeGet(item, 'likes') || safeGet(item, 'upvotes') || 0,
      comments: safeGet(item, 'comments') || safeGet(item, 'replies') || 0,
      shares: safeGet(item, 'shares') || safeGet(item, 'retweets') || 0
    },
    raw: item
  }
};

return normalizedItem;

// Parse Technical Signals from Content
// Extract: AI models, datasets, benchmarks, libraries/frameworks, version numbers

const item = $input.item.json;
const content = item.content || item.text || item.body || '';

// Initialize signals object
const signals = {
  aiModels: [],
  datasets: [],
  benchmarks: [],
  libraries: [],
  versions: []
};

// AI Models patterns (common model names)
const modelPatterns = [
  /GPT-?\d+(\.\d+)?/gi,
  /Claude[\s-]?\d*/gi,
  /LLaMA[\s-]?\d*/gi,
  /PaLM[\s-]?\d*/gi,
  /BERT/gi,
  /T5/gi,
  /Gemini[\s-]?\d*/gi,
  /Mistral[\s-]?\d*/gi,
  /Falcon[\s-]?\d*/gi,
  /Stable\s*Diffusion[\s-]?\d*/gi,
  /DALL-?E[\s-]?\d*/gi,
  /Midjourney/gi,
  /Whisper/gi,
  /CodeLlama/gi,
  /Vicuna/gi,
  /Alpaca/gi
];

// Dataset patterns
const datasetPatterns = [
  /ImageNet/gi,
  /COCO/gi,
  /MNIST/gi,
  /CIFAR-?\d+/gi,
  /SQuAD/gi,
  /GLUE/gi,
  /SuperGLUE/gi,
  /Common\s*Crawl/gi,
  /WebText/gi,
  /The\s*Pile/gi,
  /RedPajama/gi,
  /C4/gi,
  /BookCorpus/gi,
  /WikiText/gi
];

// Benchmark patterns
const benchmarkPatterns = [
  /MMLU/gi,
  /HumanEval/gi,
  /HellaSwag/gi,
  /TruthfulQA/gi,
  /GSM8K/gi,
  /MATH/gi,
  /BBH/gi,
  /ARC[\s-]?(?:Easy|Challenge)?/gi,
  /WinoGrande/gi,
  /LAMBADA/gi,
  /BIG-?Bench/gi
];

// Library/Framework patterns
const libraryPatterns = [
  /PyTorch/gi,
  /TensorFlow/gi,
  /JAX/gi,
  /Keras/gi,
  /Hugging\s*Face/gi,
  /Transformers/gi,
  /LangChain/gi,
  /LlamaIndex/gi,
  /scikit-learn/gi,
  /OpenAI/gi,
  /Anthropic/gi,
  /vLLM/gi,
  /DeepSpeed/gi,
  /Megatron/gi,
  /PEFT/gi,
  /LoRA/gi,
  /QLoRA/gi
];

// Version number pattern (e.g., v1.2.3, version 2.0, 3.5.1)
const versionPattern = /(?:v(?:ersion)?\s*)?\d+\.\d+(?:\.\d+)?(?:-(?:alpha|beta|rc)\d*)?/gi;

// Extract AI Models
modelPatterns.forEach(pattern => {
  const matches = content.match(pattern);
  if (matches) {
    signals.aiModels.push(...matches);
  }
});

// Extract Datasets
datasetPatterns.forEach(pattern => {
  const matches = content.match(pattern);
  if (matches) {
    signals.datasets.push(...matches);
  }
});

// Extract Benchmarks
benchmarkPatterns.forEach(pattern => {
  const matches = content.match(pattern);
  if (matches) {
    signals.benchmarks.push(...matches);
  }
});

// Extract Libraries/Frameworks
libraryPatterns.forEach(pattern => {
  const matches = content.match(pattern);
  if (matches) {
    signals.libraries.push(...matches);
  }
});

// Extract Version Numbers
const versionMatches = content.match(versionPattern);
if (versionMatches) {
  signals.versions.push(...versionMatches);
}

// Deduplicate and normalize
signals.aiModels = [...new Set(signals.aiModels.map(m => m.trim()))];
signals.datasets = [...new Set(signals.datasets.map(d => d.trim()))];
signals.benchmarks = [...new Set(signals.benchmarks.map(b => b.trim()))];
signals.libraries = [...new Set(signals.libraries.map(l => l.trim()))];
signals.versions = [...new Set(signals.versions.map(v => v.trim()))];

// Return enriched item with technical signals
return {
  ...item,
  technicalSignals: signals,
  signalCount: {
    models: signals.aiModels.length,
    datasets: signals.datasets.length,
    benchmarks: signals.benchmarks.length,
    libraries: signals.libraries.length,
    versions: signals.versions.length,
    total: signals.aiModels.length + signals.datasets.length + signals.benchmarks.length + signals.libraries.length + signals.versions.length
  }
};

// Time-Series Analytics: Calculate mention frequency, growth rates, and trending patterns

const items = $input.all();

// Helper function to parse dates
function parseDate(dateStr) {
  return new Date(dateStr);
}

// Helper function to get time window key
function getTimeWindowKey(date, window) {
  const d = new Date(date);
  switch(window) {
    case 'daily':
      return d.toISOString().split('T')[0];
    case 'weekly':
      const weekStart = new Date(d);
      weekStart.setDate(d.getDate() - d.getDay());
      return weekStart.toISOString().split('T')[0];
    case 'monthly':
      return `${d.getFullYear()}-${String(d.getMonth() + 1).padStart(2, '0')}`;
    default:
      return d.toISOString().split('T')[0];
  }
}

// Group data by entity/topic and time windows
const timeSeriesData = {};

for (const item of items) {
  const entities = item.json.entities || [];
  const topics = item.json.topics || [];
  const timestamp = item.json.timestamp || item.json.publishedDate || new Date().toISOString();

  // Process entities
  for (const entity of entities) {
    if (!timeSeriesData[entity]) {
      timeSeriesData[entity] = {
        daily: {},
        weekly: {},
        monthly: {}
      };
    }

    ['daily', 'weekly', 'monthly'].forEach(window => {
      const key = getTimeWindowKey(timestamp, window);
      if (!timeSeriesData[entity][window][key]) {
        timeSeriesData[entity][window][key] = 0;
      }
      timeSeriesData[entity][window][key]++;
    });
  }

  // Process topics
  for (const topic of topics) {
    if (!timeSeriesData[topic]) {
      timeSeriesData[topic] = {
        daily: {},
        weekly: {},
        monthly: {}
      };
    }

    ['daily', 'weekly', 'monthly'].forEach(window => {
      const key = getTimeWindowKey(timestamp, window);
      if (!timeSeriesData[topic][window][key]) {
        timeSeriesData[topic][window][key] = 0;
      }
      timeSeriesData[topic][window][key]++;
    });
  }
}

// Calculate growth rates and moving averages
function calculateMetrics(timeSeries, windowSize = 7) {
  const sortedDates = Object.keys(timeSeries).sort();
  const values = sortedDates.map(date => timeSeries[date]);

  // Calculate growth rate
  const growthRates = [];
  for (let i = 1; i < values.length; i++) {
    const rate = values[i-1] === 0 ? 0 : ((values[i] - values[i-1]) / values[i-1]) * 100;
    growthRates.push(rate);
  }

  // Calculate moving average
  const movingAverages = [];
  for (let i = 0; i < values.length; i++) {
    const start = Math.max(0, i - windowSize + 1);
    const window = values.slice(start, i + 1);
    const avg = window.reduce((sum, val) => sum + val, 0) / window.length;
    movingAverages.push(avg);
  }

  // Identify trending pattern
  const recentGrowth = growthRates.slice(-3).reduce((sum, val) => sum + val, 0) / Math.min(3, growthRates.length);
  let trend = 'stable';
  if (recentGrowth > 20) trend = 'rising';
  else if (recentGrowth < -20) trend = 'declining';

  return {
    totalMentions: values.reduce((sum, val) => sum + val, 0),
    averageGrowthRate: growthRates.length > 0 ? growthRates.reduce((sum, val) => sum + val, 0) / growthRates.length : 0,
    recentGrowthRate: recentGrowth,
    movingAverage: movingAverages[movingAverages.length - 1] || 0,
    trend: trend,
    dataPoints: sortedDates.length
  };
}

// Generate output with analytics for each entity/topic
const results = [];

for (const [entityOrTopic, windows] of Object.entries(timeSeriesData)) {
  const result = {
    entity: entityOrTopic,
    daily: calculateMetrics(windows.daily, 7),
    weekly: calculateMetrics(windows.weekly, 4),
    monthly: calculateMetrics(windows.monthly, 3),
    rawTimeSeries: windows
  };

  results.push(result);
}

// Sort by total mentions (daily) to prioritize high-volume entities
results.sort((a, b) => b.daily.totalMentions - a.daily.totalMentions);

return results.map(result => ({ json: result }));

// Topic Modeling & Velocity Analysis using TF-IDF
// Analyzes topic emergence, growth rate, and lifecycle stage

const items = $input.all();

// Helper function to tokenize text
function tokenize(text) {
  return text.toLowerCase()
    .replace(/[^a-z0-9\s]/g, ' ')
    .split(/\s+/)
    .filter(word => word.length > 3);
}

// Calculate TF (Term Frequency)
function calculateTF(tokens) {
  const tf = {};
  const totalTokens = tokens.length;

  tokens.forEach(token => {
    tf[token] = (tf[token] || 0) + 1;
  });

  // Normalize by total tokens
  Object.keys(tf).forEach(token => {
    tf[token] = tf[token] / totalTokens;
  });

  return tf;
}

// Calculate IDF (Inverse Document Frequency)
function calculateIDF(documents) {
  const idf = {};
  const totalDocs = documents.length;

  // Count document frequency for each term
  documents.forEach(doc => {
    const uniqueTokens = [...new Set(doc.tokens)];
    uniqueTokens.forEach(token => {
      idf[token] = (idf[token] || 0) + 1;
    });
  });

  // Calculate IDF
  Object.keys(idf).forEach(token => {
    idf[token] = Math.log(totalDocs / idf[token]);
  });

  return idf;
}

// Calculate TF-IDF scores
function calculateTFIDF(tf, idf) {
  const tfidf = {};

  Object.keys(tf).forEach(token => {
    tfidf[token] = tf[token] * (idf[token] || 0);
  });

  return tfidf;
}

// Extract topics from documents
function extractTopics(documents, topN = 10) {
  const allTFIDF = {};

  documents.forEach(doc => {
    Object.keys(doc.tfidf).forEach(token => {
      allTFIDF[token] = (allTFIDF[token] || 0) + doc.tfidf[token];
    });
  });

  // Sort by TF-IDF score and get top N
  const sortedTopics = Object.entries(allTFIDF)
    .sort((a, b) => b[1] - a[1])
    .slice(0, topN)
    .map(([topic, score]) => ({ topic, score }));

  return sortedTopics;
}

// Calculate topic velocity metrics
function calculateTopicVelocity(documents, topic) {
  // Group documents by time period (e.g., daily)
  const timeGroups = {};

  documents.forEach(doc => {
    const timestamp = new Date(doc.timestamp || doc.publishedAt || Date.now());
    const dateKey = timestamp.toISOString().split('T')[0]; // Daily grouping

    if (!timeGroups[dateKey]) {
      timeGroups[dateKey] = [];
    }

    // Check if document contains the topic
    if (doc.tokens.includes(topic)) {
      timeGroups[dateKey].push(doc);
    }
  });

  // Calculate mention counts over time
  const sortedDates = Object.keys(timeGroups).sort();
  const mentionCounts = sortedDates.map(date => timeGroups[date].length);

  // Calculate velocity (rate of change)
  const velocity = [];
  for (let i = 1; i < mentionCounts.length; i++) {
    velocity.push(mentionCounts[i] - mentionCounts[i - 1]);
  }

  // Calculate acceleration (rate of change of velocity)
  const acceleration = [];
  for (let i = 1; i < velocity.length; i++) {
    acceleration.push(velocity[i] - velocity[i - 1]);
  }

  // Determine lifecycle stage
  const avgVelocity = velocity.length > 0 ? velocity.reduce((a, b) => a + b, 0) / velocity.length : 0;
  const avgAcceleration = acceleration.length > 0 ? acceleration.reduce((a, b) => a + b, 0) / acceleration.length : 0;

  let lifecycleStage = 'stable';
  if (avgVelocity > 5 && avgAcceleration > 0) {
    lifecycleStage = 'emerging';
  } else if (avgVelocity > 0 && avgAcceleration < 0) {
    lifecycleStage = 'peaking';
  } else if (avgVelocity < -2) {
    lifecycleStage = 'declining';
  } else if (avgVelocity > 0) {
    lifecycleStage = 'growing';
  }

  return {
    topic,
    totalMentions: mentionCounts.reduce((a, b) => a + b, 0),
    mentionsByDate: sortedDates.map((date, i) => ({ date, count: mentionCounts[i] })),
    avgVelocity,
    avgAcceleration,
    lifecycleStage,
    trendDirection: avgVelocity > 0 ? 'up' : avgVelocity < 0 ? 'down' : 'flat'
  };
}

// Process documents
const documents = items.map(item => {
  const content = item.json.content || item.json.text || item.json.title || '';
  const tokens = tokenize(content);

  return {
    ...item.json,
    tokens,
    timestamp: item.json.timestamp || item.json.publishedAt || item.json.createdAt || new Date().toISOString()
  };
});

// Calculate TF for each document
documents.forEach(doc => {
  doc.tf = calculateTF(doc.tokens);
});

// Calculate IDF across all documents
const idf = calculateIDF(documents);

// Calculate TF-IDF for each document
documents.forEach(doc => {
  doc.tfidf = calculateTFIDF(doc.tf, idf);
});

// Extract top topics
const topTopics = extractTopics(documents, 20);

// Calculate velocity for each topic
const topicVelocityAnalysis = topTopics.map(({ topic, score }) => {
  const velocityMetrics = calculateTopicVelocity(documents, topic);
  return {
    ...velocityMetrics,
    tfidfScore: score
  };
});

// Sort by emerging topics (high velocity and acceleration)
const emergingTopics = topicVelocityAnalysis
  .filter(t => t.lifecycleStage === 'emerging' || t.lifecycleStage === 'growing')
  .sort((a, b) => b.avgVelocity - a.avgVelocity);

// Return analysis results
return [
  {
    json: {
      analysisType: 'topic_modeling_velocity',
      timestamp: new Date().toISOString(),
      totalDocuments: documents.length,
      topTopics: topicVelocityAnalysis,
      emergingTopics,
      summary: {
        totalTopicsAnalyzed: topTopics.length,
        emergingCount: emergingTopics.length,
        growingCount: topicVelocityAnalysis.filter(t => t.lifecycleStage === 'growing').length,
        decliningCount: topicVelocityAnalysis.filter(t => t.lifecycleStage === 'declining').length,
        stableCount: topicVelocityAnalysis.filter(t => t.lifecycleStage === 'stable').length
      }
    }
  }
];

// Sentiment Analysis using lexicon-based approach
// Analyzes sentiment scores (-1 to 1) and tracks trends over time

const item = $input.item.json;

// Simple sentiment lexicon (can be expanded)
const positiveWords = [
  'good', 'great', 'excellent', 'amazing', 'wonderful', 'fantastic', 'awesome',
  'love', 'best', 'perfect', 'outstanding', 'brilliant', 'innovative', 'revolutionary',
  'breakthrough', 'success', 'powerful', 'efficient', 'improved', 'better',
  'exciting', 'impressive', 'superior', 'advanced', 'cutting-edge', 'promising'
];

const negativeWords = [
  'bad', 'terrible', 'awful', 'horrible', 'poor', 'worst', 'hate', 'disappointing',
  'failed', 'failure', 'broken', 'bug', 'issue', 'problem', 'slow', 'difficult',
  'complicated', 'confusing', 'outdated', 'deprecated', 'vulnerable', 'insecure',
  'unstable', 'unreliable', 'inferior', 'lacking'
];

const intensifiers = ['very', 'extremely', 'highly', 'incredibly', 'absolutely'];
const negations = ['not', 'no', 'never', 'neither', 'nobody', 'nothing', "don't", "doesn't", "didn't"];

// Extract text content from item
const text = (item.content || item.text || item.title || '').toLowerCase();
const words = text.split(/\W+/);

// Calculate sentiment score
let sentimentScore = 0;
let positiveCount = 0;
let negativeCount = 0;
let totalWords = words.length;

for (let i = 0; i < words.length; i++) {
  const word = words[i];
  const prevWord = i > 0 ? words[i - 1] : '';

  // Check for intensifiers
  const intensifier = intensifiers.includes(prevWord) ? 1.5 : 1.0;

  // Check for negations
  const negated = negations.includes(prevWord) ? -1 : 1;

  if (positiveWords.includes(word)) {
    const score = 1 * intensifier * negated;
    sentimentScore += score;
    if (score > 0) positiveCount++;
    else negativeCount++;
  } else if (negativeWords.includes(word)) {
    const score = -1 * intensifier * negated;
    sentimentScore += score;
    if (score < 0) negativeCount++;
    else positiveCount++;
  }
}

// Normalize sentiment score to -1 to 1 range
const maxPossibleScore = totalWords;
const normalizedScore = maxPossibleScore > 0 ? 
  Math.max(-1, Math.min(1, sentimentScore / (maxPossibleScore * 0.1))) : 0;

// Classify sentiment
let sentimentLabel = 'neutral';
if (normalizedScore > 0.2) sentimentLabel = 'positive';
else if (normalizedScore < -0.2) sentimentLabel = 'negative';

// Calculate sentiment confidence
const sentimentWordCount = positiveCount + negativeCount;
const confidence = totalWords > 0 ? sentimentWordCount / totalWords : 0;

// Track sentiment trends (calculate moving average if timestamp available)
const timestamp = item.timestamp || item.publishedAt || item.createdAt || new Date().toISOString();
const date = new Date(timestamp);

// Return enriched item with sentiment analysis
return {
  json: {
    ...item,
    sentiment: {
      score: parseFloat(normalizedScore.toFixed(3)),
      label: sentimentLabel,
      confidence: parseFloat(confidence.toFixed(3)),
      positiveWords: positiveCount,
      negativeWords: negativeCount,
      totalWords: totalWords,
      timestamp: timestamp,
      date: date.toISOString().split('T')[0],
      hour: date.getHours(),
      dayOfWeek: date.getDay()
    },
    sentimentTrend: {
      score: normalizedScore,
      timestamp: timestamp,
      source: item.source || 'unknown',
      topic: item.topic || item.category || 'general'
    }
  }
};

// Changepoint Detection using CUSUM and Bayesian methods
// Detects sudden shifts in trend momentum from time-series data

const items = $input.all();

// Helper function: Calculate mean
function mean(arr) {
  return arr.reduce((sum, val) => sum + val, 0) / arr.length;
}

// Helper function: Calculate standard deviation
function stdDev(arr) {
  const avg = mean(arr);
  const squareDiffs = arr.map(val => Math.pow(val - avg, 2));
  return Math.sqrt(mean(squareDiffs));
}

// CUSUM (Cumulative Sum) Changepoint Detection
function cusumChangepoints(values, threshold = 3) {
  const avg = mean(values);
  const std = stdDev(values);

  let cumSum = 0;
  let changepoints = [];

  for (let i = 0; i < values.length; i++) {
    cumSum += (values[i] - avg) / std;

    if (Math.abs(cumSum) > threshold) {
      changepoints.push({
        index: i,
        value: values[i],
        cusum: cumSum,
        type: cumSum > 0 ? 'upward_shift' : 'downward_shift'
      });
      cumSum = 0; // Reset after detection
    }
  }

  return changepoints;
}

// Bayesian Changepoint Detection (simplified)
function bayesianChangepoints(values, prior = 0.01) {
  const changepoints = [];
  const n = values.length;

  for (let t = 10; t < n - 10; t++) {
    const before = values.slice(Math.max(0, t - 10), t);
    const after = values.slice(t, Math.min(n, t + 10));

    const meanBefore = mean(before);
    const meanAfter = mean(after);
    const stdBefore = stdDev(before);
    const stdAfter = stdDev(after);

    // Calculate probability of changepoint
    const meanDiff = Math.abs(meanAfter - meanBefore);
    const pooledStd = Math.sqrt((stdBefore ** 2 + stdAfter ** 2) / 2);

    if (pooledStd > 0) {
      const zScore = meanDiff / pooledStd;

      if (zScore > 2.5) { // Significant change
        changepoints.push({
          index: t,
          value: values[t],
          zScore: zScore,
          meanBefore: meanBefore,
          meanAfter: meanAfter,
          type: meanAfter > meanBefore ? 'acceleration' : 'deceleration'
        });
      }
    }
  }

  return changepoints;
}

// Process each item's time-series data
const results = items.map(item => {
  const data = item.json;

  // Extract time-series values (adjust field names based on your data structure)
  const timeSeriesField = data.timeSeries || data.values || data.metrics || [];
  const values = Array.isArray(timeSeriesField) 
    ? timeSeriesField.map(v => typeof v === 'object' ? (v.value || v.count || 0) : v)
    : [];

  if (values.length < 20) {
    return {
      json: {
        ...data,
        changepoints: [],
        changepointDetection: {
          status: 'insufficient_data',
          message: 'Need at least 20 data points for changepoint detection'
        }
      }
    };
  }

  // Run both detection methods
  const cusumResults = cusumChangepoints(values, 3);
  const bayesianResults = bayesianChangepoints(values, 0.01);

  // Combine and deduplicate changepoints
  const allChangepoints = [...cusumResults, ...bayesianResults];
  const uniqueChangepoints = [];
  const seen = new Set();

  allChangepoints.forEach(cp => {
    const key = `${cp.index}-${cp.type}`;
    if (!seen.has(key)) {
      seen.add(key);
      uniqueChangepoints.push(cp);
    }
  });

  // Sort by index
  uniqueChangepoints.sort((a, b) => a.index - b.index);

  // Calculate changepoint statistics
  const changepointStats = {
    totalChangepoints: uniqueChangepoints.length,
    upwardShifts: uniqueChangepoints.filter(cp => cp.type === 'upward_shift' || cp.type === 'acceleration').length,
    downwardShifts: uniqueChangepoints.filter(cp => cp.type === 'downward_shift' || cp.type === 'deceleration').length,
    changepointDensity: uniqueChangepoints.length / values.length,
    mostRecentChangepoint: uniqueChangepoints.length > 0 ? uniqueChangepoints[uniqueChangepoints.length - 1] : null
  };

  // Determine trend momentum status
  let momentumStatus = 'stable';
  if (changepointStats.mostRecentChangepoint) {
    const recentIndex = changepointStats.mostRecentChangepoint.index;
    const recency = (values.length - recentIndex) / values.length;

    if (recency < 0.2) { // Recent changepoint (within last 20%)
      momentumStatus = changepointStats.mostRecentChangepoint.type.includes('upward') || 
                       changepointStats.mostRecentChangepoint.type === 'acceleration' 
                       ? 'accelerating' : 'decelerating';
    }
  }

  return {
    json: {
      ...data,
      changepoints: uniqueChangepoints,
      changepointDetection: {
        status: 'success',
        stats: changepointStats,
        momentumStatus: momentumStatus,
        detectionMethods: ['cusum', 'bayesian'],
        timestamp: new Date().toISOString()
      }
    }
  };
});

return results;

// Novelty Scoring: Calculate uniqueness of content against historical baseline
// Measures how novel current topics, entities, and signals are compared to past data

const items = $input.all();
const outputItems = [];

// Helper function to calculate Jaccard similarity
function jaccardSimilarity(set1, set2) {
  const intersection = set1.filter(x => set2.includes(x)).length;
  const union = new Set([...set1, ...set2]).size;
  return union === 0 ? 0 : intersection / union;
}

// Helper function to calculate cosine similarity for numeric vectors
function cosineSimilarity(vec1, vec2) {
  if (vec1.length !== vec2.length) return 0;

  let dotProduct = 0;
  let mag1 = 0;
  let mag2 = 0;

  for (let i = 0; i < vec1.length; i++) {
    dotProduct += vec1[i] * vec2[i];
    mag1 += vec1[i] * vec1[i];
    mag2 += vec2[i] * vec2[i];
  }

  const magnitude = Math.sqrt(mag1) * Math.sqrt(mag2);
  return magnitude === 0 ? 0 : dotProduct / magnitude;
}

// Process each item
for (const item of items) {
  const data = item.json;

  // Extract current features
  const currentTopics = data.topics || [];
  const currentEntities = data.entities || [];
  const currentSignals = data.technical_signals || [];
  const currentKeywords = data.keywords || [];

  // Historical baseline (in production, fetch from database)
  // For now, use a rolling window from previous items
  const historicalWindow = items.slice(0, Math.max(0, items.indexOf(item)));

  if (historicalWindow.length === 0) {
    // First item - assign high novelty
    outputItems.push({
      json: {
        ...data,
        novelty_score: 0.95,
        topic_novelty: 0.95,
        entity_novelty: 0.95,
        signal_novelty: 0.95,
        keyword_novelty: 0.95,
        novelty_explanation: 'First item in dataset - baseline established',
        is_novel: true
      }
    });
    continue;
  }

  // Calculate topic novelty
  let topicSimilarities = [];
  for (const histItem of historicalWindow) {
    const histTopics = histItem.json.topics || [];
    if (histTopics.length > 0 && currentTopics.length > 0) {
      topicSimilarities.push(jaccardSimilarity(currentTopics, histTopics));
    }
  }
  const avgTopicSimilarity = topicSimilarities.length > 0 
    ? topicSimilarities.reduce((a, b) => a + b, 0) / topicSimilarities.length 
    : 0;
  const topicNovelty = 1 - avgTopicSimilarity;

  // Calculate entity novelty
  let entitySimilarities = [];
  for (const histItem of historicalWindow) {
    const histEntities = histItem.json.entities || [];
    if (histEntities.length > 0 && currentEntities.length > 0) {
      entitySimilarities.push(jaccardSimilarity(currentEntities, histEntities));
    }
  }
  const avgEntitySimilarity = entitySimilarities.length > 0 
    ? entitySimilarities.reduce((a, b) => a + b, 0) / entitySimilarities.length 
    : 0;
  const entityNovelty = 1 - avgEntitySimilarity;

  // Calculate signal novelty
  let signalSimilarities = [];
  for (const histItem of historicalWindow) {
    const histSignals = histItem.json.technical_signals || [];
    if (histSignals.length > 0 && currentSignals.length > 0) {
      signalSimilarities.push(jaccardSimilarity(currentSignals, histSignals));
    }
  }
  const avgSignalSimilarity = signalSimilarities.length > 0 
    ? signalSimilarities.reduce((a, b) => a + b, 0) / signalSimilarities.length 
    : 0;
  const signalNovelty = 1 - avgSignalSimilarity;

  // Calculate keyword novelty
  let keywordSimilarities = [];
  for (const histItem of historicalWindow) {
    const histKeywords = histItem.json.keywords || [];
    if (histKeywords.length > 0 && currentKeywords.length > 0) {
      keywordSimilarities.push(jaccardSimilarity(currentKeywords, histKeywords));
    }
  }
  const avgKeywordSimilarity = keywordSimilarities.length > 0 
    ? keywordSimilarities.reduce((a, b) => a + b, 0) / keywordSimilarities.length 
    : 0;
  const keywordNovelty = 1 - avgKeywordSimilarity;

  // Composite novelty score (weighted average)
  const weights = {
    topic: 0.35,
    entity: 0.25,
    signal: 0.25,
    keyword: 0.15
  };

  const noveltyScore = (
    topicNovelty * weights.topic +
    entityNovelty * weights.entity +
    signalNovelty * weights.signal +
    keywordNovelty * weights.keyword
  );

  // Determine if content is novel (threshold: 0.6)
  const isNovel = noveltyScore >= 0.6;

  // Generate explanation
  let explanation = [];
  if (topicNovelty > 0.7) explanation.push('highly novel topics');
  if (entityNovelty > 0.7) explanation.push('new entities');
  if (signalNovelty > 0.7) explanation.push('unique technical signals');
  if (keywordNovelty > 0.7) explanation.push('fresh keywords');

  const noveltyExplanation = explanation.length > 0 
    ? `Novel content detected: ${explanation.join(', ')}` 
    : 'Content similar to historical baseline';

  outputItems.push({
    json: {
      ...data,
      novelty_score: Math.round(noveltyScore * 1000) / 1000,
      topic_novelty: Math.round(topicNovelty * 1000) / 1000,
      entity_novelty: Math.round(entityNovelty * 1000) / 1000,
      signal_novelty: Math.round(signalNovelty * 1000) / 1000,
      keyword_novelty: Math.round(keywordNovelty * 1000) / 1000,
      novelty_explanation: noveltyExplanation,
      is_novel: isNovel,
      historical_window_size: historicalWindow.length
    }
  });
}

return outputItems;

// MCDM Signal Fusion using TOPSIS method
// Combines multiple analytics signals: velocity, sentiment, changepoint, novelty, time-series growth

const item = $input.item.json;

// Extract signals from merged analytics data
const signals = {
  velocity: item.velocity || 0,
  sentiment: item.sentiment || 0,
  changepointMagnitude: item.changepointMagnitude || 0,
  novelty: item.novelty || 0,
  timeSeriesGrowth: item.timeSeriesGrowth || 0
};

// Define weights for each criterion (sum = 1)
const weights = {
  velocity: 0.25,
  sentiment: 0.15,
  changepointMagnitude: 0.25,
  novelty: 0.20,
  timeSeriesGrowth: 0.15
};

// Normalize signals to [0, 1] range
function normalize(value, min = 0, max = 100) {
  if (max === min) return 0;
  return (value - min) / (max - min);
}

// Normalize each signal
const normalized = {
  velocity: normalize(signals.velocity, 0, 100),
  sentiment: normalize(signals.sentiment, -1, 1),
  changepointMagnitude: normalize(signals.changepointMagnitude, 0, 100),
  novelty: normalize(signals.novelty, 0, 100),
  timeSeriesGrowth: normalize(signals.timeSeriesGrowth, -50, 50)
};

// Calculate weighted normalized values
const weighted = {
  velocity: normalized.velocity * weights.velocity,
  sentiment: normalized.sentiment * weights.sentiment,
  changepointMagnitude: normalized.changepointMagnitude * weights.changepointMagnitude,
  novelty: normalized.novelty * weights.novelty,
  timeSeriesGrowth: normalized.timeSeriesGrowth * weights.timeSeriesGrowth
};

// TOPSIS: Calculate ideal best and worst solutions
const idealBest = {
  velocity: 1 * weights.velocity,
  sentiment: 1 * weights.sentiment,
  changepointMagnitude: 1 * weights.changepointMagnitude,
  novelty: 1 * weights.novelty,
  timeSeriesGrowth: 1 * weights.timeSeriesGrowth
};

const idealWorst = {
  velocity: 0,
  sentiment: 0,
  changepointMagnitude: 0,
  novelty: 0,
  timeSeriesGrowth: 0
};

// Calculate Euclidean distance to ideal best and worst
function euclideanDistance(point1, point2) {
  const keys = Object.keys(point1);
  const sumSquares = keys.reduce((sum, key) => {
    return sum + Math.pow(point1[key] - point2[key], 2);
  }, 0);
  return Math.sqrt(sumSquares);
}

const distanceToBest = euclideanDistance(weighted, idealBest);
const distanceToWorst = euclideanDistance(weighted, idealWorst);

// Calculate TOPSIS score (closeness coefficient)
const topsisScore = distanceToWorst / (distanceToBest + distanceToWorst);

// Alternative: Weighted Sum Method score
const weightedSumScore = Object.values(weighted).reduce((sum, val) => sum + val, 0);

// Calculate composite impact score (0-100 scale)
const impactScore = Math.round(topsisScore * 100);

// Determine trend strength category
let trendStrength = 'Low';
if (impactScore >= 75) trendStrength = 'Critical';
else if (impactScore >= 60) trendStrength = 'High';
else if (impactScore >= 40) trendStrength = 'Medium';

// Return enriched data with MCDM scores
return {
  json: {
    ...item,
    mcdm: {
      topsisScore: Math.round(topsisScore * 1000) / 1000,
      weightedSumScore: Math.round(weightedSumScore * 1000) / 1000,
      impactScore: impactScore,
      trendStrength: trendStrength,
      signalBreakdown: {
        velocity: signals.velocity,
        sentiment: signals.sentiment,
        changepointMagnitude: signals.changepointMagnitude,
        novelty: signals.novelty,
        timeSeriesGrowth: signals.timeSeriesGrowth
      },
      normalizedSignals: normalized,
      weightedSignals: weighted,
      distanceToBest: Math.round(distanceToBest * 1000) / 1000,
      distanceToWorst: Math.round(distanceToWorst * 1000) / 1000
    }
  }
};

// Rank trends by combined impact score, relevance, and maturity stage

const items = $input.all();

// Process and rank trends
const rankedTrends = items.map((item, index) => {
  const data = item.json;

  // Extract signals from merged analytics
  const impactScore = data.impactScore || 0;
  const velocityScore = data.velocityScore || 0;
  const sentimentScore = data.sentimentScore || 0;
  const noveltyScore = data.noveltyScore || 0;
  const changepointScore = data.changepointScore || 0;

  // Calculate relevance to AI domain (0-1 scale)
  const aiKeywords = ['ai', 'artificial intelligence', 'machine learning', 'ml', 'deep learning', 
                      'neural network', 'llm', 'gpt', 'transformer', 'nlp', 'computer vision',
                      'reinforcement learning', 'generative', 'diffusion', 'embedding'];

  const topic = (data.topic || '').toLowerCase();
  const content = (data.content || '').toLowerCase();
  const combinedText = topic + ' ' + content;

  const relevanceScore = aiKeywords.reduce((score, keyword) => {
    return score + (combinedText.includes(keyword) ? 1 : 0);
  }, 0) / aiKeywords.length;

  // Determine maturity stage based on velocity and time metrics
  let maturityStage = 'emerging';
  const mentionCount = data.mentionCount || 0;
  const growthRate = data.growthRate || 0;

  if (mentionCount > 100 && growthRate < 0.1) {
    maturityStage = 'mature';
  } else if (mentionCount > 100 && growthRate < 0) {
    maturityStage = 'declining';
  } else if (mentionCount > 20 && growthRate > 0.2) {
    maturityStage = 'growing';
  }

  // Calculate combined impact score with weighted factors
  const combinedImpact = (
    impactScore * 0.3 +
    velocityScore * 0.2 +
    sentimentScore * 0.15 +
    noveltyScore * 0.2 +
    changepointScore * 0.15
  ) * relevanceScore; // Multiply by relevance to prioritize AI-related trends

  return {
    json: {
      ...data,
      relevanceScore,
      maturityStage,
      combinedImpact,
      rankingMetrics: {
        impactScore,
        velocityScore,
        sentimentScore,
        noveltyScore,
        changepointScore,
        relevanceScore,
        maturityStage
      }
    }
  };
});

// Sort by combined impact score (descending)
rankedTrends.sort((a, b) => b.json.combinedImpact - a.json.combinedImpact);

// Add rank position
rankedTrends.forEach((item, index) => {
  item.json.rank = index + 1;
  item.json.rankedAt = new Date().toISOString();
});

return rankedTrends;

