// Extract data from webhook body
const data = $input.item.json.body;

// Required fields validation
const requiredFields = ['name', 'email', 'appointment_date', 'appointment_time'];
const missingFields = requiredFields.filter(field => !data[field] || data[field].trim() === '');

if (missingFields.length > 0) {
  throw new Error(`Missing required fields: ${missingFields.join(', ')}`);
}

// Clean and format data
const cleanData = {
  name: data.name.trim(),
  email: data.email.trim().toLowerCase(),
  phone: data.phone || 'Not provided',
  appointment_date: data.appointment_date,
  appointment_time: data.appointment_time,
  symptoms: data.symptoms || 'Not specified',
  age: data.age || 'Not specified',
  gender: data.gender || 'Not specified',
  timestamp: new Date().toISOString(),
  // Generate unique patient ID
  patient_id: `PAT-${Date.now()}-${Math.random().toString(36).substr(2, 9).toUpperCase()}`
};

// Format appointment datetime
const appointmentDateTime = `${cleanData.appointment_date}T${cleanData.appointment_time}:00`;
cleanData.appointment_datetime = appointmentDateTime;

// Format for display in Indian format
const appointmentDate = new Date(appointmentDateTime);
cleanData.appointment_display = appointmentDate.toLocaleString('en-IN', {
  weekday: 'long',
  year: 'numeric',
  month: 'long',
  day: 'numeric',
  hour: '2-digit',
  minute: '2-digit',
  hour12: true
});

// Also create a short display format
cleanData.appointment_short = appointmentDate.toLocaleString('en-IN', {
  day: '2-digit',
  month: 'short',
  year: 'numeric',
  hour: '2-digit',
  minute: '2-digit',
  hour12: true
});

return cleanData;

// Get patient data from the Validate Input Data node
const patientData = $('Validate Input Data').item.json;

// Create verification URL (replace with your actual clinic URL or use a temporary one)
const verificationURL = `https://clinic-verify.mediajade.com/patient/${patientData.patient_id}`;

// Generate QR code using free API (300x300 pixels, high quality)
const qrCodeURL = `https://api.qrserver.com/v1/create-qr-code/?size=300x300&format=png&qzone=1&data=${encodeURIComponent(verificationURL)}`;

// Return all patient data plus QR code info
return {
  ...patientData,
  verification_url: verificationURL,
  qr_code_url: qrCodeURL
};

