// Enhanced: Extract, Clean, and Format for AI with Sales Volume + CLEAN URLS
const inputData = $input.all();
const products = [];

// Helper: Clean Amazon URL - remove UTM parameters
function cleanAmazonURL(url) {
  if (!url) return '';

  // Remove everything after "?" (UTM parameters)
  const cleanUrl = url.split('?')[0];

  // Extract ASIN for shortest possible URL
  const asinMatch = cleanUrl.match(/\/dp\/([A-Z0-9]{10})/i) || 
                    cleanUrl.match(/\/gp\/product\/([A-Z0-9]{10})/i) ||
                    cleanUrl.match(/\/([A-Z0-9]{10})\//i);

  if (asinMatch) {
    return `https://www.amazon.com/dp/${asinMatch[1]}`;
  }

  // If no ASIN found, return cleaned URL without params
  return cleanUrl.startsWith('http') ? cleanUrl : `https://www.amazon.com${cleanUrl}`;
}

// Helper function to parse sales volume into numeric value for sorting
function parseSalesVolume(salesText) {
  if (!salesText) return 0;

  const text = salesText.toLowerCase();

  // Extract number
  const match = text.match(/(\d+\.?\d*)([km]?)\+?/i);
  if (!match) return 0;

  let value = parseFloat(match[1]);
  const multiplier = match[2];

  // Apply multiplier
  if (multiplier === 'k') value *= 1000;
  if (multiplier === 'm') value *= 1000000;

  return Math.floor(value);
}

// Helper function to format sales volume for display
function formatSalesVolume(salesText) {
  if (!salesText) return 'N/A';
  return salesText.replace(/bought in past month/gi, '').trim();
}

// Calculate product score (for smart ranking)
function calculateProductScore(product, salesNumeric) {
  const rating = product.rating || 0;
  const reviews = product.reviews_count || 0;
  const discount = product.price_strikethrough ? 
    ((product.price_strikethrough - product.price) / product.price_strikethrough) : 0;

  // Weighted scoring
  const ratingScore = rating * 20; // Max 100
  const reviewScore = Math.min(reviews / 100, 50); // Max 50
  const salesScore = Math.min(salesNumeric / 1000, 30); // Max 30
  const discountScore = discount * 20; // Max 20

  // Bonus for badges
  const badgeBonus = (product.is_amazons_choice ? 10 : 0) + 
                     (product.best_seller ? 10 : 0);

  return ratingScore + reviewScore + salesScore + discountScore + badgeBonus;
}

// Extract all products
for (const item of inputData) {
  const results = item.json.results?.[0]?.content?.results?.results;
  if (!results) continue;

  // Combine paid and organic products
  const allProducts = [
    ...(results.paid || []),
    ...(results.organic || []),
    ...(results.amazons_choices || [])
  ];

  allProducts.forEach(p => {
    // Skip if missing critical data
    if (!p.title || !p.price) return;

    const salesVolume = parseSalesVolume(p.sales_volume);

    products.push({
      title: p.title,
      price: p.price,
      original_price: p.price_strikethrough || null,
      rating: p.rating || 0,
      reviews: p.reviews_count || 0,

      // Sales data
      sales_volume_raw: p.sales_volume || 'N/A',
      sales_volume_display: formatSalesVolume(p.sales_volume),
      sales_volume_numeric: salesVolume,

      // Discount calculation
      discount: p.price_strikethrough ? 
        Math.round(((p.price_strikethrough - p.price) / p.price_strikethrough) * 100) : 0,
      savings: p.price_strikethrough ? 
        (p.price_strikethrough - p.price).toFixed(2) : 0,

      // CLEAN URL - remove UTM parameters and extract ASIN
      url: cleanAmazonURL(p.url?.startsWith('http') ? p.url : `https://www.amazon.com${p.url}`),

      // Badges
      is_amazons_choice: p.is_amazons_choice || false,
      is_bestseller: p.best_seller || false,
      is_sponsored: p.is_sponsored || false,

      // Image
      image: p.url_image || null,

      // Manufacturer
      brand: p.manufacturer || 'Generic',

      // Shipping
      shipping: p.shipping_information || 'See Amazon',

      // Calculated score for ranking (weighted)
      score: calculateProductScore(p, salesVolume)
    });
  });
}

// Remove duplicates (same ASIN)
const uniqueProducts = products.reduce((acc, product) => {
  const exists = acc.find(p => p.title === product.title && p.price === product.price);
  if (!exists) acc.push(product);
  return acc;
}, []);

// Sort by calculated score (best overall value)
uniqueProducts.sort((a, b) => b.score - a.score);

// Get query
const query = $('Extract Chat & Query').first().json.message.replaceAll('+', ' ');

// Calculate statistics
const avgPrice = uniqueProducts.reduce((sum, p) => sum + p.price, 0) / uniqueProducts.length;
const avgRating = uniqueProducts.reduce((sum, p) => sum + p.rating, 0) / uniqueProducts.length;
const totalSales = uniqueProducts.reduce((sum, p) => sum + p.sales_volume_numeric, 0);

// Create AI-ready output with enhanced categorization
return [{
  json: {
    query: query,
    total: uniqueProducts.length,

    // Statistics
    stats: {
      avg_price: avgPrice.toFixed(2),
      min_price: Math.min(...uniqueProducts.map(p => p.price)),
      max_price: Math.max(...uniqueProducts.map(p => p.price)),
      avg_rating: avgRating.toFixed(2),
      total_reviews: uniqueProducts.reduce((sum, p) => sum + p.reviews, 0),
      total_sales: totalSales
    },

    // Top picks (by overall score)
    top_picks: uniqueProducts.slice(0, 10).map(p => ({
      title: p.title,
      price: p.price,
      rating: p.rating,
      reviews: p.reviews,
      sales: p.sales_volume_display,
      discount: p.discount > 0 ? `${p.discount}% off` : null,
      badges: {
        choice: p.is_amazons_choice,
        bestseller: p.is_bestseller
      },
      url: p.url  // Already cleaned by cleanAmazonURL function
    })),

    // Budget options (under $30, sorted by rating)
    budget_options: uniqueProducts
      .filter(p => p.price < 30)
      .sort((a, b) => b.rating - a.rating || b.reviews - a.reviews)
      .slice(0, 5)
      .map(p => ({
        title: p.title,
        price: p.price,
        rating: p.rating,
        reviews: p.reviews,
        sales: p.sales_volume_display,
        url: p.url
      })),

    // Premium options ($50+, sorted by rating)
    premium_options: uniqueProducts
      .filter(p => p.price >= 50)
      .sort((a, b) => b.rating - a.rating)
      .slice(0, 5)
      .map(p => ({
        title: p.title,
        price: p.price,
        rating: p.rating,
        reviews: p.reviews,
        sales: p.sales_volume_display,
        url: p.url
      })),

    // Best rated (4.5+ stars, sorted by reviews)
    best_rated: uniqueProducts
      .filter(p => p.rating >= 4.5)
      .sort((a, b) => b.reviews - a.reviews)
      .slice(0, 5)
      .map(p => ({
        title: p.title,
        price: p.price,
        rating: p.rating,
        reviews: p.reviews,
        sales: p.sales_volume_display,
        url: p.url
      })),

    // Best value (highest discount with good rating)
    best_value: uniqueProducts
      .filter(p => p.discount > 0 && p.rating >= 4.0)
      .sort((a, b) => b.discount - a.discount)
      .slice(0, 5)
      .map(p => ({
        title: p.title,
        price: p.price,
        original_price: p.original_price,
        discount: `${p.discount}%`,
        savings: `$${p.savings}`,
        rating: p.rating,
        reviews: p.reviews,
        url: p.url
      })),

    // Most popular (by sales volume)
    most_popular: uniqueProducts
      .filter(p => p.sales_volume_numeric > 0)
      .sort((a, b) => b.sales_volume_numeric - a.sales_volume_numeric)
      .slice(0, 5)
      .map(p => ({
        title: p.title,
        price: p.price,
        rating: p.rating,
        sales: p.sales_volume_display,
        sales_count: p.sales_volume_numeric,
        url: p.url
      })),

    // Amazon's Choice products
    amazons_choice: uniqueProducts
      .filter(p => p.is_amazons_choice)
      .slice(0, 3)
      .map(p => ({
        title: p.title,
        price: p.price,
        rating: p.rating,
        reviews: p.reviews,
        sales: p.sales_volume_display,
        url: p.url
      })),

    // All products (for reference)
    all_products: uniqueProducts
  }
}];

// Optimized error handler - clean & informative
const errorData = $input.first().json;

function getNestedValue(obj, path, defaultValue = 'Unknown') {
  return path.split('.').reduce((curr, prop) => 
    curr?.[prop], obj) || defaultValue;
}

const workflowName = getNestedValue(errorData, 'workflow.name', 'Unknown Workflow');
const nodeName = getNestedValue(errorData, 'execution.error.node.name', 'Unknown Node');
const errorMessage = getNestedValue(errorData, 'execution.error.message', 'No error message');
const timestamp = getNestedValue(errorData, 'execution.startedAt', new Date().toISOString());
const executionId = getNestedValue(errorData, 'execution.id', 'Unknown');

// Tangkap error details
const errorObj = errorData.execution?.error || {};
const errorDescription = errorObj.description || '';
const errorHttpCode = errorObj.httpCode || '';

// Stack trace - ambil HANYA baris pertama yang paling penting
const errorStack = errorObj.stack || '';
const mainError = errorStack.split('\n')[0] || '';

// Format detail yang clean
let errorDetails = [];
if (errorDescription && errorDescription !== errorMessage) {
  errorDetails.push(errorDescription);
}
if (errorHttpCode) {
  errorDetails.push(`HTTP ${errorHttpCode}`);
}

const detailText = errorDetails.length > 0 
  ? errorDetails.join(' | ') 
  : 'No additional details';

// Main error dari stack (tanpa "NodeApiError: " prefix)
const cleanMainError = mainError.replace(/^(NodeApiError|Error):\s*/, '').trim();

// Build message - COMPACT VERSION
const message = `üö® <b>WORKFLOW ERROR</b>\n\n` +
  `üìã <b>${workflowName}</b>\n` +
  `‚öôÔ∏è Node: <code>${nodeName}</code>\n\n` +
  `‚ùå <b>${errorMessage}</b>\n` +
  `üí° ${detailText}\n\n` +
  `üïê ${new Date(timestamp).toLocaleString('id-ID', { 
    day: '2-digit', 
    month: 'short', 
    hour: '2-digit', 
    minute: '2-digit' 
  })}\n` +
  `üîó Exec: <code>${executionId}</code>`;

return {
  json: {
    message: message
  }
};
