// Extract and format product data
const product = items[0].json;

const productName = product.title || 'New Product';
const productPrice = product.variants?.[0]?.price || 'Price TBA';
const productImage = product.images?.[0]?.src || '';
const productUrl = `https://yourstore.myshopify.com/products/${product.handle}`;
const productDescription = product.body_html?.replace(/<[^>]*>/g, '').substring(0, 150) || '';
const timestamp = new Date().toISOString();


// // Generate social media caption
// const caption = `üéâ New Arrival! ${productName}\n\n${productDescription}...\n\nüí∞ ${productPrice}\nüõí Shop now: ${productUrl}\n\n#NewProduct #ShopNow #Ecommerce #${productName.replace(/\s+/g, '')}`;

return [
  {
    json: {
      productName,
      productPrice,
      productImage,
      productUrl,
      productDescription,
      timestamp
    }
  }
];

