const brief = $input.first().json.output.brief;
const competitive = $input.first().json.output.competitive_intel;

let seoScore = 0;
if (brief.target_keywords && brief.target_keywords.length >= 5) seoScore += 3;
if (brief.meta_description && brief.meta_description.length <= 160) seoScore += 2;
if (brief.title && brief.title.length <= 60) seoScore += 2;
if (brief.outline && brief.outline.length >= 7) seoScore += 2;
if (brief.semantic_entities && brief.semantic_entities.length >= 5) seoScore += 1;

let diffScore = 0;
if (brief.content_gaps_addressed && brief.content_gaps_addressed.length >= 3) diffScore += 4;
if (competitive.our_advantage && competitive.our_advantage.length >= 2) diffScore += 3;
if (brief.word_count > competitive.avg_competitor_word_count) diffScore += 3;

let completeScore = 0;
if (brief.outline && brief.outline.length >= 5) completeScore += 2;
if (brief.cta_ideas && brief.cta_ideas.length >= 2) completeScore += 2;
if (brief.internal_links && brief.internal_links.length >= 3) completeScore += 2;
if (brief.target_keywords && brief.target_keywords.length >= 3) completeScore += 2;
if (brief.semantic_entities && brief.semantic_entities.length >= 5) completeScore += 2;

const overallScore = ((seoScore + diffScore + completeScore) / 3).toFixed(1);

return {
  json: {
    ...$input.first().json,
    output: {
      ...$input.first().json.output,
      quality_scores: {
        seo_score: seoScore,
        differentiation_score: diffScore,
        completeness_score: completeScore,
        overall_score: parseFloat(overallScore)
      }
    }
  }
};

