const output = $input.first().json.output;
const brief = output.brief;
const competitive = output.competitive_intel;
const quality = output.quality_scores;
const metadata = output.metadata;

const keywordsHtml = brief.target_keywords.map(kw => 
  typeof kw === 'object' 
    ? `<span class="keyword ${kw.priority === 'primary' ? 'primary' : ''}">${kw.keyword} ${kw.volume ? `(${kw.volume})` : ''}</span>`
    : `<span class="keyword">${kw}</span>`
).join('');

const outlineHtml = brief.outline.map(section => 
  typeof section === 'object'
    ? `<li><strong>${section.h2}</strong> <span class="purpose">${section.purpose || ''}</span>
       ${section.h3_suggestions && section.h3_suggestions.length > 0 ? `<ul style="margin-top: 8px;">${section.h3_suggestions.map(h3 => `<li style="border:none; background:transparent; padding:5px 0;">${h3}</li>`).join('')}</ul>` : ''}
       </li>`
    : `<li>${section}</li>`
).join('');

const html = `<!DOCTYPE html>
<html>
<head>
  <meta charset="UTF-8">
  <title>Brief Preview - ${brief.title}</title>
  <style>
    body { font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Arial, sans-serif; max-width: 900px; margin: 40px auto; padding: 20px; background: #f5f7fa; }
    .header { background: linear-gradient(135deg, #667eea 0%, #764ba2 100%); color: white; padding: 30px; border-radius: 12px; margin-bottom: 30px; }
    .header h1 { margin: 0 0 10px 0; font-size: 32px; }
    .header .meta { opacity: 0.9; font-size: 14px; }
    .card { background: white; padding: 25px; border-radius: 8px; margin-bottom: 20px; box-shadow: 0 2px 8px rgba(0,0,0,0.1); }
    .card h2 { margin: 0 0 15px 0; font-size: 20px; color: #2d3748; border-bottom: 2px solid #667eea; padding-bottom: 10px; }
    .keywords { display: flex; flex-wrap: wrap; gap: 8px; margin: 15px 0; }
    .keyword { background: #edf2f7; padding: 6px 12px; border-radius: 20px; font-size: 13px; border: 1px solid #cbd5e0; }
    .keyword.primary { background: #667eea; color: white; border-color: #667eea; }
    .outline { list-style: none; padding: 0; }
    .outline li { padding: 12px; margin: 8px 0; background: #f7fafc; border-left: 4px solid #667eea; border-radius: 4px; }
    .outline li .purpose { font-size: 11px; color: #718096; margin-left: 10px; background: #edf2f7; padding: 2px 8px; border-radius: 10px; }
    .scores { display: grid; grid-template-columns: repeat(4, 1fr); gap: 15px; }
    .score-box { text-align: center; padding: 20px; background: #f7fafc; border-radius: 8px; border: 2px solid #e2e8f0; }
    .score-box .number { font-size: 36px; font-weight: bold; color: #667eea; }
    .score-box .label { font-size: 12px; color: #718096; margin-top: 5px; text-transform: uppercase; }
  </style>
</head>
<body>
  <div class="header">
    <h1>${brief.title}</h1>
    <div class="meta">Content ID: ${metadata.content_id} | Version: ${metadata.version_no} | ${metadata.is_new_topic ? 'üÜï New Topic' : 'üîÑ Updated'}</div>
  </div>
  <div class="card">
    <h2>üìä Quality Scores</h2>
    <div class="scores">
      <div class="score-box"><div class="number">${quality.overall_score}</div><div class="label">Overall</div></div>
      <div class="score-box"><div class="number">${quality.seo_score}</div><div class="label">SEO</div></div>
      <div class="score-box"><div class="number">${quality.differentiation_score}</div><div class="label">Differentiation</div></div>
      <div class="score-box"><div class="number">${quality.completeness_score}</div><div class="label">Completeness</div></div>
    </div>
  </div>
  <div class="card">
    <h2>üéØ Meta Information</h2>
    <p><strong>Meta Description:</strong></p>
    <p style="background: #f7fafc; padding: 15px; border-radius: 6px;">${brief.meta_description}</p>
    <p><strong>Word Count:</strong> ${brief.word_count} | <strong>Tone:</strong> ${brief.tone}</p>
  </div>
  <div class="card">
    <h2>üîë Target Keywords</h2>
    <div class="keywords">${keywordsHtml}</div>
  </div>
  <div class="card">
    <h2>üìù Content Outline</h2>
    <ol class="outline">${outlineHtml}</ol>
  </div>
  <div class="card">
    <h2>üèÜ Competitive Intelligence</h2>
    <p><strong>Avg Competitor Words:</strong> ${competitive.avg_competitor_word_count || 'N/A'}</p>
    <p><strong>Difficulty:</strong> ${competitive.difficulty_assessment || 'N/A'}</p>
  </div>
</body>
</html>`;

return {
  json: {
    html_preview: html,
    ...output
  },
  binary: {
    preview: {
      data: Buffer.from(html).toString('base64'),
      mimeType: 'text/html',
      fileName: `brief_${metadata.content_id}_${metadata.version_id}.html`
    }
  }
};
