const currentPrice = $input.first().json.lowest_price;
const historicalData = $input.all();

if (historicalData.length < 2) {
  return [{
    json: {
      price_change_percent: 0,
      price_volatility: 'insufficient_data',
      trend_strength: 0,
      prediction_confidence: 'low'
    }
  }];
}

const prices = historicalData.map(item => item.json.lowest_price).filter(p => p);
const avgPrice = prices.reduce((a, b) => a + b, 0) / prices.length;
const lastPrice = prices[prices.length - 2] || currentPrice;
const priceChange = ((currentPrice - lastPrice) / lastPrice) * 100;

const variance = prices.reduce((sum, p) => sum + Math.pow(p - avgPrice, 2), 0) / prices.length;
const volatility = Math.sqrt(variance);

let volatilityLevel = 'low';
if (volatility > avgPrice * 0.15) volatilityLevel = 'high';
else if (volatility > avgPrice * 0.08) volatilityLevel = 'medium';

const trendStrength = Math.abs(priceChange);
let confidence = 'low';
if (historicalData.length > 10 && volatility < avgPrice * 0.1) confidence = 'high';
else if (historicalData.length > 5) confidence = 'medium';

return [{
  json: {
    current_price: currentPrice,
    average_price: Math.round(avgPrice),
    price_change_percent: Math.round(priceChange * 100) / 100,
    price_volatility: volatilityLevel,
    volatility_value: Math.round(volatility),
    trend_strength: Math.round(trendStrength * 100) / 100,
    prediction_confidence: confidence,
    data_points: historicalData.length,
    ...($input.first().json)
  }
}];

