// Merge F1 data from multiple sources
const items = $input.all();

let seasonData = null;
let driverStandings = null;
let constructorStandings = null;

// Process each input item and categorize by source
for (const item of items) {
  const data = item.json;

  // Identify the type of data based on structure
  if (data.MRData && data.MRData.RaceTable) {
    // This is season/race data
    seasonData = data.MRData.RaceTable;
  } else if (data.MRData && data.MRData.StandingsTable && data.MRData.StandingsTable.StandingsLists) {
    const standingsList = data.MRData.StandingsTable.StandingsLists[0];

    if (standingsList.DriverStandings) {
      // This is driver standings
      driverStandings = standingsList.DriverStandings;
    } else if (standingsList.ConstructorStandings) {
      // This is constructor standings
      constructorStandings = standingsList.ConstructorStandings;
    }
  }
}

// Create merged data object
const mergedData = {
  season: seasonData,
  driverStandings: driverStandings,
  constructorStandings: constructorStandings,
  timestamp: new Date().toISOString()
};

// Return as single item
return [{ json: mergedData }];

// Calculate advanced driver performance metrics
const items = $input.all();

// Extract driver data from aggregated historical data
const driverMetrics = [];

// Process each item to calculate metrics
for (const item of items) {
  const data = item.json;

  // Initialize metrics object for each driver
  const drivers = {};

  // Extract race results and calculate metrics
  if (data.races && Array.isArray(data.races)) {
    for (const race of data.races) {
      if (race.Results && Array.isArray(race.Results)) {
        for (const result of race.Results) {
          const driverId = result.Driver?.driverId || 'unknown';

          if (!drivers[driverId]) {
            drivers[driverId] = {
              driverId: driverId,
              driverName: `${result.Driver?.givenName || ''} ${result.Driver?.familyName || ''}`.trim(),
              races: [],
              totalPoints: 0,
              podiums: 0,
              wins: 0,
              dnfs: 0,
              qualifyingPositions: [],
              finishingPositions: []
            };
          }

          // Add race data
          const position = parseInt(result.position) || 999;
          const points = parseFloat(result.points) || 0;
          const status = result.status || '';

          drivers[driverId].races.push({
            raceName: race.raceName,
            position: position,
            points: points,
            status: status,
            grid: parseInt(result.grid) || 0
          });

          drivers[driverId].totalPoints += points;
          drivers[driverId].finishingPositions.push(position);

          if (result.grid) {
            drivers[driverId].qualifyingPositions.push(parseInt(result.grid));
          }

          // Count podiums and wins
          if (position === 1) drivers[driverId].wins++;
          if (position <= 3) drivers[driverId].podiums++;

          // Count DNFs (Did Not Finish)
          if (status && !status.includes('Finished') && !status.includes('Lapped')) {
            drivers[driverId].dnfs++;
          }
        }
      }
    }
  }

  // Calculate advanced metrics for each driver
  for (const driverId in drivers) {
    const driver = drivers[driverId];
    const totalRaces = driver.races.length;

    if (totalRaces > 0) {
      // Average finishing position
      const avgFinishPosition = driver.finishingPositions.reduce((a, b) => a + b, 0) / driver.finishingPositions.length;

      // Podium rate
      const podiumRate = (driver.podiums / totalRaces) * 100;

      // DNF rate
      const dnfRate = (driver.dnfs / totalRaces) * 100;

      // Points per race
      const pointsPerRace = driver.totalPoints / totalRaces;

      // Average qualifying position
      const avgQualifyingPosition = driver.qualifyingPositions.length > 0
        ? driver.qualifyingPositions.reduce((a, b) => a + b, 0) / driver.qualifyingPositions.length
        : null;

      // Consistency score (lower standard deviation = more consistent)
      const mean = avgFinishPosition;
      const variance = driver.finishingPositions.reduce((sum, pos) => sum + Math.pow(pos - mean, 2), 0) / driver.finishingPositions.length;
      const consistencyScore = 100 - Math.min(Math.sqrt(variance) * 5, 100); // Scale to 0-100

      // Win rate
      const winRate = (driver.wins / totalRaces) * 100;

      // Recent form (last 5 races average position)
      const recentRaces = driver.finishingPositions.slice(-5);
      const recentForm = recentRaces.reduce((a, b) => a + b, 0) / recentRaces.length;

      driverMetrics.push({
        driverId: driver.driverId,
        driverName: driver.driverName,
        totalRaces: totalRaces,
        totalPoints: driver.totalPoints,
        wins: driver.wins,
        podiums: driver.podiums,
        dnfs: driver.dnfs,
        metrics: {
          averageFinishingPosition: parseFloat(avgFinishPosition.toFixed(2)),
          podiumRate: parseFloat(podiumRate.toFixed(2)),
          winRate: parseFloat(winRate.toFixed(2)),
          dnfRate: parseFloat(dnfRate.toFixed(2)),
          pointsPerRace: parseFloat(pointsPerRace.toFixed(2)),
          averageQualifyingPosition: avgQualifyingPosition ? parseFloat(avgQualifyingPosition.toFixed(2)) : null,
          consistencyScore: parseFloat(consistencyScore.toFixed(2)),
          recentFormAverage: parseFloat(recentForm.toFixed(2))
        },
        raceHistory: driver.races
      });
    }
  }
}

// Sort by total points descending
driverMetrics.sort((a, b) => b.totalPoints - a.totalPoints);

// Return enriched data with calculated metrics
return driverMetrics.map(driver => ({ json: driver }));

