// Statistical Analysis Tool for F1 Data
// Performs calculations including mean, median, standard deviation, win rates, and correlation analysis

const data = JSON.parse(query);

// Helper function to calculate mean
function mean(values) {
  if (!values || values.length === 0) return 0;
  return values.reduce((sum, val) => sum + val, 0) / values.length;
}

// Helper function to calculate median
function median(values) {
  if (!values || values.length === 0) return 0;
  const sorted = [...values].sort((a, b) => a - b);
  const mid = Math.floor(sorted.length / 2);
  return sorted.length % 2 === 0 ? (sorted[mid - 1] + sorted[mid]) / 2 : sorted[mid];
}

// Helper function to calculate standard deviation
function standardDeviation(values) {
  if (!values || values.length === 0) return 0;
  const avg = mean(values);
  const squareDiffs = values.map(value => Math.pow(value - avg, 2));
  return Math.sqrt(mean(squareDiffs));
}

// Helper function to calculate win rate
function winRate(wins, total) {
  if (!total || total === 0) return 0;
  return (wins / total) * 100;
}

// Helper function to calculate correlation coefficient
function correlation(x, y) {
  if (!x || !y || x.length !== y.length || x.length === 0) return 0;
  const n = x.length;
  const meanX = mean(x);
  const meanY = mean(y);

  let numerator = 0;
  let denomX = 0;
  let denomY = 0;

  for (let i = 0; i < n; i++) {
    const diffX = x[i] - meanX;
    const diffY = y[i] - meanY;
    numerator += diffX * diffY;
    denomX += diffX * diffX;
    denomY += diffY * diffY;
  }

  if (denomX === 0 || denomY === 0) return 0;
  return numerator / Math.sqrt(denomX * denomY);
}

// Perform analysis based on data structure
const results = {
  timestamp: new Date().toISOString(),
  analysis: {}
};

// Analyze driver performance if available
if (data.driverPoints && Array.isArray(data.driverPoints)) {
  results.analysis.driverStats = {
    mean: mean(data.driverPoints),
    median: median(data.driverPoints),
    stdDev: standardDeviation(data.driverPoints)
  };
}

// Calculate win rates if available
if (data.wins !== undefined && data.totalRaces !== undefined) {
  results.analysis.winRate = winRate(data.wins, data.totalRaces).toFixed(2) + '%';
}

// Calculate correlation if two datasets provided
if (data.dataset1 && data.dataset2 && Array.isArray(data.dataset1) && Array.isArray(data.dataset2)) {
  results.analysis.correlation = correlation(data.dataset1, data.dataset2).toFixed(4);
}

// Return formatted results
return JSON.stringify(results, null, 2);
