// Data Cleaning and Normalization for HDB Resale Data
// This code processes raw HDB data to ensure quality and consistency

const items = $input.all();
const cleanedData = [];
const seenRecords = new Set();

// Helper function to normalize town names
function normalizeTown(town) {
  if (!town) return null;
  return town.toString().trim().toUpperCase();
}

// Helper function to standardize flat types
function standardizeFlatType(flatType) {
  if (!flatType) return null;
  const normalized = flatType.toString().trim().toUpperCase();

  // Standardize common variations
  const typeMap = {
    '1 ROOM': '1-ROOM',
    '2 ROOM': '2-ROOM',
    '3 ROOM': '3-ROOM',
    '4 ROOM': '4-ROOM',
    '5 ROOM': '5-ROOM',
    'EXECUTIVE': 'EXECUTIVE',
    'MULTI-GENERATION': 'MULTI-GENERATION',
    'MULTI GENERATION': 'MULTI-GENERATION'
  };

  return typeMap[normalized] || normalized;
}

// Helper function to parse price
function parsePrice(price) {
  if (!price) return null;

  // Remove currency symbols, commas, and whitespace
  const cleaned = price.toString().replace(/[$,\s]/g, '');
  const parsed = parseFloat(cleaned);

  return isNaN(parsed) ? null : parsed;
}

// Helper function to parse date
function parseDate(dateStr) {
  if (!dateStr) return null;

  try {
    // Handle various date formats (YYYY-MM, YYYY/MM, etc.)
    const cleaned = dateStr.toString().trim();
    const date = new Date(cleaned);

    if (isNaN(date.getTime())) return null;
    return date.toISOString();
  } catch (e) {
    return null;
  }
}

// Helper function to parse floor area
function parseFloorArea(area) {
  if (!area) return null;

  const cleaned = area.toString().replace(/[^0-9.]/g, '');
  const parsed = parseFloat(cleaned);

  return isNaN(parsed) ? null : parsed;
}

// Process each item
for (const item of items) {
  const data = item.json;

  // Skip if data is not an object
  if (typeof data !== 'object' || data === null) continue;

  // Extract and clean fields
  const month = data.month || data.Month || data.MONTH;
  const town = normalizeTown(data.town || data.Town || data.TOWN);
  const flatType = standardizeFlatType(data.flat_type || data.flatType || data.FlatType || data.flat_model);
  const block = data.block || data.Block || data.BLOCK;
  const streetName = data.street_name || data.streetName || data.StreetName;
  const storeyRange = data.storey_range || data.storeyRange || data.StoreyRange;
  const floorArea = parseFloorArea(data.floor_area_sqm || data.floorArea || data.FloorArea);
  const flatModel = data.flat_model || data.flatModel || data.FlatModel;
  const leaseCommenceDate = data.lease_commence_date || data.leaseCommenceDate || data.LeaseCommenceDate;
  const remainingLease = data.remaining_lease || data.remainingLease || data.RemainingLease;
  const resalePrice = parsePrice(data.resale_price || data.resalePrice || data.ResalePrice || data.price);

  // Validate required fields
  if (!month || !town || !flatType || !resalePrice) {
    continue; // Skip invalid records
  }

  // Additional validation
  if (resalePrice <= 0 || resalePrice > 2000000) continue; // Unrealistic prices
  if (floorArea && (floorArea < 20 || floorArea > 300)) continue; // Unrealistic floor areas

  // Create unique identifier to detect duplicates
  const recordId = `${month}_${town}_${flatType}_${block}_${streetName}_${resalePrice}`;

  if (seenRecords.has(recordId)) {
    continue; // Skip duplicate
  }

  seenRecords.add(recordId);

  // Create cleaned record
  const cleanedRecord = {
    month: parseDate(month),
    town: town,
    flat_type: flatType,
    block: block ? block.toString().trim() : null,
    street_name: streetName ? streetName.toString().trim() : null,
    storey_range: storeyRange ? storeyRange.toString().trim() : null,
    floor_area_sqm: floorArea,
    flat_model: flatModel ? flatModel.toString().trim() : null,
    lease_commence_date: leaseCommenceDate ? parseInt(leaseCommenceDate) : null,
    remaining_lease: remainingLease ? remainingLease.toString().trim() : null,
    resale_price: resalePrice,
    // Add derived fields
    year: month ? new Date(month).getFullYear() : null,
    price_per_sqm: floorArea && resalePrice ? Math.round(resalePrice / floorArea) : null
  };

  cleanedData.push({ json: cleanedRecord });
}

console.log(`Data Cleaning Summary:`);
console.log(`- Input records: ${items.length}`);
console.log(`- Cleaned records: ${cleanedData.length}`);
console.log(`- Duplicates removed: ${items.length - cleanedData.length - (items.length - seenRecords.size)}`);
console.log(`- Invalid records filtered: ${items.length - cleanedData.length}`);

if (cleanedData.length === 0) {
  throw new Error('No valid records after cleaning. Please check the input data format.');
}

return cleanedData;

// Statistical Pattern Mining for HDB Flat Price Data
// This code performs comprehensive statistical analysis on the cleaned HDB data

const items = $input.all();

if (!items || items.length === 0) {
  return [];
}

// Helper function to calculate statistical measures
function calculateStats(values) {
  if (!values || values.length === 0) return null;

  const sorted = values.slice().sort((a, b) => a - b);
  const n = sorted.length;

  // Mean
  const mean = values.reduce((sum, val) => sum + val, 0) / n;

  // Median
  const median = n % 2 === 0 
    ? (sorted[n/2 - 1] + sorted[n/2]) / 2 
    : sorted[Math.floor(n/2)];

  // Standard Deviation
  const variance = values.reduce((sum, val) => sum + Math.pow(val - mean, 2), 0) / n;
  const stdDev = Math.sqrt(variance);

  // Quartiles
  const q1Index = Math.floor(n * 0.25);
  const q3Index = Math.floor(n * 0.75);
  const q1 = sorted[q1Index];
  const q3 = sorted[q3Index];

  return {
    mean: Math.round(mean * 100) / 100,
    median: Math.round(median * 100) / 100,
    stdDev: Math.round(stdDev * 100) / 100,
    q1: Math.round(q1 * 100) / 100,
    q3: Math.round(q3 * 100) / 100,
    min: sorted[0],
    max: sorted[n - 1],
    count: n
  };
}

// Group data by flat type and town
const groupedData = {};
const timeSeriesData = {};

items.forEach(item => {
  const data = item.json;
  const flatType = data.flat_type || 'Unknown';
  const town = data.town || 'Unknown';
  const price = parseFloat(data.resale_price);
  const floorLevel = parseFloat(data.floor_area_sqm) || 0;
  const leaseRemaining = parseFloat(data.remaining_lease_years) || 0;
  const flatSize = parseFloat(data.floor_area_sqm) || 0;
  const year = data.year || new Date(data.month).getFullYear();
  const month = data.month;

  // Group by flat type and town
  const key = `${flatType}_${town}`;
  if (!groupedData[key]) {
    groupedData[key] = {
      flatType,
      town,
      prices: [],
      floorLevels: [],
      leaseRemaining: [],
      flatSizes: []
    };
  }

  groupedData[key].prices.push(price);
  groupedData[key].floorLevels.push(floorLevel);
  groupedData[key].leaseRemaining.push(leaseRemaining);
  groupedData[key].flatSizes.push(flatSize);

  // Time series grouping
  if (!timeSeriesData[year]) {
    timeSeriesData[year] = { prices: [], months: {} };
  }
  timeSeriesData[year].prices.push(price);

  if (month) {
    const monthKey = month.substring(0, 7); // YYYY-MM
    if (!timeSeriesData[year].months[monthKey]) {
      timeSeriesData[year].months[monthKey] = [];
    }
    timeSeriesData[year].months[monthKey].push(price);
  }
});

// Calculate statistics by flat type and town
const statisticalInsights = [];

for (const [key, data] of Object.entries(groupedData)) {
  const priceStats = calculateStats(data.prices);
  const floorStats = calculateStats(data.floorLevels);
  const leaseStats = calculateStats(data.leaseRemaining);
  const sizeStats = calculateStats(data.flatSizes);

  // Calculate correlations (simplified Pearson correlation)
  function correlation(x, y) {
    if (x.length !== y.length || x.length === 0) return 0;
    const n = x.length;
    const meanX = x.reduce((a, b) => a + b, 0) / n;
    const meanY = y.reduce((a, b) => a + b, 0) / n;

    let numerator = 0;
    let denomX = 0;
    let denomY = 0;

    for (let i = 0; i < n; i++) {
      const dx = x[i] - meanX;
      const dy = y[i] - meanY;
      numerator += dx * dy;
      denomX += dx * dx;
      denomY += dy * dy;
    }

    if (denomX === 0 || denomY === 0) return 0;
    return numerator / Math.sqrt(denomX * denomY);
  }

  const priceFloorCorr = correlation(data.prices, data.floorLevels);
  const priceLeaseCorr = correlation(data.prices, data.leaseRemaining);
  const priceSizeCorr = correlation(data.prices, data.flatSizes);

  statisticalInsights.push({
    flatType: data.flatType,
    town: data.town,
    priceStatistics: priceStats,
    floorLevelStats: floorStats,
    leaseRemainingStats: leaseStats,
    flatSizeStats: sizeStats,
    correlations: {
      priceVsFloorLevel: Math.round(priceFloorCorr * 1000) / 1000,
      priceVsLeaseRemaining: Math.round(priceLeaseCorr * 1000) / 1000,
      priceVsFlatSize: Math.round(priceSizeCorr * 1000) / 1000
    }
  });
}

// Calculate year-over-year growth rates
const years = Object.keys(timeSeriesData).sort();
const yoyGrowth = [];

for (let i = 1; i < years.length; i++) {
  const prevYear = years[i - 1];
  const currYear = years[i];
  const prevAvg = timeSeriesData[prevYear].prices.reduce((a, b) => a + b, 0) / timeSeriesData[prevYear].prices.length;
  const currAvg = timeSeriesData[currYear].prices.reduce((a, b) => a + b, 0) / timeSeriesData[currYear].prices.length;
  const growthRate = ((currAvg - prevAvg) / prevAvg) * 100;

  yoyGrowth.push({
    fromYear: prevYear,
    toYear: currYear,
    previousAvgPrice: Math.round(prevAvg * 100) / 100,
    currentAvgPrice: Math.round(currAvg * 100) / 100,
    growthRate: Math.round(growthRate * 100) / 100
  });
}

// Detect seasonal patterns (monthly averages)
const seasonalPatterns = {};
for (const [year, data] of Object.entries(timeSeriesData)) {
  for (const [month, prices] of Object.entries(data.months)) {
    const monthNum = month.substring(5, 7);
    if (!seasonalPatterns[monthNum]) {
      seasonalPatterns[monthNum] = [];
    }
    const avgPrice = prices.reduce((a, b) => a + b, 0) / prices.length;
    seasonalPatterns[monthNum].push(avgPrice);
  }
}

const seasonalInsights = [];
for (const [month, prices] of Object.entries(seasonalPatterns)) {
  const stats = calculateStats(prices);
  seasonalInsights.push({
    month: month,
    avgPrice: stats.mean,
    priceRange: { min: stats.min, max: stats.max },
    volatility: stats.stdDev
  });
}

// Return comprehensive statistical insights
return [{
  json: {
    statisticalPatterns: {
      byFlatTypeAndTown: statisticalInsights,
      yearOverYearGrowth: yoyGrowth,
      seasonalPatterns: seasonalInsights.sort((a, b) => a.month.localeCompare(b.month)),
      overallTrends: {
        totalRecordsAnalyzed: items.length,
        yearsAnalyzed: years,
        averageGrowthRate: yoyGrowth.length > 0 
          ? Math.round((yoyGrowth.reduce((sum, g) => sum + g.growthRate, 0) / yoyGrowth.length) * 100) / 100 
          : 0
      }
    },
    analysisTimestamp: new Date().toISOString()
  }
}];

// Time Series Analysis for HDB Flat Prices
// Calculates moving averages, momentum indicators, trends, cycles, volatility, and exponential smoothing

const items = $input.all();

// Helper function to calculate moving average
function calculateMovingAverage(data, period) {
  const result = [];
  for (let i = 0; i < data.length; i++) {
    if (i < period - 1) {
      result.push(null);
    } else {
      const sum = data.slice(i - period + 1, i + 1).reduce((a, b) => a + b, 0);
      result.push(sum / period);
    }
  }
  return result;
}

// Helper function to calculate exponential moving average
function calculateEMA(data, period) {
  const k = 2 / (period + 1);
  const ema = [data[0]];

  for (let i = 1; i < data.length; i++) {
    ema.push(data[i] * k + ema[i - 1] * (1 - k));
  }
  return ema;
}

// Helper function to calculate standard deviation
function calculateStdDev(data) {
  const mean = data.reduce((a, b) => a + b, 0) / data.length;
  const variance = data.reduce((sum, val) => sum + Math.pow(val - mean, 2), 0) / data.length;
  return Math.sqrt(variance);
}

// Sort items by date
const sortedItems = items.sort((a, b) => {
  const dateA = new Date(a.json.month || a.json.date || a.json.year);
  const dateB = new Date(b.json.month || b.json.date || b.json.year);
  return dateA - dateB;
});

// Extract price data
const prices = sortedItems.map(item => parseFloat(item.json.resale_price || item.json.price || 0));

// Calculate moving averages
const ma3 = calculateMovingAverage(prices, 3);
const ma6 = calculateMovingAverage(prices, 6);
const ma12 = calculateMovingAverage(prices, 12);

// Calculate exponential moving averages
const ema3 = calculateEMA(prices, 3);
const ema6 = calculateEMA(prices, 6);
const ema12 = calculateEMA(prices, 12);

// Calculate momentum indicators
const momentum = prices.map((price, i) => {
  if (i < 3) return null;
  return price - prices[i - 3];
});

// Calculate rate of change (ROC)
const roc = prices.map((price, i) => {
  if (i < 12 || prices[i - 12] === 0) return null;
  return ((price - prices[i - 12]) / prices[i - 12]) * 100;
});

// Identify trend direction
const recentPrices = prices.slice(-12);
const firstHalf = recentPrices.slice(0, 6).reduce((a, b) => a + b, 0) / 6;
const secondHalf = recentPrices.slice(6).reduce((a, b) => a + b, 0) / 6;
const trendDirection = secondHalf > firstHalf ? 'upward' : secondHalf < firstHalf ? 'downward' : 'stable';
const trendStrength = Math.abs(((secondHalf - firstHalf) / firstHalf) * 100);

// Calculate volatility metrics
const returns = prices.slice(1).map((price, i) => (price - prices[i]) / prices[i]);
const volatility = calculateStdDev(returns) * 100;

// Calculate rolling volatility (12-month window)
const rollingVolatility = [];
for (let i = 11; i < prices.length; i++) {
  const windowPrices = prices.slice(i - 11, i + 1);
  const windowReturns = windowPrices.slice(1).map((price, j) => (price - windowPrices[j]) / windowPrices[j]);
  rollingVolatility.push(calculateStdDev(windowReturns) * 100);
}

// Detect cyclical patterns (simplified seasonal decomposition)
const monthlyAverages = {};
sortedItems.forEach(item => {
  const date = new Date(item.json.month || item.json.date || item.json.year);
  const month = date.getMonth();
  if (!monthlyAverages[month]) {
    monthlyAverages[month] = [];
  }
  monthlyAverages[month].push(parseFloat(item.json.resale_price || item.json.price || 0));
});

const seasonalFactors = {};
for (let month in monthlyAverages) {
  seasonalFactors[month] = monthlyAverages[month].reduce((a, b) => a + b, 0) / monthlyAverages[month].length;
}

const overallMean = prices.reduce((a, b) => a + b, 0) / prices.length;
const seasonalIndices = {};
for (let month in seasonalFactors) {
  seasonalIndices[month] = (seasonalFactors[month] / overallMean) * 100;
}

// Prepare output with time series features
const timeSeriesFeatures = {
  moving_averages: {
    ma_3_month: ma3.filter(v => v !== null).slice(-1)[0] || null,
    ma_6_month: ma6.filter(v => v !== null).slice(-1)[0] || null,
    ma_12_month: ma12.filter(v => v !== null).slice(-1)[0] || null
  },
  exponential_moving_averages: {
    ema_3_month: ema3.slice(-1)[0] || null,
    ema_6_month: ema6.slice(-1)[0] || null,
    ema_12_month: ema12.slice(-1)[0] || null
  },
  momentum_indicators: {
    momentum_3_month: momentum.filter(v => v !== null).slice(-1)[0] || null,
    rate_of_change_12_month: roc.filter(v => v !== null).slice(-1)[0] || null
  },
  trend_analysis: {
    direction: trendDirection,
    strength_percentage: trendStrength.toFixed(2),
    current_price: prices.slice(-1)[0],
    price_change_12_month: prices.length >= 12 ? prices.slice(-1)[0] - prices[prices.length - 12] : null
  },
  volatility_metrics: {
    overall_volatility_percentage: volatility.toFixed(2),
    recent_volatility_12_month: rollingVolatility.slice(-1)[0]?.toFixed(2) || null,
    price_range: {
      min: Math.min(...prices),
      max: Math.max(...prices),
      range: Math.max(...prices) - Math.min(...prices)
    }
  },
  seasonal_patterns: {
    seasonal_indices: seasonalIndices,
    strongest_month: Object.keys(seasonalIndices).reduce((a, b) => seasonalIndices[a] > seasonalIndices[b] ? a : b),
    weakest_month: Object.keys(seasonalIndices).reduce((a, b) => seasonalIndices[a] < seasonalIndices[b] ? a : b)
  },
  data_summary: {
    total_observations: prices.length,
    date_range: {
      start: sortedItems[0].json.month || sortedItems[0].json.date || sortedItems[0].json.year,
      end: sortedItems[sortedItems.length - 1].json.month || sortedItems[sortedItems.length - 1].json.date || sortedItems[sortedItems.length - 1].json.year
    },
    mean_price: overallMean.toFixed(2)
  }
};

return [{ json: timeSeriesFeatures }];

