}

// Linear Regression Function
function linearRegression(x, y) {
  const n = x.length;
  const sumX = x.reduce((a, b) => a + b, 0);
  const sumY = y.reduce((a, b) => a + b, 0);
  const sumXY = x.reduce((sum, xi, i) => sum + xi * y[i], 0);
  const sumX2 = x.reduce((sum, xi) => sum + xi * xi, 0);

  const slope = (n * sumXY - sumX * sumY) / (n * sumX2 - sumX * sumX);
  const intercept = (sumY - slope * sumX) / n;

  return { slope, intercept };
}

// Calculate Mean Absolute Percentage Error
function calculateMAPE(actual, predicted) {
  const n = actual.length;
  let sum = 0;
  for (let i = 0; i < n; i++) {
    sum += Math.abs((actual[i] - predicted[i]) / actual[i]);
  }
  return (sum / n) * 100;
}

// Exponential Smoothing
function exponentialSmoothing(data, alpha = 0.3) {
  const smoothed = [data[0]];
  for (let i = 1; i < data.length; i++) {
    smoothed.push(alpha * data[i] + (1 - alpha) * smoothed[i - 1]);
  }
  return smoothed;
}

// Monte Carlo Simulation for confidence intervals
function monteCarloSimulation(basePrice, volatility, months, simulations = 1000) {
  const results = [];

  for (let month = 1; month <= months; month++) {
    const monthResults = [];

    for (let sim = 0; sim < simulations; sim++) {
      // Random walk with drift
      const randomShock = (Math.random() - 0.5) * 2 * volatility;
      const price = basePrice * (1 + randomShock * Math.sqrt(month));
      monthResults.push(price);
    }

    monthResults.sort((a, b) => a - b);
    results.push({
      month: month,
      mean: monthResults.reduce((a, b) => a + b, 0) / simulations,
      lower: monthResults[Math.floor(simulations * 0.05)],
      upper: monthResults[Math.floor(simulations * 0.95)],
      median: monthResults[Math.floor(simulations * 0.5)]
    });
  }

  return results;
}

// Main forecasting logic
const timeIndices = prices.map((_, i) => i);
const { slope, intercept } = linearRegression(timeIndices, prices);

// Calculate volatility (standard deviation of returns)
const returns = [];
for (let i = 1; i < prices.length; i++) {
  returns.push((prices[i] - prices[i - 1]) / prices[i - 1]);
}
const meanReturn = returns.reduce((a, b) => a + b, 0) / returns.length;
const variance = returns.reduce((sum, r) => sum + Math.pow(r - meanReturn, 2), 0) / returns.length;
const volatility = Math.sqrt(variance);

// Apply exponential smoothing
const smoothedPrices = exponentialSmoothing(prices);

// Generate predictions for next 12 months
const predictions = [];
const lastIndex = prices.length - 1;
const lastPrice = prices[lastIndex];

for (let month = 1; month <= 12; month++) {
  const futureIndex = lastIndex + month;
  const linearPrediction = slope * futureIndex + intercept;
  const trendAdjusted = linearPrediction * (1 + meanReturn * month);

  predictions.push({
    month: month,
    predicted_price: Math.round(trendAdjusted),
    linear_trend: Math.round(linearPrediction)
  });
}

// Monte Carlo simulation for confidence intervals
const mcResults = monteCarloSimulation(lastPrice, volatility, 12);

// Combine predictions with confidence intervals
const forecast = predictions.map((pred, i) => ({
  month: pred.month,
  predicted_price: pred.predicted_price,
  lower_bound: Math.round(mcResults[i].lower),
  upper_bound: Math.round(mcResults[i].upper),
  confidence_level: '90%'
}));

// Calculate fitted values for accuracy metrics
const fittedValues = timeIndices.map(i => slope * i + intercept);
const mape = calculateMAPE(prices, fittedValues);

// Prepare output
const output = {
  forecast_12_months: forecast,
  model_metrics: {
    slope: slope.toFixed(4),
    intercept: intercept.toFixed(2),
    volatility: (volatility * 100).toFixed(2) + '%',
    mape: mape.toFixed(2) + '%',
    mean_return: (meanReturn * 100).toFixed(2) + '%'
  },
  summary: `Forecasted ${forecast.length} months ahead. Average predicted price: $${Math.round(forecast.reduce((sum, f) => sum + f.predicted_price, 0) / forecast.length)}. Model accuracy (MAPE): ${mape.toFixed(2)}%`
};

return JSON.stringify(output, null, 2);

