parameters.inputSchema:
{
	"type": "object",
	"properties": {
		"commitments": {
			"type": "array",
			"description": "List of commitments made during the call",
			"items": {
				"type": "object",
				"properties": {
					"party": {
						"type": "string",
						"description": "Who made the commitment: customer or sales rep"
					},
					"commitment": {
						"type": "string",
						"description": "Description of the commitment"
					},
					"deadline": {
						"type": "string",
						"description": "Deadline or timeframe for the commitment"
					},
					"confidence": {
						"type": "number",
						"description": "Confidence level of this being a real commitment (0-1)"
					}
				}
			}
		}
	}
}
